// Rendering parameters
#define FLOW
#define RAY_LENGTH_MAX		50.0
#define RAY_STEP_MAX		50
#define LIGHT				vec3 (0.5, 0.0, -2.0)
#define AMBIENT				0.5
#define SPECULAR_POWER		4.0
#define SPECULAR_INTENSITY	0.2
#define FADE_POWER			3.0
#define GAMMA				(1.0 / 2.2)
#define HSV2RGB_FAST
#define TEXTURE_SCALE		0.4

// Constants
#define PI		3.14159265359
#define DELTA	0.01

vec3 vRotateZ (in vec3 p, in float angle) {
	float c = cos (angle);
	float s = sin (angle);
	return vec3 (c * p.x + s * p.y, c * p.y - s * p.x, p.z);
}

float sphere (in vec3 p, in float r) {
	return length (p) - r;
}

float box (in vec3 p, in vec3 b, in float r) {
	#ifdef BOX_NEGATIVE
	vec3 d = abs (p) - b + r;
	return min (max (d.x, max (d.y, d.z)), 0.0) + length (max (d, 0.0)) - r;
	#else
	return length (max (abs (p) - b + r, 0.0)) - r;
	#endif
}

float torusZ (in vec3 p, in float r1, in float r2) {
	vec2 q = vec2 (length (p.xy) - r1, p.z);
	return length (q) - r2;
}

float cylinderZ (in vec3 p, in float r) {
 	return length (p.xy) - r;
}

vec3 twistZ (in vec3 p, in float k, in float angle) {
	return vRotateZ (p, angle + k * p.z);
}

float fixDistance (in float d, in float correction, in float k) {
	correction = max (correction, 0.0);
	k = clamp (k, 0.0, 1.0);
	return min (d, max ((d - DELTA) * k + DELTA, d - correction));
}

float getDistance (in vec3 p, out vec4 q) {

	// Global deformation
	p += vec3 (2.0 * sin (p.z * 0.2 + iTime * 2.0), sin (p.z * 0.1 + iTime), 0.0);

	// Cylinder
	q.xyz = p;
	q.w = -1.0;
	float d = fixDistance (-cylinderZ (q.xyz, 4.0) + 0.5 * sin (atan (q.y, q.x) * 6.0) * sin (q.z), 0.4, 0.8);

	// Twisted boxes
	vec3 q_;
	q_.xy = mod (p.xy, 5.0) - 0.5 * 5.0;
	q_.z = mod (p.z, 12.0) - 0.5 * 12.0;	
	q_ = twistZ (q_, 1.0, iTime);
	float d_ = fixDistance (box (q_, vec3 (0.6, 0.6, 1.5), 0.3), 0.4, 0.8);
	if (d_ < d) {
		q.xyz = q_;
		d = d_;
	}

	// Rotating spheres
	q_ = p;
	q_.z += 12.0;
	q_ = vRotateZ (q_, sin (iTime * 4.0));
	q_.xy = mod (q_.xy, 4.5) - 0.5 * 4.5;
	q_.z = mod (q_.z, 24.0) - 0.5 * 24.0;
	d_ = sphere (q_, 0.5);
	if (d_ < d) {
		q.xyz = q_;
		d = d_;
	}

	// Torus
	q_ = p;
	q_.z = mod (q_.z + 12.0, 24.0) - 0.5 * 24.0;
	d_ = torusZ (q_, 3.5, 0.4);
	if (d_ < d) {
		q.xyz = q_;
		d = d_;
	}

	// Flow of boxes and spheres
	#ifdef FLOW
	q_ = p;
	q_.z += iTime * 20.0;
	const float spacing = 0.5;
	const float stepCount = 3.0;
	const float period = spacing * stepCount;
	for (float step = 0.0; step < stepCount; ++step) {
		float k1 = floor (q_.z / period + 0.5);
 		float k2 = k1 * stepCount + step;
		vec3 qq = q_ - vec3 (0.4 * sin (k2), 0.4 * sin (k2 * 13.0), period * k1);
		if (mod (k2, 2.0) > 0.5) {
			d_ = box (vRotateZ (qq, k2), vec3 (0.08), 0.01);
		} else {
			d_ = sphere (qq, 0.08);
		}
		if (d_ < d) {
			q.xyz = qq;
			q.w = 1.0;
			d = d_;
		}
		q_.z += spacing;
	}
	#endif

	// Final distance
	return d;
}

vec3 getNormal (in vec3 p) {
	vec4 q;
	const vec2 h = vec2 (DELTA, 0.0);
	return normalize (vec3 (
		getDistance (p + h.xyy, q) - getDistance (p - h.xyy, q),
		getDistance (p + h.yxy, q) - getDistance (p - h.yxy, q),
		getDistance (p + h.yyx, q) - getDistance (p - h.yyx, q)
	));
}

vec3 hsv2rgb (in vec3 hsv) {
	#ifdef HSV2RGB_SAFE
	hsv.yz = clamp (hsv.yz, 0.0, 1.0);
	#endif
	#ifdef HSV2RGB_FAST
	return hsv.z * (1.0 + 0.5 * hsv.y * (cos (2.0 * PI * (hsv.x + vec3 (0.0, 2.0 / 3.0, 1.0 / 3.0))) - 1.0));
	#else
	return hsv.z * (1.0 + hsv.y * clamp (abs (fract (hsv.x + vec3 (0.0, 2.0 / 3.0, 1.0 / 3.0)) * 6.0 - 3.0) - 2.0, -1.0, 0.0));
	#endif
}

void mainImage (out vec4 fragColor, in vec2 fragCoord) {

	// Define the ray corresponding to this fragment
	vec2 frag = (2.0 * fragCoord.xy - iResolution.xy) / iResolution.y;
	vec3 direction = normalize (vec3 (frag, 2.0));

	// Set the camera
	vec3 origin = vec3 (0.0, 0.0, iTime * 6.0);
	vec3 forward = vec3 (0.2 * cos (iTime), 0.2 * sin (iTime), cos (iTime * 0.3));
	vec3 up = vRotateZ (vec3 (0.0, 1.0, 0.0), PI * sin (iTime) * sin (iTime * 0.2));
	mat3 rotation;
	rotation [2] = normalize (forward);
	rotation [0] = normalize (cross (up, forward));
	rotation [1] = cross (rotation [2], rotation [0]);
	direction = rotation * direction;

	// Ray marching
	vec3 p = origin;
	vec4 q;
	float rayLength = 0.0;
	int rayStepCounter = 0;
	for (int rayStep = 0; rayStep < RAY_STEP_MAX; ++rayStep) {
		float dist = getDistance (p, q);
		rayLength += dist;
		if (dist < DELTA || rayLength > RAY_LENGTH_MAX) {
			break;
		}
		p += dist * direction;
		++rayStepCounter;
	}

	// Compute the fragment color
	vec3 color;
	float bass = texture (iChannel0, vec2 (0.0)).x;
	if (rayLength > RAY_LENGTH_MAX) {
		color = vec3 (0.0);
	} else {

		// Object color
		vec3 normal = getNormal (p);
		float hue = (p.z + iTime) * 0.1;
		if (q.w < 0.0) {
			#ifdef TEXTURE_SCALE
			vec3 textureUV = q.xyz * TEXTURE_SCALE;
			vec3 textureFactor = abs (normal);
			mat3 textureColor = mat3 (
				texture (iChannel1, textureUV.yz).rgb,
				texture (iChannel1, textureUV.zx).rgb,
				texture (iChannel1, textureUV.xy).rgb
				);
			color = hsv2rgb (vec3 (hue, 1.0, 1.0));
			color *= textureColor * textureFactor / (textureFactor.x + textureFactor.y + textureFactor.z);
			#else
			float treble = texture (iChannel0, vec2 (0.8, 0.0)).x;
			float saturation = 0.8 + (0.2 + 0.8 * treble) * 0.4 * sin (q.x * 10.0) * sin (q.y * 10.0) * sin (q.z * 10.0);
			float value = 0.2 + 0.8 * bass;
			color = hsv2rgb (vec3 (hue, saturation, value));
			#endif
		} else {
			color = hsv2rgb (vec3 (hue, 1.0, 1.0));
		}

		// Lighting
		vec3 lightDirection = normalize (LIGHT);
		vec3 reflectDirection = reflect (direction, normal);
		float diffuse = max (0.0, dot (normal, lightDirection));
		float specular = pow (max (0.0, dot (reflectDirection, lightDirection)), SPECULAR_POWER) * SPECULAR_INTENSITY;
		float fade = pow (1.0 - rayLength / RAY_LENGTH_MAX, FADE_POWER);
		color = ((AMBIENT + diffuse) * color + specular) * fade;

		// Special effect
		color *= max (1.0, 10.0 * sin (p.z * 0.1 - iTime * 4.0) - 7.0);

		// Gamma correction
		color = pow (color, vec3 (GAMMA));
	}

	// Another special effect
	color.r = mix (color.r, float (rayStepCounter) / float (RAY_STEP_MAX / 2), bass * bass);

	// Set the fragment color
	fragColor = vec4 (color, 1.0);
}

#define DELTA			0.01
#define RAY_LENGTH_MAX	300.0
#define RAY_STEP_MAX	200

float fixDistance (in float d, in float correction, in float k) {
	correction = max (correction, 0.0);
	k = clamp (k, 0.0, 1.0);
	return min (d, max ((d - DELTA) * k + DELTA, d - correction));
}

float getDistance (in vec3 p) {
	p += vec3 (3.0 * sin (p.z * 0.2 + iTime * 2.0), sin (p.z * 0.3 + iTime), 0.0);
	return fixDistance (length (p.xy) - 4.0 + 0.8 * sin (abs (p.x * p.y) + p.z * 4.0) * sin (p.z), 2.5, 0.2);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord) {

	// Define the ray corresponding to this fragment
	vec2 frag = (2.0 * fragCoord.xy - iResolution.xy) / iResolution.y;
	vec3 direction = normalize (vec3 (frag, 2.0));

	// Set the camera
	vec3 origin = vec3 ((17.0 + 5.0 * sin (iTime)) * cos (iTime * 0.2), 12.0 * sin (iTime * 0.2), 0.0);
	vec3 forward = vec3 (-origin.x, -origin.y, 22.0 + 6.0 * cos (iTime * 0.2));
	vec3 up = vec3 (0.0, 1.0, 0.0);
	mat3 rotation;
	rotation [2] = normalize (forward);
	rotation [0] = normalize (cross (up, forward));
	rotation [1] = cross (rotation [2], rotation [0]);
	direction = rotation * direction;

	// Ray marching
	vec3 p = origin;
	float dist = RAY_LENGTH_MAX;
	float rayLength = 0.0;
	int stepCount = 0;
	for (int rayStep = 0; rayStep < RAY_STEP_MAX; ++rayStep) {
		dist = getDistance (p);
		rayLength += dist;
		if (dist < DELTA || rayLength > RAY_LENGTH_MAX) {
			break;
		}
		p = origin + direction * rayLength;
		++stepCount;
	}

	// Compute the fragment color
	vec4 color = vec4 (float (stepCount * 3) / float (RAY_STEP_MAX), float (stepCount) * 1.5 / float (RAY_STEP_MAX), 0.0, 1.0);
	vec3 LIGHT = normalize (vec3 (1.0, -3.0, -1.0));
	if (dist < DELTA) {
		vec2 h = vec2 (DELTA, 0.0);
		vec3 normal = normalize (vec3 (
			getDistance (p + h.xyy) - getDistance (p - h.xyy),
			getDistance (p + h.yxy) - getDistance (p - h.yxy),
			getDistance (p + h.yyx) - getDistance (p - h.yyx)));
		color.rg += 0.5 * max (0.0, dot (normal, LIGHT));
	}
	else {
		color.b += 0.1 + 0.5 * max (0.0, dot (-direction, LIGHT));
	}
	fragColor = color;
}

#define DELTA				0.02
#define RAY_LENGTH_MAX		150.0
#define RAY_STEP_MAX		200
#define AMBIENT				0.3
#define SPECULAR_POWER		5.0
#define SPECULAR_INTENSITY	0.3
#define FADE_POWER			3.0
#define GAMMA				0.8
#define M_PI				3.1415926535897932384626433832795

//#define ATAN2 // Comment this to use the original atan function
#define SHADOW

vec3 vRotateX (in vec3 p, in float angle) {
	float c = cos (angle);
	float s = sin (angle);
	return vec3 (p.x, c * p.y + s * p.z, c * p.z - s * p.y);
}

vec3 vRotateY (in vec3 p, in float angle) {
	float c = cos (angle);
	float s = sin (angle);
	return vec3 (c * p.x - s * p.z, p.y, c * p.z + s * p.x);
}

float fixDistance (in float d, in float correction, in float k) {
	correction = max (correction, 0.0);
	k = clamp (k, 0.0, 1.0);
	return min (d, max ((d - DELTA) * k + DELTA, d - correction));
}

float smin (float a, float b, float k) {

	// From http://www.iquilezles.org/www/articles/smin/smin.htm
	float h = clamp (0.5 + 0.5 * (b - a) / k, 0.0, 1.0);
	return mix (b, a, h) - k * h * (1.0 - h);
}

#ifndef ATAN2
#define atan2 atan
#else
float atan2 (float y, float x) {

	// From http://www.deepdyve.com/lp/institute-of-electrical-and-electronics-engineers/full-quadrant-approximations-for-the-arctangent-function-tips-and-V6yJDoI0iF
	float t1 = abs (y);
	float t2 = abs (x);
	float t3 = min (t1, t2) / max (t1, t2);
	t3 = t3 / (1.0 + 0.28086 * t3 * t3);
	t3 = t1 > t2 ? M_PI / 2.0 - t3 : t3;
	t3 = x < 0.0 ? M_PI - t3 : t3;
	t3 = y < 0.0 ? -t3 : t3;
	return t3;
}

float atan2_bis (float y, float x) {

	// From http://http.developer.nvidia.com/Cg/atan2.html
	float t1 = abs (y);
	float t2 = abs (x);
	float t3 = min (t1, t2) / max (t1, t2);
	float t4 = t3 * t3;
	float t5 = -0.013480470;
	t5 = t5 * t4 + 0.057477314;
	t5 = t5 * t4 - 0.121239071;
	t5 = t5 * t4 + 0.195635925;
	t5 = t5 * t4 - 0.332994597;
	t5 = t5 * t4 + 0.999995630;
	t3 = t5 * t3;
	t3 = t1 > t2 ? M_PI / 2.0 - t3 : t3;
	t3 = x < 0.0 ? M_PI - t3 : t3;
	t3 = y < 0.0 ? -t3 : t3;
	return t3;
}

float atan2_ter (float y, float x) {

	// From http://rc0rc0.wordpress.com/2013/06/05/minimax-approximation-to-arctan-atan-atan2/
	float t1 = abs (y);
	float t2 = abs (x);
	float t3 = min (t1, t2) / max (t1, t2);
	t3 = 0.97239 * t3 - 0.19195 * t3 * t3 * t3;
	t3 = t1 > t2 ? M_PI / 2.0 - t3 : t3;
	t3 = x < 0.0 ? M_PI - t3 : t3;
	t3 = y < 0.0 ? -t3 : t3;
	return t3;
}
#endif

float getDistance (in vec3 p) {
	float k = 0.2 + 0.2 * sin (p.y * 2.0 + iTime * 3.0);
	vec3 pp = p;
	pp.z *= 1.2;
	float body = length (pp) + k - 4.5;
	body = fixDistance (body, 2.0, 0.8);

	p.y += 1.0;
	float angle = 2.0 * M_PI / 8.0;
	pp = vRotateY (p, -angle * 0.5);
	angle *= floor (atan2 (pp.x, pp.z) / angle);
	p = vRotateY (p, angle);
	p.xy /= min (1.1, 40.0 / p.z);
	k = clamp (p.z - 5.0, 0.0, 0.5);
	p = vRotateX (p, p.z * (0.01 + 0.01 * sin (iTime * 0.5 + angle * 5.0)) + 0.1 * sin (p.z * 0.3 + iTime + angle * 3.0));
	p = vRotateY (p, 0.1 * sin (p.z * 0.2 + iTime * 2.0 + angle * 7.0));
	p.z = sin (p.z * 2.0) / 2.0;
	float tentacle = length (p) - 1.0 - k * sin (abs (p.x * p.y * p.y* p.y + p.z));
	tentacle = fixDistance (tentacle, 60.0, 0.15);

	return smin (body, tentacle, 0.2);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord) {

	// Old monitor effect
	vec2 frag = 2.0 * fragCoord.xy / iResolution.xy - 1.0;
	float round = pow (length (frag * frag), 6.0);
	frag.y *= 1.0 + round * 0.2;
	vec3 colorMonitor = vec3 (1.0 - round);
	colorMonitor *= vec3 (0.8, 1.0 + abs (mod (frag.y, 0.04) / 0.04 - 0.5), 0.8);

	// Define the ray corresponding to this fragment
	frag.x *= iResolution.x / iResolution.y;
	frag.x += 0.01 * sin (frag.y * 40.0 + iTime);
	vec3 direction = normalize (vec3 (frag, 2.0));

	// Set the camera
	vec3 origin = vec3 ((50.0 * cos (iTime * 0.1)), -3.0 + 10.0 * sin (iTime * 0.2), -35.0 + 20.0 * sin (iTime * 0.3));
	vec3 forward = -origin;
	vec3 up = vec3 (0.0, 1.0, 0.0);
	mat3 rotation;
	rotation [2] = normalize (forward);
	rotation [0] = normalize (cross (up, forward));
	rotation [1] = cross (rotation [2], rotation [0]);
	direction = rotation * direction;

	// Ray marching
	vec3 p = origin;
	float dist = RAY_LENGTH_MAX;
	float rayLength = 0.0;
	for (int rayStep = 0; rayStep < RAY_STEP_MAX; ++rayStep) {
		dist = getDistance (p);
		rayLength += dist;
		if (dist < DELTA || rayLength > RAY_LENGTH_MAX) {
			break;
		}
		p = origin + direction * rayLength;
	}

	// Compute the background color
	vec3 lightDirection = normalize (vec3 (2.0, sin (iTime), -2.0));
	vec3 finalColor = vec3 (0.1, 0.2, 0.2 + 0.3 * max (0.0, dot (-direction, lightDirection)));
	if (dist < DELTA) {

		// Define the color of the octopus
		vec3 color = vec3 (0.3, 0.1, 0.2);

		// Initialize the lighting
		float fade = pow (1.0 - rayLength / RAY_LENGTH_MAX, FADE_POWER);
		float diffuse = 0.0;
		float specular = 0.0;

		#ifdef SHADOW
		// Ray march again to check whether the light is visible
		dist = RAY_LENGTH_MAX;
		rayLength = DELTA * 10.0;;
		for (int rayStep = 0; rayStep < RAY_STEP_MAX; ++rayStep) {
			vec3 pp = p + lightDirection * rayLength;
			dist = getDistance (pp);
			rayLength += dist;
			if (dist < DELTA || rayLength > RAY_LENGTH_MAX) {
				break;
			}
		}
		if (dist >= DELTA)
		#endif
		{
			// Get the normal
			vec2 h = vec2 (DELTA, 0.0);
			vec3 normal = normalize (vec3 (
				getDistance (p + h.xyy) - getDistance (p - h.xyy),
				getDistance (p + h.yxy) - getDistance (p - h.yxy),
				getDistance (p + h.yyx) - getDistance (p - h.yyx)));

			// Lighting
			diffuse = max (0.0, dot (normal, lightDirection));
			specular = pow (max (0.0, dot (reflect (direction, normal), lightDirection)), SPECULAR_POWER) * SPECULAR_INTENSITY;
		}

		// Compute the final color
		finalColor = mix (finalColor, (AMBIENT + diffuse) * color + specular, fade);
	}

	// Set the fragment color
	finalColor = pow (finalColor, vec3 (GAMMA));
	fragColor = vec4 (finalColor * colorMonitor, 1.0);
}

#define DELTA				0.001
#define RAY_COUNT			5
#define RAY_LENGTH_MAX		100.0
#define RAY_STEP_MAX		50
#define LIGHT				vec3 (1.0, 1.0, -1.0)
#define REFRACT_FACTOR		0.6
#define REFRACT_INDEX		1.2 // 2.417 for real diamonds... but it would require RAY_COUNT to be increased (because of total internal reflections)
#define AMBIENT				0.2
#define SPECULAR_POWER		3.0
#define SPECULAR_INTENSITY	0.5
#define FADE_POWER			1.0
#define M_PI				3.1415926535897932384626433832795
#define GLOW_FACTOR			0.5
#define LUMINOSITY_FACTOR	2.0

//#define ATAN2 // Comment this to use the original atan function

mat3 mRotate (in vec3 angle) {
	float c = cos (angle.x);
	float s = sin (angle.x);
	mat3 rx = mat3 (1.0, 0.0, 0.0, 0.0, c, s, 0.0, -s, c);

	c = cos (angle.y);
	s = sin (angle.y);
	mat3 ry = mat3 (c, 0.0, -s, 0.0, 1.0, 0.0, s, 0.0, c);

	c = cos (angle.z);
	s = sin (angle.z);
	mat3 rz = mat3 (c, s, 0.0, -s, c, 0.0, 0.0, 0.0, 1.0);

	return rz * ry * rx;
}

vec3 vRotateY (in vec3 p, in float angle) {
	float c = cos (angle);
	float s = sin (angle);
	return vec3 (c * p.x - s * p.z, p.y, c * p.z + s * p.x);
}

#ifndef ATAN2
#define atan2 atan
#else
float atan2 (in float y, in float x) {

	// From http://www.deepdyve.com/lp/institute-of-electrical-and-electronics-engineers/full-quadrant-approximations-for-the-arctangent-function-tips-and-V6yJDoI0iF
	// atan (x) = x / (1 + 0.28086 x^2)

	float t1 = abs (y);
	float t2 = abs (x);
	float t3 = min (t1, t2) / max (t1, t2);
	t3 = t3 / (1.0 + 0.28086 * t3 * t3);
	t3 = t1 > t2 ? M_PI / 2.0 - t3 : t3;
	t3 = x < 0.0 ? M_PI - t3 : t3;
	t3 = y < 0.0 ? -t3 : t3;
	return t3;
}

float atan2_nobranch (in float y, in float x) {

	// From http://www.deepdyve.com/lp/institute-of-electrical-and-electronics-engineers/full-quadrant-approximations-for-the-arctangent-function-tips-and-V6yJDoI0iF
	// atan (x) = x / (1 + 0.28086 x^2)

	float t1 = abs (y);
	float t2 = abs (x);
	float t3 = min (t1, t2) / max (t1, t2);
	t3 = t3 / (1.0 + 0.28086 * t3 * t3);
	float t4 = M_PI / 2.0 - t3;
	t3 = step (0.0, t2 - t1) * (t3 - t4) + t4;
	t4 = M_PI - t3;
	t3 = step (0.0, x) * (t3 - t4) + t4;
	t3 = step (0.0, y) * (t3 + t3) - t3;
	return t3;
}
#endif

vec3 normalTopA = normalize (vec3 (0.0, 1.0, 1.4));
vec3 normalTopB = normalize (vec3 (0.0, 1.0, 1.0));
vec3 normalTopC = normalize (vec3 (0.0, 1.0, 0.8));
vec3 normalBottomA = normalize (vec3 (0.0, -1.0, 1.6));
vec3 normalBottomB = normalize (vec3 (0.0, -1.0, 2.0));
vec3 k;
float getDistance (in vec3 p) {
	float repeat = 20.0;
	vec3 q = p + repeat * 0.5;
	k = floor (q / repeat);
	q -= repeat * (k + 0.5);
	p = mRotate (k + iTime) * q;

	float topCut = p.y - 1.1;
	float angleStep = M_PI / max (1.0, abs (4.0 + k.x + 2.0 * k.y + 4.0 * k.z));
	float angle = angleStep * (0.5 + floor (atan2 (p.x, p.z) / angleStep));
	q = vRotateY (p, angle);
	float topA = dot (q, normalTopA) - 2.0;
	float bottomA = dot (q, normalBottomA) - 2.0;
	float topC = dot (q, normalTopC) - 1.8;
	q = vRotateY (p, -angleStep * 0.5);
	angle = angleStep * floor (atan2 (q.x, q.z) / angleStep);
	q = vRotateY (p, angle);
	float bottomB = dot (q, normalBottomB) - 1.95;
	float topB = dot (q, normalTopB) - 1.92;

	return max (topCut, max (topA, max (topB, max (topC, max (bottomA, bottomB)))));
}

vec3 getFragmentColor (in vec3 origin, in vec3 direction) {
	vec3 lightDirection = normalize (LIGHT);
	vec2 delta = vec2 (DELTA, 0.0);

	vec3 fragColor = vec3 (0.0, 0.0, 0.0);
	float intensity = 1.0;

	float distanceFactor = 1.0;
	float refractionRatio = 1.0 / REFRACT_INDEX;
	float rayStepCount = 0.0;
	for (int rayIndex = 0; rayIndex < RAY_COUNT; ++rayIndex) {

		// Ray marching
		float dist = RAY_LENGTH_MAX;
		float rayLength = 0.0;
		for (int rayStep = 0; rayStep < RAY_STEP_MAX; ++rayStep) {
			dist = distanceFactor * getDistance (origin);
			float distMin = max (dist, DELTA);
			rayLength += distMin;
			if (dist < 0.0 || rayLength > RAY_LENGTH_MAX) {
				break;
			}
			origin += direction * distMin;
			++rayStepCount;
		}

		// Check whether we hit something
		vec3 backColor = vec3 (0.0, 0.0, 0.1 + 0.2 * max (0.0, dot (-direction, lightDirection)));
		if (dist >= 0.0) {
			fragColor = fragColor * (1.0 - intensity) + backColor * intensity;
			break;
		}

		// Get the normal
		vec3 normal = normalize (distanceFactor * vec3 (
			getDistance (origin + delta.xyy) - getDistance (origin - delta.xyy),
			getDistance (origin + delta.yxy) - getDistance (origin - delta.yxy),
			getDistance (origin + delta.yyx) - getDistance (origin - delta.yyx)));

		// Basic lighting
		vec3 reflection = reflect (direction, normal);
		if (distanceFactor > 0.0) {
			float relfectionDiffuse = max (0.0, dot (normal, lightDirection));
			float relfectionSpecular = pow (max (0.0, dot (reflection, lightDirection)), SPECULAR_POWER) * SPECULAR_INTENSITY;
			float fade = pow (1.0 - rayLength / RAY_LENGTH_MAX, FADE_POWER);

			vec3 localColor = max (sin (k * k), 0.2);
			localColor = (AMBIENT + relfectionDiffuse) * localColor + relfectionSpecular;
			localColor = mix (backColor, localColor, fade);

			fragColor = fragColor * (1.0 - intensity) + localColor * intensity;
			intensity *= REFRACT_FACTOR;
		}

		// Next ray...
		vec3 refraction = refract (direction, normal, refractionRatio);
		if (dot (refraction, refraction) < DELTA) {
			direction = reflection;
			origin += direction * DELTA * 2.0;
		} else {
			direction = refraction;
			distanceFactor = -distanceFactor;
			refractionRatio = 1.0 / refractionRatio;
		}
	}

	// Return the fragment color
	return fragColor * LUMINOSITY_FACTOR + GLOW_FACTOR * rayStepCount / float (RAY_STEP_MAX * RAY_COUNT);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord) {

	// Define the ray corresponding to this fragment
	vec2 frag = (2.0 * fragCoord.xy - iResolution.xy) / iResolution.y;
	vec3 direction = normalize (vec3 (frag, 2.0));

	// Set the camera
	vec3 origin = vec3 ((15.0 * cos (iTime * 0.1)), 10.0 * sin (iTime * 0.2), 15.0 * sin (iTime * 0.1));
	vec3 forward = -origin;
	vec3 up = vec3 (sin (iTime * 0.3), 2.0, 0.0);
	mat3 rotation;
	rotation [2] = normalize (forward);
	rotation [0] = normalize (cross (up, forward));
	rotation [1] = cross (rotation [2], rotation [0]);
	direction = rotation * direction;

	// Set the fragment color
	fragColor = vec4 (getFragmentColor (origin, direction), 1.0);
}

// Concept from: http://www.iquilezles.org/www/articles/voronoilines/voronoilines.htm

// Relation between the "HASH_MAGNITUDE" (M) and "KERNEL" (K):
// - In a given cell C1, the greatest distance from a point P1 of this cell to the cell's reference point R1 is D:
//   D = SQRT2 * (M / 2 + 1 / 2)
//   D = (M + 1) * SQRT2 / 2
//   (This happens when P1 is in a corner of C1, and R1 is as far as possible in the opposite direction.)
// - So, the farther other reference point R2 that can be closer to P1 is somewhere on the circle of center P1 and radius D.
// - The center of the cell C2 which the reference point is R2 is maximum at a distance M / 2 from R2.
// - The relative position of the center of C2 compare to C1 is K:
//   K = floor (1 / 2 + sqrt ((D + M / 2) ^ 2 - (1 / 2) ^ 2))
//   K = floor ((1 + sqrt (((M + 1) * SQRT2 + M) ^ 2 - 1)) / 2)
// - If we neglect "- 1" in the square root, we can approximate K:
//   K = floor ((1 + (M + 1) * SQRT2 + M) / 2)
//   K = floor ((M + 1) * (SQRT2 + 1) / 2)
// - Reciprocally, if we set K, we can compute the maximum value of M:
//   M < 2 * (K + 1) / (SQRT2 + 1) - 1
// Having said that, you can actually use greater values for M without too much risk, as the relations given here above are just for the worst case (i.e. theoretical).

#define SQRT2	1.41421356237
#define SQRT3	1.73205080757

#define SIZE 			15
#define HASH_MAGNITUDE	(6.0 / (SQRT2 + 1.0) - 1.0) // Perfect if: HASH_MAGNITUDE < 2 * (KERNEL + 1) / (SQRT2 + 1) - 1
#define KERNEL			2 // Perfect if: KERNEL >= floor ((HASH_MAGNITUDE + 1) * (SQRT2 + 1) / 2)

#define BORDER
//#define CENTER
#define DISTANCE
#define STRIPS
#define HEXAGONAL

float hash (in int index) {
	float x = float (index);
	return HASH_MAGNITUDE * 0.5 * sin (sin (x) * x + sin (x * x) * iTime);
}

vec2 pointInCell (in ivec2 cell) {
	int index = cell.x + cell.y * SIZE;
	vec2 point = vec2 (cell);
	#ifdef HEXAGONAL
	point.x += fract (point.y * 0.5) - 0.25;
	#endif
	return point + vec2 (hash (index), hash (index + 1)) * (0.5 + 0.5 * sin (iTime * 0.5));
}

void mainImage (out vec4 fragColor, in vec2 fragCoord) {
	vec2 p = float (SIZE) * (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;
	#ifdef HEXAGONAL
	p.y /= SQRT3 * 0.5;
	#endif
	ivec2 pCell = ivec2 (floor (p + 0.5));

	float dMin = HASH_MAGNITUDE + 1.0;
	vec2 pqMin;
	ivec2 minCell;
	for (int y = -KERNEL; y <= KERNEL; ++y) {
		for (int x = -KERNEL; x <= KERNEL; ++x) {
			ivec2 qCell = pCell + ivec2 (x, y);
			vec2 pq = pointInCell (qCell) - p;
			#ifdef HEXAGONAL
			pq.y *= SQRT3 * 0.5;
			#endif
			float d = dot (pq, pq);
			if (d < dMin) {
				dMin = d;
				pqMin = pq;
				minCell = qCell;
			}
		}
	}
	int col = minCell.x + minCell.y * SIZE;
	vec4 color = 0.6 + vec4 (hash (col), hash (col + 1), hash (col + 2), 0.0) * 0.8 / HASH_MAGNITUDE;

	#ifdef CENTER
	dMin = sqrt (dMin);
	#else
	dMin = HASH_MAGNITUDE + 1.0;
	#endif

	#ifdef BORDER
	for (int y = -KERNEL; y <= KERNEL; ++y) {
		for (int x = -KERNEL; x <= KERNEL; ++x) {
			ivec2 qCell = pCell + ivec2 (x, y);
			if (qCell != minCell) {
				vec2 pq = pointInCell (qCell) - p;
				#ifdef HEXAGONAL
				pq.y *= SQRT3 * 0.5;
				#endif
				dMin = min (dMin, dot (0.5 * (pqMin + pq), normalize (pq - pqMin)));
			}
		}
	}
	#endif

	color *= smoothstep (0.02, 0.1, dMin);

	#ifdef DISTANCE
	color *= 0.9 + 0.1 * sin (dMin * 40.0);
	#endif

	#ifdef STRIPS
	float strip = float (col);
	float stripCos = cos (strip);
	float stripSin = sin (strip);
	strip = mix (1.0, sin (40.0 * (pqMin.x * stripCos - pqMin.y * stripSin)), mod (strip * 0.5, 2.0));
	strip *= sin (40.0 * (pqMin.x * stripSin + pqMin.y * stripCos));
	color *= 0.8 + 0.2 * strip;
	#endif

	fragColor = color;
}

#define DELTA				0.01
#define RAY_LENGTH_MAX		50.0
#define RAY_STEP_MAX		50
#define LIGHT				vec3 (0.5, 0.75, 1.0)
#define BACK_COLOR			vec3 (0.25, 0.5, 1.0)
#define AMBIENT				0.2
#define SPECULAR_POWER		4.0
#define SPECULAR_INTENSITY	0.5
#define REFLECT_COUNT		2
#define FADE_POWER			2.0
#define GAMMA				(1.0 / 2.2)
#define M_PI				3.1415926535897932384626433832795

#define SHADOW

mat3 mRotate (in vec3 angle) {
	float c = cos (angle.x);
	float s = sin (angle.x);
	mat3 rx = mat3 (1.0, 0.0, 0.0, 0.0, c, s, 0.0, -s, c);

	c = cos (angle.y);
	s = sin (angle.y);
	mat3 ry = mat3 (c, 0.0, -s, 0.0, 1.0, 0.0, s, 0.0, c);

	c = cos (angle.z);
	s = sin (angle.z);
	mat3 rz = mat3 (c, s, 0.0, -s, c, 0.0, 0.0, 0.0, 1.0);

	return rz * ry * rx;
}

vec3 vRotateY (in vec3 p, in float angle) {
	float c = cos (angle);
	float s = sin (angle);
	return vec3 (c * p.x - s * p.z, p.y, c * p.z + s * p.x);
}

float sphere (in vec3 p, in float r) {
	return length (p) - r;
}

float box (in vec3 p, in vec3 b, in float r) {
	return length (max (abs (p) - b + r, 0.0)) - r;
}

float plane (in vec3 p, in vec3 n, in float d) {
	return dot (p, normalize (n)) + d;
}

float torusZ (in vec3 p, in float r1, in float r2) {
	vec2 q = vec2 (length (p.xy) - r1, p.z);
	return length (q) - r2;
}

float cylinderX (in vec3 p, in float r) {
 	return length (p.yz) - r;
}

float cylinderY (in vec3 p, in float r) {
 	return length (p.xz) - r;
}

vec3 twistY (in vec3 p, in float k, in float angle) {
	return vRotateY (p, angle + k * p.y);
}

float fixDistance (in float d, in float correction, in float k) {
	correction = max (correction, 0.0);
	k = clamp (k, 0.0, 1.0);
	return min (d, max ((d - DELTA) * k + DELTA, d - correction));
}

float getDistance (in vec3 p, out vec4 q) {

	// Ground
	vec3 q_ = p;
	q_.z += 0.8 * (sin (iTime + p.x * 0.5) + sin (p.y * 0.5));
	float dist = fixDistance (plane (q_, vec3 (-0.2, 0.0, 1.0), 5.0), 0.6, 0.8); // need to fix the distance because of the sine deformation
	q = vec4 (q_, 0.0);

	// Twisted box
	q_ = p;
	q_.x -= 5.0;
	q_ = twistY (q_, cos (iTime), iTime);
	float dist_ = fixDistance (box (q_, vec3 (1.0, 1.0, 1.0), 0.1), 0.2, 0.8); // need to fix the distance because of the twist
	if (dist_ < dist) {
		q = vec4 (q_, 1.0);
		dist = dist_;
	}

	// Simple box
	q_ = p;
	q_.y -= 3.0;
	q_ = mRotate (vec3 (0.7, 0.0, -0.2)) * q_;
	dist_ = box (q_, vec3 (1.0, 0.5, 1.0), 0.3);
	if (dist_ < dist) {
		q = vec4 (q_, 2.0);
		dist = dist_;
	}

	// Sphere
	q_ = p + vec3 (3.0, -1.0, sin (iTime * 2.0) - 1.0);
	dist_ = sphere (q_, 1.0);
	if (dist_ < dist) {
		q = vec4 (q_, 3.0);
		dist = dist_;
	}

	// Twisted torus
	q_ = p;
	q_.y += 4.0;
	q_ = twistY (q_, 2.0, 0.0);
	dist_ = fixDistance (torusZ (q_, 1.0, 0.3), 0.8, 0.5); // need to fix the distance because of the twist
	if (dist_ < dist) {
		q = vec4 (q_, 4.0);
		dist = dist_;
	}

	// Cylinders
	q_ = p;
	q_.z += 2.0;
	q_.y = mod (q_.y, 5.0) - 0.5 * 5.0;
	dist_ = cylinderX (q_, 0.2);
	if (dist_ < dist) {
		q = vec4 (q_, 5.0);
		dist = dist_;
	}
	q_ = p;
	q_.z += 2.0;
	q_.x = mod (q_.x, 5.0) - 0.5 * 5.0;
	dist_ = cylinderY (q_, 0.2);
	if (dist_ < dist) {
		q = vec4 (q_, 6.0);
		dist = dist_;
	}

	// Smallest distance
	return dist;
}

vec3 getObjectColor (in vec4 q, out float reflection) {
	float tint = 0.6 + 0.4 * sin (q.x * 10.0) * sin (q.y * 10.0) * sin (q.z * 10.0);
	if (q.w < 0.5) {
		reflection = 0.0;
		return (0.6 + 0.4 * tint) * vec3 (0.2, 1.0, 1.0);
	}
	if (q.w < 1.5) {
		reflection = 0.2;
		return tint * vec3 (1.0, 0.0, 0.0);
	}
	if (q.w < 2.5) {
		reflection = 0.0;
		return tint * vec3 (0.0, 0.0, 1.0);
	}
	if (q.w < 3.5) {
		reflection = 0.5;
		return tint * vec3 (1.0, 1.0, 0.0);
	}
	if (q.w < 4.5) {
		reflection = 0.0;
		return tint * vec3 (1.0, 0.2, 0.5);
	}
	reflection = 0.0;
	return tint * vec3 (0.5, 0.5, 0.6);
}

vec3 getNormal (in vec3 p) {
	vec4 q;
	vec2 h = vec2 (DELTA, 0.0);
	return normalize (vec3 (
		getDistance (p + h.xyy, q) - getDistance (p - h.xyy, q),
		getDistance (p + h.yxy, q) - getDistance (p - h.yxy, q),
		getDistance (p + h.yyx, q) - getDistance (p - h.yyx, q)
	));
}

void mainImage (out vec4 fragColor, in vec2 fragCoord) {

	// Old monitor effect
	vec2 frag = 2.0 * fragCoord.xy / iResolution.xy - 1.0;
	float round = pow (length (frag * frag), 6.0);
	frag.y *= 1.0 + round * 0.2;
	vec3 colorMonitor = vec3 (1.0 - round);
	colorMonitor *= vec3 (0.8, 1.0 + abs (mod (frag.y, 0.04) / 0.04 - 0.5), 0.8);

	// Define the ray corresponding to this fragment
	frag.x *= iResolution.x / iResolution.y;
	vec3 direction = normalize (vec3 (frag, 2.0));

	// Set the camera
	float angle = M_PI * sin (0.1 * iTime);
	vec3 origin = vec3 (10.0 * cos (angle), 10.0 * sin (angle), 2.5);
	direction = mRotate (vec3 (M_PI / 2.0 + 0.45, 0.0, angle - M_PI / 2.0)) * direction;

	// Compute the fragment color
	vec3 lightDirection = normalize (LIGHT);
	float moveAway = DELTA * 10.0;

	vec3 color = vec3 (0.0, 0.0, 0.0);
	float absorb = 1.0;
	float fade = 0.0;
	for (int reflectionIndex = 0; reflectionIndex < REFLECT_COUNT; ++reflectionIndex) {

		// Ray marching
		vec4 objectInfo;
		float rayLength = 0.0;
		for (int rayStep = 0; rayStep < RAY_STEP_MAX; ++rayStep) {
			float dist = getDistance (origin, objectInfo);
			rayLength += dist;
			if (dist < DELTA || rayLength > RAY_LENGTH_MAX) {
				break;
			}
			origin += dist * direction;
		}

		// Combine colors
		if (reflectionIndex == 0) {
			fade = pow (max (0.0, 1.0 - rayLength / RAY_LENGTH_MAX), FADE_POWER);
		} else {
			color *= 1.0 - absorb;
		}
		if (rayLength > RAY_LENGTH_MAX) {
			color += BACK_COLOR * absorb;
			break;
		}

		// Get the object color
		float reflection;
		vec3 objectColor = getObjectColor (objectInfo, reflection);

		// Lighting
		vec3 normal = getNormal (origin);
		direction = reflect (direction, normal);

		#ifdef SHADOW
		vec3 p = origin + moveAway * lightDirection;
		rayLength = 0.0;
		for (int rayStep = 0; rayStep < RAY_STEP_MAX; ++rayStep) {
			float dist = getDistance (p, objectInfo);
			rayLength += dist;
			if (dist < DELTA || rayLength > RAY_LENGTH_MAX) {
				break;
			}
			p += dist * lightDirection;
		}		
		if (rayLength < RAY_LENGTH_MAX) {
			objectColor *= AMBIENT;
		}
		else
		#endif
		{
			float diffuse = max (0.0, dot (normal, lightDirection));
			float specular = pow (max (0.0, dot (direction, lightDirection)), SPECULAR_POWER) * SPECULAR_INTENSITY;
			objectColor = (AMBIENT + diffuse) * objectColor + specular;
		}
		color += objectColor * absorb;

		// Next ray...
		if (reflection < DELTA) {
			break;
		}
		absorb *= reflection;
		origin += moveAway * direction;
	}

	// Fading
	color = mix (BACK_COLOR, color, fade);

	// Gamma correction
	color = pow (color, vec3 (GAMMA));

	// Set the fragment color
	fragColor = vec4 (color * colorMonitor, 1.0);
}

// From https://www.shadertoy.com/view/MdsXWM

const float samples = 6.0;

vec3 hsv2rgb (in vec3 hsv) {
	hsv.yz = clamp (hsv.yz, 0.0, 1.0);
	return hsv.z * (1.0 + 0.5 * hsv.y * (cos (2.0 * 3.14159 * (hsv.x + vec3 (0.0, 2.0 / 3.0, 1.0 / 3.0))) - 1.0));
}

void mainImage (out vec4 fragColor, in vec2 fragCoord) {
	vec2 p = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;
	p.x += 0.05 * cos (iTime + p.y * 20.0);
	vec3 color = vec3(0.0, 0.0, 0.2);
	float r = texture (iChannel0, vec2 (0.0)).x * 0.4;
	for(float d = 0.0; d < 1.0; d += 1.0 / samples) {
		vec3 c = hsv2rgb (vec3 (d + iTime, 1.0, 1.0));
		float v = texture (iChannel0, vec2 (d, 0.0)).x * 0.7;
		float a = 2.0 * 3.14159 * d + iTime;
		vec2 o = r * vec2 (cos (a), sin (a));
		color += c * v / length (p - o);
	}
	color /= samples;
	color = mix (vec3 (length (color)), color, smoothstep (r + 0.02, r + 0.05, length (p)));
	fragColor = vec4 (pow (color, vec3 (0.6 + r)), 1.0);
}

// Forked from: https://www.shadertoy.com/view/ldfXzn

#define DELTA				0.001
#define RAY_COUNT			7
#define RAY_LENGTH_MAX		100.0
#define RAY_STEP_MAX		100
#define LIGHT				vec3 (1.0, 1.0, -1.0)
#define REFRACT_FACTOR		0.6
#define REFRACT_INDEX		1.6
#define AMBIENT				0.2
#define SPECULAR_POWER		3.0
#define SPECULAR_INTENSITY	0.5
#define FADE_POWER			1.0
#define M_PI				3.1415926535897932384626433832795
#define GLOW_FACTOR			1.5
#define LUMINOSITY_FACTOR	2.0

mat3 mRotate (in vec3 angle) {
	float c = cos (angle.x);
	float s = sin (angle.x);
	mat3 rx = mat3 (1.0, 0.0, 0.0, 0.0, c, s, 0.0, -s, c);

	c = cos (angle.y);
	s = sin (angle.y);
	mat3 ry = mat3 (c, 0.0, -s, 0.0, 1.0, 0.0, s, 0.0, c);

	c = cos (angle.z);
	s = sin (angle.z);
	mat3 rz = mat3 (c, s, 0.0, -s, c, 0.0, 0.0, 0.0, 1.0);

	return rz * ry * rx;
}

vec3 k;
float getDistance (in vec3 p) {
	float repeat = 20.0;
	vec3 q = p + repeat * 0.5;
	k = floor (q / repeat);
	q -= repeat * (k + 0.5);
	p = mRotate (k) * q;

	float top = p.y - 3.0;
	float angleStep = M_PI / max (2.0, abs (k.x + 2.0 * k.y + 4.0 * k.z));
	float angle = angleStep * (0.5 + floor (atan (p.x, p.z) / angleStep));
	float side = cos (angle) * p.z + sin (angle) * p.x - 2.0;
	float bottom = -p.y - 3.0;

	return max (top, max (side, bottom));
}

vec3 getFragmentColor (in vec3 origin, in vec3 direction) {
	vec3 lightDirection = normalize (LIGHT);
	vec2 delta = vec2 (DELTA, 0.0);

	vec3 fragColor = vec3 (0.0, 0.0, 0.0);
	float intensity = 1.0;

	float distanceFactor = 1.0;
	float refractionRatio = 1.0 / REFRACT_INDEX;
	float rayStepCount = 0.0;
	for (int rayIndex = 0; rayIndex < RAY_COUNT; ++rayIndex) {

		// Ray marching
		float dist = RAY_LENGTH_MAX;
		float rayLength = 0.0;
		for (int rayStep = 0; rayStep < RAY_STEP_MAX; ++rayStep) {
			dist = distanceFactor * getDistance (origin);
			float distMin = max (dist, DELTA);
			rayLength += distMin;
			if (dist < 0.0 || rayLength > RAY_LENGTH_MAX) {
				break;
			}
			origin += direction * distMin;
			++rayStepCount;
		}

		// Check whether we hit something
		vec3 backColor = vec3 (0.0, 0.0, 0.1 + 0.2 * max (0.0, dot (-direction, lightDirection)));
		if (dist >= 0.0) {
			fragColor = fragColor * (1.0 - intensity) + backColor * intensity;
			break;
		}

		// Get the normal
		vec3 normal = normalize (distanceFactor * vec3 (
			getDistance (origin + delta.xyy) - getDistance (origin - delta.xyy),
			getDistance (origin + delta.yxy) - getDistance (origin - delta.yxy),
			getDistance (origin + delta.yyx) - getDistance (origin - delta.yyx)));

		// Basic lighting
		vec3 reflection = reflect (direction, normal);
		if (distanceFactor > 0.0) {
			float relfectionDiffuse = max (0.0, dot (normal, lightDirection));
			float relfectionSpecular = pow (max (0.0, dot (reflection, lightDirection)), SPECULAR_POWER) * SPECULAR_INTENSITY;
			float fade = pow (1.0 - rayLength / RAY_LENGTH_MAX, FADE_POWER);

			vec3 localColor = max (sin (k * k), 0.2);
			localColor = (AMBIENT + relfectionDiffuse) * localColor + relfectionSpecular;
			localColor = mix (backColor, localColor, fade);

			fragColor = fragColor * (1.0 - intensity) + localColor * intensity;
			intensity *= REFRACT_FACTOR;
		}

		// Next ray...
		vec3 refraction = refract (direction, normal, refractionRatio);
		if (dot (refraction, refraction) < DELTA) {
			direction = reflection;
			origin += direction * DELTA * 2.0;
		}
		else {
			direction = refraction;
			distanceFactor = -distanceFactor;
			refractionRatio = 1.0 / refractionRatio;
		}
	}

	// Return the fragment color
	return fragColor * LUMINOSITY_FACTOR + GLOW_FACTOR * rayStepCount / float (RAY_STEP_MAX * RAY_COUNT);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord) {

	// Define the ray corresponding to this fragment
	vec2 frag = (2.0 * fragCoord.xy - iResolution.xy) / iResolution.y;
	vec3 direction = normalize (vec3 (frag, 2.0));

	// Set the camera
	vec3 origin = vec3 ((15.0 * cos (iTime * 0.1)), 10.0 * sin (iTime * 0.2), 15.0 * sin (iTime * 0.1));
	vec3 forward = -origin;
	vec3 up = vec3 (sin (iTime * 0.3), 2.0, 0.0);
	mat3 rotation;
	rotation [2] = normalize (forward);
	rotation [0] = normalize (cross (up, forward));
	rotation [1] = cross (rotation [2], rotation [0]);
	direction = rotation * direction;

	// Set the fragment color
	fragColor = vec4 (getFragmentColor (origin, direction), 1.0);
}

vec3 hsv2rgb (in vec3 hsv) {
	hsv.yz = clamp (hsv.yz, 0.0, 1.0);
	return hsv.z * (1.0 + 0.5 * hsv.y * (cos (2.0 * 3.14159 * (hsv.x + vec3 (0.0, 2.0 / 3.0, 1.0 / 3.0))) - 1.0));
}

float rand (in vec2 seed) {
	return fract (sin (dot (seed, vec2 (12.9898, 78.233))) * 137.5453);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord) {
	vec2 frag = (2.0 * fragCoord.xy - iResolution.xy) / iResolution.y;
	frag *= 1.0 - 0.2 * cos (frag.yx) * sin (3.14159 * 0.5 * texture (iChannel0, vec2 (0.0)).x);
	frag *= 5.0;
	float random = rand (floor (frag));
	vec2 black = smoothstep (1.0, 0.8, cos (frag * 3.14159 * 2.0));
	vec3 color = hsv2rgb (vec3 (random, 1.0, 1.0));
	color *= black.x * black.y * smoothstep (1.0, 0.0, length (fract (frag) - 0.5));
	color *= 0.5 + 0.5 * cos (random + random * iTime + iTime + 3.14159 * 0.5 * texture (iChannel0, vec2 (0.7)).x);
	fragColor = vec4 (color, 1.0);
}

// LED watch: the two bars on the left represent hours, the two on the
// right minutes. LEDs in the first bar count for 6, LEDs in the third
// bar count for 10; LEDs in the 2 other bars count for 1.

#define M_PI 3.1415926535897932384626433832795
#define VERTICAL
#define HSV2RGB_FAST

float boxDist (in vec2 p, in vec2 b) {
	return length (max (abs (p) - b, 0.0));
}

float boxDist (in vec2 p, in vec3 b) {
	return length (max (abs (p) - b.xy + b.z, 0.0)) - b.z;
}

vec3 hsv2rgb (in vec3 hsv) {
	#ifdef HSV2RGB_SAFE
	hsv.yz = clamp (hsv.yz, 0.0, 1.0);
	#endif
	#ifdef HSV2RGB_FAST
	return hsv.z * (1.0 + 0.5 * hsv.y * (cos (2.0 * M_PI * (hsv.x + vec3 (0.0, 2.0 / 3.0, 1.0 / 3.0))) - 1.0));
	#else
	return hsv.z * (1.0 + hsv.y * clamp (abs (fract (hsv.x + vec3 (0.0, 2.0 / 3.0, 1.0 / 3.0)) * 6.0 - 3.0) - 2.0, -1.0, 0.0));
	#endif
}

float rand (in vec2 seed) {
	return fract (sin (dot (seed, vec2 (12.9898, 78.233))) * 137.5453);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord) {

	// Get the fragment's position in the watch space
	#ifndef VERTICAL
	vec2 frag = (2.0 * fragCoord.xy - iResolution.xy) / iResolution.x;
	#else
	vec2 frag = (2.0 * fragCoord.yx - iResolution.yx) / iResolution.y;
	#endif
	frag *= 1.0 - vec2 (0.02, 0.04) * cos (frag.yx * 2.0) * max (0.0, 50.0 * sin (iTime * M_PI * 2.0 / 5.0) - 49.0);
	#ifndef VERTICAL
	frag *= 8.0;
	#else
	frag *= vec2 (6.0, -6.0);
	#endif

	// Define the panel and the border
	vec3 panelSize = vec3 (5.0, 2.2, 1.0);
	float panelDist = -boxDist (frag, panelSize);
	float borderDist = boxDist (frag, panelSize + 0.1);

	// Define the LEDs
	vec2 led = frag;
	led.x -= 0.5;
	vec2 ledId = floor (led);
	float random = rand (ledId);
	float ledThresholdBar2 = step (-1.5, ledId.y);
	float ledThresholdBar4 = step (0.5, ledId.y);
	float ledTimeModulo = 10.0 - 4.0 * ledThresholdBar2 - 2.0 * ledThresholdBar4;
	float ledMode = max (0.0, cos (iTime * 0.5));
	float ledIntensity = mix (0.8 * step (ledId.x + 6.0, mod (iDate.w / (60.0 + 540.0 * ledThresholdBar2 + 3000.0 * step (-0.5, ledId.y) + 18000.0 * ledThresholdBar4), ledTimeModulo)), 0.5 + 0.5 * cos (random + random * iTime + iTime), ledMode);
	float ledDist = boxDist (fract (led) - 0.5, vec2 (0.2, 0.03));

	// Create everything (panel, casing, small indicator and LEDs)
	vec3 color = vec3 (0.2, 0.2, 0.2) * (0.7 + 0.3 * cos (frag.x * M_PI / panelSize.x)) * smoothstep (0.1, 0.15, panelDist) * smoothstep (0.2, 0.25, abs (fract (frag.y) - 0.5));
	color += (0.4 + 0.2 * cos (frag.x + frag.y + iTime)) * smoothstep (0.1, 0.0, max (borderDist, panelDist));
	color += 0.3 * smoothstep (0.05, 0.0, boxDist (frag + vec2 (0.0, 1.9), vec3 (0.04, 0.1, 0.04)));
	color += mix (vec3 (0.1) * smoothstep (0.025, 0.0, ledDist), hsv2rgb (vec3 (random + iTime * 0.1, ledMode, 1.0)) * smoothstep (0.25, 0.0, ledDist), ledIntensity) * step (-4.5, frag.x) * step (5.5 - ledTimeModulo, -frag.x) * step (-2.0, -abs (frag.y));

	// Get the fragment's position in the background tile space
	#ifndef VERTICAL
	frag = 15.0 * fragCoord.xy / iResolution.x;
	#else
	frag = 15.0 * fragCoord.yx / iResolution.y;
	#endif
	frag -= vec2 (iTime, sin (iTime));

	// Create the background tiles
	random = rand (floor (frag));
	color += hsv2rgb (vec3 (random, 1.0, 1.0)) * smoothstep (0.6, 0.0, length (fract (frag) - 0.5)) * (0.2 + 0.2 * cos (random + random * iTime + iTime)) * smoothstep (0.0, 0.2, borderDist);

	// Set the fragment color
	fragColor = vec4 (color, 1.0);
}

// Yellow LEDs represent seconds, red ones minutes, and green ones
// hours; all numbers are shown in their binary format (LEDs values
// from right to left: 1, 2, 4, 8, 16 and 32).

#define M_PI 3.14159265359
#define HSV2RGB_FAST

vec3 hsv2rgb (in vec3 hsv) {
	#ifdef HSV2RGB_SAFE
	hsv.yz = clamp (hsv.yz, 0.0, 1.0);
	#endif
	#ifdef HSV2RGB_FAST
	return hsv.z * (1.0 + 0.5 * hsv.y * (cos (2.0 * M_PI * (hsv.x + vec3 (0.0, 2.0 / 3.0, 1.0 / 3.0))) - 1.0));
	#else
	return hsv.z * (1.0 + hsv.y * clamp (abs (fract (hsv.x + vec3 (0.0, 2.0 / 3.0, 1.0 / 3.0)) * 6.0 - 3.0) - 2.0, -1.0, 0.0));
	#endif
}

float segDist (in vec2 p, in vec4 ab, in float cut) {
	p -= ab.xy;
	ab.zw -= ab.xy;
	float l = dot (ab.zw, ab.zw);
	cut *= sqrt (l);
	return length (p - ab.zw * clamp (dot (p, ab.zw), cut, l - cut) / l);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord) {

	// Get the fragment's position
	vec2 frag = 7.0 * (2.0 * fragCoord.xy - iResolution.xy) / iResolution.x;

	// Define the background lights
	vec3 lightColor = vec3(0.0, 0.0, 0.2);
	const float lightCount = 5.0;
	for(float lightId = 0.0; lightId < 1.0; lightId += 1.0 / lightCount) {
		float lightAngle = 2.0 * M_PI * lightId + iTime;
		lightColor += hsv2rgb (vec3 (lightId + iTime * 0.1, 1.0, 1.0)) / length (frag - 2.0 * vec2 (cos (lightAngle), sin (lightAngle))) * (3.0 + 2.0 * cos (iTime * (1.0 + lightId))) ;
	}
	lightColor /= lightCount;

	// Define the casing color
	float casingColor = 0.3 + 0.2 * cos (frag.x + frag.y + iTime);

	// Rotate the watch every 5s
	float fragAngle = 0.1 * sin (iTime * 2.0 * M_PI) * step (4.0, mod (iTime, 5.0));
	vec2 fragRotate = vec2 (cos (fragAngle), sin (fragAngle));
	frag = mat2 (fragRotate.x, fragRotate.y, -fragRotate.y, fragRotate.x) * frag;

	// Define the panel and the border
	float panelDist = length (frag) - 3.5;
	float borderDist = panelDist - 0.05;

	// Define the LEDs
	float ledThresholdBar2 = step (-0.5, frag.y);
	float ledThresholdBar3 = step (0.5, frag.y);
	vec2 ledPosition = frag + vec2 (0.5 * ledThresholdBar3, 0.5);
	float ledDist = length (fract (ledPosition) - 0.5);
	float ledDisplay = step (ledThresholdBar3 * 0.5 - 3.0, -abs (frag.x)) * step (-1.5, -abs (frag.y));

	float ledTime = mod (iDate.w / (1.0 + 59.0 * ledThresholdBar2 + 3540.0 * ledThresholdBar3), 60.0 - 36.0 * ledThresholdBar3);
	vec3 ledColor = vec3 (1.0 - ledThresholdBar3, ledThresholdBar3 - ledThresholdBar2 + 1.0, 0.0);
	ledColor *= step (0.5, fract (ledTime / exp2 (3.0 - floor (ledPosition.x))));

	// Define the indicators
	float indicatorDist = min (abs (length (frag - vec2 (-1.0, 1.7)) - 0.08), abs (length (frag - vec2 (-1.0, 1.84)) - 0.06));
	indicatorDist = min (indicatorDist, segDist (frag, vec4 (-1.0, 1.8, -1.0, 1.0), 0.3));
	indicatorDist = min (indicatorDist, segDist (frag, vec4 (-1.0, 1.0, -0.5, 0.0), 0.3));
	indicatorDist = min (indicatorDist, segDist (frag, vec4 (-0.5, 0.0, -0.5, -1.0), 0.3));

	// Create everything (lights, panel, casing, LEDs, indicators)
	vec3 color = lightColor * smoothstep (0.0, 0.2, borderDist);
	color += vec3 (0.1, 0.1, 0.3) * (0.7 + 0.3 * cos (frag.y * M_PI / 3.5)) * smoothstep (0.0, -0.1, panelDist) * (1.0 - smoothstep (0.15, 0.1, ledDist) * ledDisplay);
	color += casingColor * smoothstep (0.05, 0.0, max (borderDist, -panelDist));
	color += ledColor * smoothstep (0.25, 0.1, ledDist) * ledDisplay;
	color += 0.1 * smoothstep (0.05, 0.0, indicatorDist);

	// Set the fragment color
	fragColor = vec4 (color, 1.0);
}

#ifdef GL_ES
precision highp float;
#endif

#define HACK // Hack to speed up the voxel (= reuse the raymarching position)

#define CAMERA_FOCAL_LENGTH	1.5
#define DELTA				0.01
#define RAY_STEP_MAX		80
#define SQRT3				1.73205080757
#define PI					3.14159265359

float scene (in vec3 p) {

	// Just a sphere cropped by smaller spheres, into a big sphere
	float r1 = length (p);
	float r2 = length (abs (p) - 5.0);
	return min (max (r1 - 8.5, 3.0 - r2), 32.0 - r1);
}

vec3 mapping (in vec3 position, in vec3 normal) {
	position = cos (position * 2.0 * PI);
	return (0.8 + 0.2 * position.x * position.y * position.z) * (0.4 + 0.6 * normal);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord) {

	// Define the ray corresponding to this fragment
	vec3 ray = normalize (vec3 ((2.0 * fragCoord.xy - iResolution.xy) / iResolution.y, CAMERA_FOCAL_LENGTH));

	// Set the camera
	vec3 origin = vec3 (20.0 * cos (iTime), 12.0 * sin (iTime * 0.5), 20.0 * sin (iTime));
	vec3 forward = -origin;
	vec3 up = vec3 (cos (iTime), 1.0, 0.0);
	mat3 rotation;
	rotation [2] = normalize (forward);
	rotation [0] = normalize (cross (up, forward));
	rotation [1] = cross (rotation [2], rotation [0]);
	ray = rotation * ray;

	// Raymarching
	#ifdef HACK
	float hack = 1.0 / 0.0;
	#endif
	vec3 rayPosition = origin;
	for (int rayStep = 0; rayStep < RAY_STEP_MAX; ++rayStep) {
		float dist = scene (rayPosition);
		if (dist < DELTA) {
			break;
		}
		rayPosition += dist * ray;

		#ifdef HACK
		hack = min (hack, dist);
		origin = mix (origin, rayPosition, step (SQRT3, hack));
		#endif
	}
	vec2 h = vec2 (DELTA, 0.0);
	vec3 normal = normalize (vec3 (
		scene (rayPosition + h.xyy) - scene (rayPosition - h.xyy),
		scene (rayPosition + h.yxy) - scene (rayPosition - h.yxy),
		scene (rayPosition + h.yyx) - scene (rayPosition - h.yyx)));
	vec3 colorRaymarching = mapping (rayPosition, normal);

	// Voxel
	#ifdef HACK
	origin -= SQRT3 * ray;
	#endif
	vec3 raySign = sign (ray);
	vec3 rayIncrement = raySign / ray;
	vec3 rayTime = (0.5 + raySign * (0.5 - fract (origin + 0.5))) * rayIncrement;
	rayPosition = floor (origin + 0.5);
	vec3 rayAxis = vec3 (0.0);
	for (int rayStep = 0; rayStep < RAY_STEP_MAX; ++rayStep) {
		rayAxis = step (rayTime.xyz, rayTime.yzx) * step (rayTime.xyz, rayTime.zxy);
		rayPosition += rayAxis * raySign;
		if (scene (rayPosition) < DELTA) {
			break;
		}
		rayTime += rayAxis * rayIncrement;
	}
	vec3 colorVoxel = mapping (origin + ray * min (rayTime.x, min (rayTime.y , rayTime.z)), -rayAxis * raySign);

	// Set the fragment color
	fragColor = vec4 (mix (colorRaymarching, colorVoxel, smoothstep (-0.2, 0.2, cos (iTime * 0.8))), 1.0);
}

#define M_PI		3.1415926535897932384626433832795
#define ITERATION	3

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
	// Inputs
	float touchPressure = step (0.0, iMouse.z) * (0.6 + 0.4 * cos (iTime * 4.0));
	const float touchRadius = 0.3;
	const float gridRadius = 0.5;
	const float gridResolution = 30.0;
	vec4 gridColor = vec4 (1.0, 1.0 - touchPressure, 0.0, 1.0);

	// Get the position of this fragment
	vec3 frag = vec3 (fragCoord.xy / iResolution.y, 0.0);

	// Get the touch information
	vec2 touchPosition = iMouse.xy / iResolution.y;
	float touchDistance = length (frag.xy - touchPosition);

	// Raymarching
	vec3 ray = normalize (frag - vec3 (0.5 * iResolution.x / iResolution.y, 0.5, -10.0));
	for (int i = 0; i < ITERATION; ++i)
	{
		float deformation = 0.5 + 0.5 * cos (M_PI * min (touchDistance / touchRadius, 1.0));
		frag += (touchPressure * deformation - frag.z) * ray;
		touchDistance = length (frag.xy - touchPosition);
	}

	// Get the color from the texture
	vec4 color = texture (iChannel0, vec2 (frag.x * iResolution.y / iResolution.x, -frag.y));

	// Add the grid
	vec2 gridPosition = smoothstep (0.05, 0.1, abs (fract (frag.xy * gridResolution) - 0.5));
	color = mix (color, gridColor, (1.0 - gridPosition.x * gridPosition.y) * smoothstep (gridRadius * touchPressure, 0.0, touchDistance));

	// Set the fragment color
	fragColor = color;
}

// Parameters
#define CAMERA_FOCAL_LENGTH	1.2
#define DOT_COUNT			100.0
#define SOUND
#define MOUSE
#define HSV2RGB_FAST

// Constants
#define PI	3.14159265359

// PRNG
float rand (in vec2 seed) {
	return fract (sin (dot (seed, vec2 (12.9898, 78.233))) * 137.5453);
}

// HSV to RGB
vec3 hsv2rgb (in vec3 hsv) {
	#ifdef HSV2RGB_SAFE
	hsv.yz = clamp (hsv.yz, 0.0, 1.0);
	#endif
	#ifdef HSV2RGB_FAST
	return hsv.z * (1.0 + 0.5 * hsv.y * (cos (2.0 * PI * (hsv.x + vec3 (0.0, 2.0 / 3.0, 1.0 / 3.0))) - 1.0));
	#else
	return hsv.z * (1.0 + hsv.y * clamp (abs (fract (hsv.x + vec3 (0.0, 2.0 / 3.0, 1.0 / 3.0)) * 6.0 - 3.0) - 2.0, -1.0, 0.0));
	#endif
}

// Main function
void mainImage (out vec4 fragColor, in vec2 fragCoord) {

	// Define the ray corresponding to this fragment
	vec3 ray = vec3 ((2.0 * fragCoord.xy - iResolution.xy) / iResolution.y, CAMERA_FOCAL_LENGTH);

	// Get the music info
	#ifdef SOUND
	float soundBass = texture (iChannel0, vec2 (0.0)).x;
	float soundTreble = texture (iChannel0, vec2 (0.9, 0.0)).x;
	#else
	float soundBass = 0.6 + 0.4 * cos (iTime * 0.2);
	float soundTreble = 0.5 + 0.5 * cos (iTime * 1.2);
	#endif

	// Define the number of rows
	float dotRowCount = floor (20.0 + 60.0 * soundTreble * soundBass) * 2.0;

	// Compute the orientation of the camera
	float yawAngle = cos (iTime * 2.0);
	float pitchAngle = 2.0 * PI * cos (iTime * 0.2 + soundTreble * 0.4);
	#ifdef MOUSE
	yawAngle += 2.0 * PI * iMouse.x / iResolution.x;
	pitchAngle += PI * (1.0 - iMouse.y / iResolution.y);
	#endif

	float cosYaw = cos (yawAngle);
	float sinYaw = sin (yawAngle);
	float cosPitch = cos (pitchAngle);
	float sinPitch = sin (pitchAngle);

	mat3 cameraOrientation;
	cameraOrientation [0] = vec3 (cosYaw, 0.0, -sinYaw);
	cameraOrientation [1] = vec3 (sinYaw * sinPitch, cosPitch, cosYaw * sinPitch);
	cameraOrientation [2] = vec3 (sinYaw * cosPitch, -sinPitch, cosYaw * cosPitch);

	ray = cameraOrientation * ray;

	// Compute the position of the camera
	float cameraDist = -2.0 * (cos (iTime) * cos (iTime * 3.5) + soundBass);
	vec3 cameraPosition = cameraOrientation [2] * cameraDist;

	// Compute the intersection point (ray / cylinder)
	float a = dot (ray.xz, ray.xz);
	float b = dot (cameraPosition.xz, ray.xz);
	float c = b * b - a * (dot (cameraPosition.xz, cameraPosition.xz) - 1.0);
	float ok = step (0.0, c);
	c = sqrt (c);
	vec3 hit;
	if (b < -c) {
		hit = cameraPosition - ray * (b + c) / a;
		if (abs (hit.y * DOT_COUNT / PI + 1.0) > dotRowCount) {
			hit = cameraPosition - ray * (b - c) / a;
		}
	} else {
		hit = cameraPosition - ray * (b - c) / a;
	}
	vec2 frag = vec2 ((atan (hit.z, hit.x) + PI) * DOT_COUNT, hit.y * DOT_COUNT + PI) / (2.0 * PI);

	// Compute the fragment color
	vec2 id = floor (frag);
	float random = rand (id);
	vec3 color = hsv2rgb (vec3 (iTime * 0.05 + id.y * 0.005, 1.0, 1.0));
	color += 0.5 * cos (random * vec3 (1.0, 2.0, 3.0));
	color *= smoothstep (0.5, 0.1, length (fract (frag) - 0.5));
	color *= 0.5 + 1.5 * step (0.9, cos (random * iTime * 5.0));
	color *= 0.5 + 0.5 * cos (random * iTime + PI * 0.5 * soundTreble);
	color *= smoothstep (dotRowCount, 0.0, (abs (id.y + 0.5) - 1.0) * 2.0);
	fragColor = vec4 (color * ok, 1.0);
}

// Parameters
#define CAMERA_FOCAL_LENGTH		1.5
#define REFLECT_COUNT 			1
#define REFLECT_INDEX			0.4
#define VOXEL_STEP_INCIDENT		80.0
#define VOXEL_STEP_REFLECTED	20.0
#define SOUND
#define MOUSE
#define HSV2RGB_FAST

// Constants
#define PI		3.14159265359
#define SQRT2	1.41421356237
#define DELTA	0.01

// PRNG
// From https://www.shadertoy.com/view/4djSRW
float rand (in vec2 seed) {
	seed = fract (seed * vec2 (5.3983, 5.4427));
	seed += dot (seed.yx, seed.xy + vec2 (21.5351, 14.3137));
	return fract (seed.x * seed.y * 95.4337);
}

// HSV to RGB
vec3 hsv2rgb (in vec3 hsv) {
	#ifdef HSV2RGB_SAFE
	hsv.yz = clamp (hsv.yz, 0.0, 1.0);
	#endif
	#ifdef HSV2RGB_FAST
	return hsv.z * (1.0 + 0.5 * hsv.y * (cos (2.0 * PI * (hsv.x + vec3 (0.0, 2.0 / 3.0, 1.0 / 3.0))) - 1.0));
	#else
	return hsv.z * (1.0 + hsv.y * clamp (abs (fract (hsv.x + vec3 (0.0, 2.0 / 3.0, 1.0 / 3.0)) * 6.0 - 3.0) - 2.0, -1.0, 0.0));
	#endif
}

// Main function
void mainImage (out vec4 fragColor, in vec2 fragCoord) {

	// Define the ray corresponding to this fragment
	vec3 ray = normalize (vec3 ((2.0 * fragCoord.xy - iResolution.xy) / iResolution.y, CAMERA_FOCAL_LENGTH));

	// Get the music info
	#ifdef SOUND
	float soundBass = texture (iChannel0, vec2 (0.0)).x;
	float soundTreble = texture (iChannel0, vec2 (0.9, 0.0)).x;
	#else
	float soundBass = 0.6 + 0.4 * cos (iTime * 0.2);
	float soundTreble = 0.5 + 0.5 * cos (iTime * 1.2);
	#endif

	// Set the camera
	vec3 origin = vec3 (0.0, 10.0 - 8.0 * cos (iTime * 0.3), iTime * 10.0);
	float cameraAngle = iTime * 0.1;
	#ifdef MOUSE
	cameraAngle += 2.0 * PI * iMouse.x / iResolution.x;
	#endif
	vec3 cameraForward = vec3 (cos (cameraAngle), cos (iTime * 0.3) - 1.5, sin (cameraAngle));
	vec3 cameraUp = vec3 (0.2 * cos (iTime * 0.7), 1.0, 0.0);
	mat3 cameraRotation;
	cameraRotation [2] = normalize (cameraForward);
	cameraRotation [0] = normalize (cross (cameraUp, cameraForward));
	cameraRotation [1] = cross (cameraRotation [2], cameraRotation [0]);
	ray = cameraRotation * ray;

	// Handle reflections
	vec3 colorMixed = vec3 (0.0);
	float absorb = 1.0;
	float voxelStepStop = VOXEL_STEP_INCIDENT;
	for (int reflectNumber = 0; reflectNumber <= REFLECT_COUNT; ++reflectNumber) {

		// Voxel
		vec2 voxelSign = sign (ray.xz);
		vec2 voxelIncrement = voxelSign / ray.xz;
		float voxelTimeCurrent = 0.0;
		vec2 voxelTimeNext = (0.5 + voxelSign * (0.5 - fract (origin.xz + 0.5))) * voxelIncrement;
		vec2 voxelPosition = floor (origin.xz + 0.5);
		float voxelHeight = 0.0;
		bool voxelDone = false;
		vec3 voxelNormal = vec3 (0.0);
		for (float voxelStep = 1.0; voxelStep <= VOXEL_STEP_INCIDENT; ++voxelStep) {

			// Compute the height of this column
			voxelHeight = 4.0 * rand (voxelPosition)* smoothstep (0.2, 0.8, soundBass) * sin (soundTreble * PI * 0.5 + voxelPosition.x * voxelPosition.y);

			// Check whether we hit the side of the column
			if (voxelDone = voxelHeight > origin.y + voxelTimeCurrent * ray.y) {
				break;
			}

			// Check whether we hit the top of the column
			float timeNext = min (voxelTimeNext.x, voxelTimeNext.y);
			float timeIntersect = (voxelHeight - origin.y) / ray.y;
			if (voxelDone = timeIntersect > voxelTimeCurrent && timeIntersect < timeNext) {
				voxelTimeCurrent = timeIntersect;
				voxelNormal = vec3 (0.0, 1.0, 0.0);
				break;
			}

			// Next voxel...
			#if REFLECT_COUNT > 0
			if (voxelStep >= voxelStepStop) {
				break;
			}
			#endif
			voxelTimeCurrent = timeNext;
			voxelNormal.xz = step (voxelTimeNext.xy, voxelTimeNext.yx);
			voxelTimeNext += voxelNormal.xz * voxelIncrement;
			voxelPosition += voxelNormal.xz * voxelSign;
		}
		if (!voxelDone) {
			break;
		}
		origin += voxelTimeCurrent * ray;

		// Compute the local color
		vec3 mapping = origin;
		mapping.y -= voxelHeight + 0.5;
		mapping *= 1.0 - voxelNormal;
		mapping += 0.5;
		float id = rand (voxelPosition);
		vec3 color = hsv2rgb (vec3 (id + (iTime + floor (mapping.y)) * 0.05, 1.0, 0.7 + 0.3 * cos (id * iTime + PI * soundTreble)));
		color *= smoothstep (1.0 - 0.4 * cos (soundBass * PI), 0.1, length (fract (mapping) - 0.5));
		color *= 0.5 + smoothstep (0.5, 0.9, cos (id * 100.0 + iTime * 0.5));
		color *= 1.0 - voxelTimeCurrent / voxelStepStop * SQRT2;

		// Mix the colors
		#if REFLECT_COUNT == 0
		colorMixed = color;
		#else
		colorMixed += color * absorb;
		absorb *= REFLECT_INDEX;

		// Reflection
		ray = reflect (ray, voxelNormal);
		origin += ray * DELTA;
		voxelStepStop = VOXEL_STEP_REFLECTED;
		#endif
	}

	// Set the fragment color
	fragColor = vec4 (colorMixed, 1.0);
}

// Parameters
#define CAMERA_FOCAL_LENGTH	1.5
#define VOXEL_STEP			50.0
#define SOUND
#define MOUSE
#define HSV2RGB_FAST

// Constants
#define PI		3.14159265359
#define SQRT2	1.41421356237

// PRNG
// From https://www.shadertoy.com/view/4djSRW
float rand (in vec2 seed) {
	seed = fract (seed * vec2 (5.3983, 5.4427));
	seed += dot (seed.yx, seed.xy + vec2 (21.5351, 14.3137));
	return fract (seed.x * seed.y * 95.4337);
}

// HSV to RGB
vec3 hsv2rgb (in vec3 hsv) {
	#ifdef HSV2RGB_SAFE
	hsv.yz = clamp (hsv.yz, 0.0, 1.0);
	#endif
	#ifdef HSV2RGB_FAST
	return hsv.z * (1.0 + 0.5 * hsv.y * (cos (2.0 * PI * (hsv.x + vec3 (0.0, 2.0 / 3.0, 1.0 / 3.0))) - 1.0));
	#else
	return hsv.z * (1.0 + hsv.y * clamp (abs (fract (hsv.x + vec3 (0.0, 2.0 / 3.0, 1.0 / 3.0)) * 6.0 - 3.0) - 2.0, -1.0, 0.0));
	#endif
}

// Main function
void mainImage (out vec4 fragColor, in vec2 fragCoord) {

	// Define the ray corresponding to this fragment
	vec3 ray = normalize (vec3 ((2.0 * fragCoord.xy - iResolution.xy) / iResolution.y, CAMERA_FOCAL_LENGTH));

	// Get the music info
	#ifdef SOUND
	float soundBass = texture (iChannel0, vec2 (0.0)).x;
	float soundTreble = texture (iChannel0, vec2 (0.9, 0.0)).x;
	#else
	float soundBass = 0.6 + 0.4 * cos (iTime * 0.2);
	float soundTreble = 0.5 + 0.5 * cos (iTime * 1.2);
	#endif

	// Set the camera
	vec3 origin = vec3 (0.0, 6.0 - 3.0 * cos (iTime * 0.3), iTime * 2.0 + 700.0 * (0.5 + 0.5 * sin (iTime * 0.1)));
	float cameraAngle = iTime * 0.1;
	#ifdef MOUSE
	cameraAngle += 2.0 * PI * iMouse.x / iResolution.x;
	#endif
	vec3 cameraForward = vec3 (cos (cameraAngle), cos (iTime * 0.3) - 1.5, sin (cameraAngle));
	vec3 cameraUp = vec3 (0.2 * cos (iTime * 0.7), 1.0, 0.0);
	mat3 cameraRotation;
	cameraRotation [2] = normalize (cameraForward);
	cameraRotation [0] = normalize (cross (cameraUp, cameraForward));
	cameraRotation [1] = cross (cameraRotation [2], cameraRotation [0]);
	ray = cameraRotation * ray;

	// Voxel
	vec3 color = vec3 (0.0);

    vec2 voxelSign = sign (ray.xz);
	vec2 voxelIncrement = voxelSign / ray.xz;
	float voxelTimeCurrent = 0.0;
	vec2 voxelTimeNext = (0.5 + voxelSign * (0.5 - fract (origin.xz + 0.5))) * voxelIncrement;
	vec2 voxelPosition = floor (origin.xz + 0.5);
	float voxelHeight = 0.0;
	bool voxelDone = false;
	vec3 voxelNormal = vec3 (0.0);
	for (float voxelStep = 1.0; voxelStep <= VOXEL_STEP; ++voxelStep) {

		// Compute the height of this column
		voxelHeight = 2.0 * rand (voxelPosition) * smoothstep (0.2, 0.5, soundBass) * sin (soundBass * 8.0 + voxelPosition.x * voxelPosition.y) - 5.0 * (0.5 + 0.5 * cos (voxelPosition.y * 0.15));

		// Check whether we hit the side of the column
		if (voxelDone = voxelHeight > origin.y + voxelTimeCurrent * ray.y) {
			break;
		}

		// Check whether we hit the top of the column
		float timeNext = min (voxelTimeNext.x, voxelTimeNext.y);
		float timeIntersect = (voxelHeight - origin.y) / ray.y;
		if (voxelDone = timeIntersect > voxelTimeCurrent && timeIntersect < timeNext) {
			voxelTimeCurrent = timeIntersect;
			voxelNormal = vec3 (0.0, 1.0, 0.0);
			break;
		}

		// Next voxel...
		voxelTimeCurrent = timeNext;
		voxelNormal.xz = step (voxelTimeNext.xy, voxelTimeNext.yx);
		voxelTimeNext += voxelNormal.xz * voxelIncrement;
		voxelPosition += voxelNormal.xz * voxelSign;
	}
	if (voxelDone) {
		origin += voxelTimeCurrent * ray;

		// Compute the local color
		vec3 mapping = origin;
		mapping.y -= voxelHeight + 0.5;
		mapping *= 1.0 - voxelNormal;
		mapping += 0.5;
		float id = rand (voxelPosition);
		color = hsv2rgb (vec3 ((iTime + floor (mapping.y)) * 0.05 + voxelPosition.x * 0.01, smoothstep (0.2, 0.4, soundBass), 0.7 + 0.3 * cos (id * iTime + PI * soundTreble)));
		color *= smoothstep (0.8 - 0.6 * cos (soundBass * PI), 0.1, length (fract (mapping) - 0.5));
		color *= 0.5 + smoothstep (0.90, 0.95, cos (id * 100.0 + soundTreble * PI * 0.5 + iTime * 0.5));
		color *= 1.0 - voxelTimeCurrent / VOXEL_STEP * SQRT2;
	}

	// Set the fragment color
	fragColor = vec4 (color, 1.0);
}

///////////////////////////////////////////////////////////////////////////////
//                                                                           //
//  GGGG IIIII  AAA  N   N TTTTT     PPPP   AAA   CCCC     M   M  AAA  N   N //
// G       I   A   A NN  N   T       P   P A   A C         MM MM A   A NN  N //
// G  GG   I   AAAAA N N N   T       PPPP  AAAAA C     --- M M M AAAAA N N N //
// G   G   I   A   A N  NN   T       P     A   A C         M   M A   A N  NN //
//  GGGG IIIII A   A N   N   T       P     A   A  CCCC     M   M A   A N   N //
//                                                                           //
///////////////////////////////////////////////////////////////////////////////

// Parameters
#define VOXEL_RESOLUTION	1.5
#define VOXEL_LIGHTING
#define SHADOW
#define GROUND
#define GHOST
#define MOUSE
#define HSV2RGB_FAST

#define CAMERA_FOCAL_LENGTH	8.0
#define DELTA				0.01
#define RAY_LENGTH_MAX		500.0
#define RAY_STEP_MAX		100.0
#define AMBIENT				0.2
#define SPECULAR_POWER		2.0
#define SPECULAR_INTENSITY	0.3
#define SHADOW_LENGTH		150.0
#define SHADOW_POWER		3.0
#define FADE_POWER			1.0
#define BACKGROUND			0.7
#define GLOW				0.4
#define GAMMA				0.8

// Math constants
#define PI		3.14159265359
#define SQRT3	1.73205080757

// Global variable to handle the glow effect
float glowCounter;

// PRNG (from https://www.shadertoy.com/view/4djSRW)
float rand (in vec3 seed) {
	seed = fract (seed * vec3 (5.3983, 5.4427, 6.9371));
	seed += dot (seed.yzx, seed.xyz + vec3 (21.5351, 14.3137, 15.3219));
	return fract (seed.x * seed.y * seed.z * 95.4337);
}

// Distance to the voxel
float distVoxel (in vec3 p) {

	// Update the glow counter
	++glowCounter;

	// Rounded box
	const float voxelRadius = 0.25;
	return length (max (abs (p) - 0.5 + voxelRadius, 0.0)) - voxelRadius;
}

// Distance to the scene and color of the closest point
vec2 distScene (in vec3 p, out vec3 P) {

	// Update the glow counter
	++glowCounter;

	// Scaling
	p *= VOXEL_RESOLUTION;

	// Velocity, period of the waves, spacing of the gums
	float v = VOXEL_RESOLUTION * floor (iTime * 100.0 / VOXEL_RESOLUTION);
	const float k1 = 0.05;
	const float k2 = 60.0;

	// Giant Pac-Man
	float body = length (p);
	body = max (body - 32.0, 27.0 - body);
	float eyes = 6.0 - length (vec3 (abs (p.x) - 12.5, p.y - 19.5, p.z - 20.0));
	float mouthAngle = PI * (0.07 + 0.07 * cos (2.0 * v * PI / k2));
	float mouthTop = dot (p, vec3 (0.0, -cos (mouthAngle), sin (mouthAngle))) - 2.0;
	mouthAngle *= 2.5;
	float mouthBottom = dot (p, vec3 (0.0, cos (mouthAngle), sin (mouthAngle)));
	float pacMan = max (max (body, eyes), min (mouthTop, mouthBottom));
	vec2 d = vec2 (pacMan, 0.13);
	P = p;

	// Gums
	vec3 q = vec3 (p.xy, mod (p.z + v, k2) - k2 * 0.5);
	float gum = max (length (q) - 6.0, -p.z);
	if (gum < d.x) {
		d = vec2 (gum, 0.35);
		P = q;
	}

	// Ground
	#ifdef GROUND
	q = vec3 (p.xy, p.z + v);
	float ground = (q.y + 50.0 + 14.0 * cos (q.x * k1) * cos (q.z * k1)) * 0.7;
	if (ground < d.x) {
		d = vec2 (ground, 0.55);
		P = q;
	}
	#endif

	// Ghost
	#ifdef GHOST
	v = VOXEL_RESOLUTION * floor ((130.0 + 60.0 * cos (iTime * 3.0)) / VOXEL_RESOLUTION);
	q = vec3 (p.xy, p.z + v);
	body = length (vec3 (q.x, max (q.y - 4.0, 0.0), q.z));
	body = max (body - 28.0, 22.0 - body);
	eyes = 8.0 - length (vec3 (abs (q.x) - 12.0, q.y - 10.0, q.z - 22.0));
	float bottom = (q.y + 28.0 + 4.0 * cos (p.x * 0.4) * cos (p.z * 0.4)) * 0.7;
	float ghost = max (max (body, eyes), -bottom);
	if (ghost < d.x) {
		d = vec2 (ghost, 0.76);
		P = q;
	}
	#endif

	// Scaling
	d.x /= VOXEL_RESOLUTION;
	return d;
}

// Distance to the (voxelized?) scene
vec4 dist (inout vec3 p, in vec3 ray, in float voxelized, in float rayLengthMax) {
	vec3 P = p;
	vec2 d = vec2 (1.0 / 0.0, 0.0);
	float rayLength = 0.0;
	float rayLengthInVoxel = 0.0;
	float rayLengthCheckVoxel = 0.0;
	vec3 raySign = sign (ray);
	vec3 rayDeltaVoxel = raySign / ray;
	for (float rayStep = 0.0; rayStep < RAY_STEP_MAX; ++rayStep) {
		if (rayLength < rayLengthInVoxel) {
			d.x = distVoxel (fract (p + 0.5) - 0.5);
			if (d.x < DELTA) {
				break;
			}
		} else if (rayLength < rayLengthCheckVoxel) {
			vec3 rayDelta = (0.5 - raySign * (fract (p + 0.5) - 0.5)) * rayDeltaVoxel;
			float dNext = min (rayDelta.x, min (rayDelta.y, rayDelta.z));
			d = distScene (floor (p + 0.5), P);
			if (d.x < 0.0) {
				rayDelta = rayDeltaVoxel - rayDelta;
				d.x = max (rayLengthInVoxel - rayLength, DELTA - min (rayDelta.x, min (rayDelta.y, rayDelta.z)));
				rayLengthInVoxel = rayLength + dNext;
			} else {
				d.x = DELTA + dNext;
			}
		} else {
			d = distScene (p, P);
			if (voxelized > 0.5) {
				if (d.x < SQRT3 * 0.5) {
					rayLengthCheckVoxel = rayLength + abs (d.x) + SQRT3 * 0.5;
					d.x = max (rayLengthInVoxel - rayLength + DELTA, d.x - SQRT3 * 0.5);
				}
			} else if (d.x < DELTA) {
				break;
			}
		}
		rayLength += d.x;
		if (rayLength > rayLengthMax) {
			break;
		}
		p += d.x * ray;
	}
	return vec4 (d, rayLength, rand (P));
}

// Normal at a given point
vec3 normal (in vec3 p, in float voxelized) {
	vec2 h = vec2 (DELTA, -DELTA);
	vec3 n;
	if (voxelized > 0.5) {
		p = fract (p + 0.5) - 0.5;
		n = h.xxx * distVoxel (p + h.xxx) +
			h.xyy * distVoxel (p + h.xyy) +
			h.yxy * distVoxel (p + h.yxy) +
			h.yyx * distVoxel (p + h.yyx);
	} else {
		n = h.xxx * distScene (p + h.xxx, n).x +
			h.xyy * distScene (p + h.xyy, n).x +
			h.yxy * distScene (p + h.yxy, n).x +
			h.yyx * distScene (p + h.yyx, n).x;
	}
	return normalize (n);
}

// HSV to RGB
vec3 hsv2rgb (in vec3 hsv) {
	#ifdef HSV2RGB_SAFE
	hsv.yz = clamp (hsv.yz, 0.0, 1.0);
	#endif
	#ifdef HSV2RGB_FAST
	return hsv.z * (1.0 + 0.5 * hsv.y * (cos (2.0 * PI * (hsv.x + vec3 (0.0, 2.0 / 3.0, 1.0 / 3.0))) - 1.0));
	#else
	return hsv.z * (1.0 + hsv.y * clamp (abs (fract (hsv.x + vec3 (0.0, 2.0 / 3.0, 1.0 / 3.0)) * 6.0 - 3.0) - 2.0, -1.0, 0.0));
	#endif
}

// Main function
void mainImage (out vec4 fragColor, in vec2 fragCoord) {

	// Get the fragment
	vec2 frag = (2.0 * fragCoord.xy - iResolution.xy) / iResolution.y;

	// Define the rendering mode
	float modeTiming = iTime * 0.234;
	float modeAngle = PI * cos (iTime * 0.2);
	modeAngle = dot (frag - vec2 (cos (iTime * 2.0), 0.0), vec2 (cos (modeAngle), sin (modeAngle)));
	float modeVoxel = step (0.5, fract (modeTiming / (4.0 * PI)));
	modeTiming = cos (modeTiming);
	float mode3D = smoothstep (0.8, 0.5, modeTiming);
	float modeSwitch = smoothstep (0.995, 1.0, modeTiming) + smoothstep (0.02, 0.0, abs (modeAngle)) * modeVoxel;
	modeVoxel = 1.0 + (step (0.0, modeAngle) - 1.0) * modeVoxel;

	// Define the ray corresponding to this fragment
	vec3 ray = normalize (vec3 (frag, mix (8.0, CAMERA_FOCAL_LENGTH, mode3D)));

	// Compute the orientation of the camera
	float yawAngle = PI * (1.2 + 0.2 * cos (iTime * 0.5));
	float pitchAngle = PI * (0.1 * cos (iTime * 0.3) - 0.05);
	#ifdef MOUSE
	yawAngle += 4.0 * PI * iMouse.x / iResolution.x;
	pitchAngle += PI * 0.3 * (1.0 - iMouse.y / iResolution.y);
	#endif
	yawAngle = mix (PI * 1.5, yawAngle, mode3D);
	pitchAngle *= mode3D;

	float cosYaw = cos (yawAngle);
	float sinYaw = sin (yawAngle);
	float cosPitch = cos (pitchAngle);
	float sinPitch = sin (pitchAngle);

	mat3 cameraOrientation;
	cameraOrientation [0] = vec3 (cosYaw, 0.0, -sinYaw);
	cameraOrientation [1] = vec3 (sinYaw * sinPitch, cosPitch, cosYaw * sinPitch);
	cameraOrientation [2] = vec3 (sinYaw * cosPitch, -sinPitch, cosYaw * cosPitch);

	ray = cameraOrientation * ray;

	// Compute the origin of the ray
	float cameraDist = mix (300.0, 195.0 + 150.0 * cos (iTime * 0.8), mode3D);
	vec3 origin = (vec3 (0.0, 0.0, 40.0 * sin (iTime * 0.2)) - cameraOrientation [2] * cameraDist) / VOXEL_RESOLUTION;

	// Compute the distance to the scene
	glowCounter = 0.0;
	vec4 d = dist (origin, ray, modeVoxel, RAY_LENGTH_MAX / VOXEL_RESOLUTION);

	// Set the background color
	vec3 finalColor = hsv2rgb (vec3 (0.2 * ray.y + 0.4 * modeVoxel - 0.37, 1.0, mode3D * BACKGROUND));
	vec3 glowColor = GLOW * vec3 (1.0, 0.3, 0.0) * glowCounter / RAY_STEP_MAX;
	if (d.x < DELTA) {

		// Set the object color
		vec3 color = hsv2rgb (vec3 (d.y + 0.1 * d.w * modeVoxel, 0.5 + 0.5 * modeVoxel, 1.0));

		// Lighting
		vec3 l = normalize (mix (vec3 (1.0, 0.0, 0.0), vec3 (1.25 + cos (iTime * 0.2), 1.0, 1.0), mode3D));
		#ifdef VOXEL_LIGHTING
		if (modeVoxel > 0.5) {
			vec3 n = normal (floor (origin + 0.5), 0.0);
			float diffuse = max (0.0, dot (n, l));
			float specular = pow (max (0.0, dot (reflect (ray, n), l)), SPECULAR_POWER) * SPECULAR_INTENSITY;
			color = (AMBIENT + diffuse) * color + specular;
		}
		#endif
		vec3 n = normal (origin, modeVoxel);
		float diffuse = dot (n, l);
		float specular;
		if (diffuse < 0.0) {
			diffuse = 0.0;
			specular = 0.0;
		} else {
			specular = pow (max (0.0, dot (reflect (ray, n), l)), SPECULAR_POWER) * SPECULAR_INTENSITY;
			#ifdef SHADOW
			origin += n * DELTA * 2.0;
			vec4 shadow = dist (origin, l, modeVoxel, SHADOW_LENGTH / VOXEL_RESOLUTION);
			if (shadow.x < DELTA) {
				shadow.z = pow (min (1.0, shadow.z * VOXEL_RESOLUTION / SHADOW_LENGTH), SHADOW_POWER);
				diffuse *= shadow.z;
				specular *= shadow.z;
			}
			#endif
		}
		color = (AMBIENT + diffuse) * color + specular;

		// Fading
		float fade = pow (max (0.0, 1.0 - d.z * VOXEL_RESOLUTION / RAY_LENGTH_MAX), FADE_POWER);
		finalColor = mix (finalColor, color, fade);
	}

	// Set the fragment color
	finalColor = mix (pow (finalColor, vec3 (GAMMA)) + glowColor, vec3 (1.0), modeSwitch);
	fragColor = vec4 (finalColor, 1.0);
}

// Quick & dirty, but...

//#define EFFECT

float line (vec2 p, vec2 a, vec2 b) {
	p -= a;
	b -= a;
	a = p - b * dot (p, b) / dot (b , b);
	b *= 0.5;
	p = abs (p - b) - abs (b);
	return max (length (a), max (p.x, p.y));
}

float circle (vec2 p, vec2 o, float r) {
	return abs (length (p - o) - r);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord) {
	vec2 frag = (2.0 * fragCoord.xy - iResolution.xy) / iResolution.x;
	frag = 3.0 * frag + vec2 (2.6, 1.1);

	float d1 = max (circle (frag, vec2 (0.4, 0.5), 0.5), frag.x - 0.4);

	d1 = min (d1, line (frag, vec2 (0.8, 0.0), vec2 (0.8, 1.0)));
	d1 = min (d1, line (frag, vec2 (1.3, 0.0), vec2 (1.3, 1.0)));
	d1 = min (d1, line (frag, vec2 (0.8, 0.5), vec2 (1.3, 0.5)));

	d1 = min (d1, line (frag, vec2 (1.7, 0.0), vec2 (1.9, 1.0)));
	d1 = min (d1, line (frag, vec2 (2.3, 0.0), vec2 (2.1, 1.0)));
	d1 = min (d1, line (frag, vec2 (1.8, 0.2), vec2 (2.2, 0.2)));

	d1 = min (d1, max (circle (frag, vec2 (2.9, 0.7), 0.3), 2.8 - frag.x));
	d1 = min (d1, line (frag, vec2 (2.7, 0.0), vec2 (2.7, 1.0)));
	d1 = min (d1, line (frag, vec2 (2.9, 0.4), vec2 (3.2, 0.0)));

	d1 = min (d1, line (frag, vec2 (3.6, 0.0), vec2 (3.6, 1.0)));
	d1 = min (d1, line (frag, vec2 (3.6, 0.0), vec2 (4.0, 0.0)));

	d1 = min (d1, line (frag, vec2 (4.4, 0.0), vec2 (4.4, 1.0)));

	d1 = min (d1, line (frag, vec2 (4.8, 0.0), vec2 (4.8, 1.0)));
	d1 = min (d1, line (frag, vec2 (4.8, 0.0), vec2 (5.3, 0.0)));
	d1 = min (d1, line (frag, vec2 (4.8, 0.5), vec2 (5.3, 0.5)));
	d1 = min (d1, line (frag, vec2 (4.8, 1.0), vec2 (5.3, 1.0)));

	d1 -= 0.16;

	float d2 = max (circle (frag, vec2 (0.2, 1.7), 0.3), frag.y - 1.6);
	d2 = min (d2, line (frag, vec2 (0.5, 1.8), vec2 (0.5, 2.2)));

	d2 = min (d2, line (frag, vec2 (0.9, 1.4), vec2 (0.9, 2.2)));
