	Material material;
	if (materialIndex == 0) { // Inner ball
		material = Material (vec3 (1.0, 0.3, 0.0), 0.4, vec3 (1.50, 1.55, 1.60));
	} else if (materialIndex == 1) { // Outter ball
		material = Material (vec3 (1.0, 0.0, 0.0), 0.7, vec3 (1.20, 1.25, 1.30));
	} else { // materialIndex == 2 // Star
		material = Material (vec3 (1.0, 1.0, 0.5), -0.5, vec3 (1.31, 1.31, 1.31));
	}
	return material;
}

// Distance to the inner ball
float getDistanceInnerBall (in vec3 p, in float d) {
	return d - 0.7;
}

// Distance to the outter ball
float getDistanceOutterBall (in vec3 p, in float d) {
	return d - 0.9;
}

// Distance to the star
float getDistanceStar (in vec3 p, in float d) {
	return max (length (p.xy) - 0.25 - 0.05 * cos (atan (p.y, p.x) * 5.0), d - 0.72);
}

// Distance to a given material
float getDistanceMaterial (in vec3 p, in int materialIndex) {
	float d = length (p);
	float materialDist;
	if (materialIndex == 0) {
		materialDist = getDistanceInnerBall (p, d);
	} else if (materialIndex == 1) {
		materialDist = getDistanceOutterBall (p, d);
	} else { // materialIndex == 2
		materialDist = getDistanceStar (p, d);
	}
	return materialDist;
}

// Distance to the scene
#define MATERIAL_PROCESS(MATERIAL) if (materialDist < 0.0) materialTo = MATERIAL; sceneDist = min (sceneDist, materialFrom != MATERIAL ? materialDist : -materialDist);
float getDistanceScene (in vec3 p, in int materialFrom, out int materialTo) {
	float d = length (p);

	// Air
	materialTo = -1;
	float sceneDist = RAY_LENGTH_MAX;

	// Outter ball
	float materialDist = getDistanceOutterBall (p, d);
	MATERIAL_PROCESS (1)

	// Star
	materialDist = getDistanceStar (p, d);
	MATERIAL_PROCESS (2)

	// Inner ball
	materialDist = getDistanceInnerBall (p, d);
	MATERIAL_PROCESS (0)

	// Return the distance
	return sceneDist;
}

// Normal at a given point
vec3 getNormal (in vec3 p, in int materialIndex) {
	const vec2 h = vec2 (DELTA, -DELTA);
	return normalize (
		h.xxx * getDistanceMaterial (p + h.xxx, materialIndex) +
		h.xyy * getDistanceMaterial (p + h.xyy, materialIndex) +
		h.yxy * getDistanceMaterial (p + h.yxy, materialIndex) +
		h.yyx * getDistanceMaterial (p + h.yyx, materialIndex)
	);
}

// Cast a ray for a given color channel (and its corresponding refraction index)
vec3 lightDirection = normalize (LIGHT);
COLOR raycast (in vec3 origin, in vec3 direction, in vec4 normal, in int materialTo, in COLOR color, in vec3 channel) {

	// Check the behavior of the material
	Material material = getMaterial (materialTo);
	float alpha = abs (material.behavior);
	color *= 1.0 - alpha;

	// The ray continues...
	int materialFrom = -1;
	float refractIndexFrom = 1.0;
	for (int rayBounce = 1; rayBounce < RAY_BOUNCE_MAX; ++rayBounce) {

		// Interface with the material
		float refractIndexTo;
		vec3 refraction;
		if (materialTo == -1) {
			refractIndexTo = 1.0;
			refraction = refract (direction, normal.xyz, refractIndexFrom);
		} else {
			refractIndexTo = dot (material.refractIndex, channel);
			refraction = material.behavior < 0.0 ? vec3 (0.0) : refract (direction, normal.xyz, refractIndexFrom / refractIndexTo);
		}
		if (dot (refraction, refraction) < DELTA) {
			direction = reflect (direction, normal.xyz);
			origin += direction * DELTA * 2.0;
		} else {
			direction = refraction;
			materialFrom = materialTo;
			refractIndexFrom = refractIndexTo;
		}

		// Ray marching
		for (int rayStep = 0; rayStep < RAY_STEP_MAX; ++rayStep) {
			float dist = max (getDistanceScene (origin, materialFrom, materialTo), DELTA);
			normal.w += dist;
			if (materialFrom != materialTo || normal.w > RAY_LENGTH_MAX) {
				break;
			}
			origin += direction * dist;
		}

		// Check whether we hit something
		if (materialFrom == materialTo) {
			break;
		}

		// Get the normal
		if (materialTo == -1) {
			normal.xyz = -getNormal (origin, materialFrom);
		} else {
			normal.xyz = getNormal (origin, materialTo);

			// Basic lighting
			material = getMaterial (materialTo);
			float relfectionDiffuse = max (0.0, dot (normal.xyz, lightDirection));
			float relfectionSpecular = pow (max (0.0, dot (reflect (direction, normal.xyz), lightDirection)), SPECULAR_POWER) * SPECULAR_INTENSITY;
			COLOR localColor = (AMBIENT + relfectionDiffuse) * CHANNEL (material.color) + relfectionSpecular;
			float localAlpha = abs (material.behavior);
			color += localColor * (1.0 - localAlpha) * alpha;
			alpha *= localAlpha;
		}
	}

	// Get the background color
	COLOR backColor = CHANNEL (texture (iChannel0, direction).rgb);

	// Return the intensity of this color channel
	return color + backColor * alpha;
}

// Main function
void mainImage (out vec4 fragColor, in vec2 fragCoord) {

	// Define the ray corresponding to this fragment
	vec2 frag = (2.0 * fragCoord.xy - iResolution.xy) / iResolution.y;
	vec3 direction = normalize (vec3 (frag, 4.0));

	// Set the camera
	vec3 origin = 4.0 * vec3 (cos (iTime * 0.1), sin (iTime * 0.2), sin (iTime * 0.1));
	vec3 forward = -origin;
	vec3 up = vec3 (sin (iTime * 0.3), 2.0, 0.0);
	mat3 rotation;
	rotation [2] = normalize (forward);
	rotation [0] = normalize (cross (up, forward));
	rotation [1] = cross (rotation [2], rotation [0]);
	direction = rotation * direction;

	// Cast the initial ray
	vec4 normal = vec4 (0.0);
	int materialTo = -1;
	for (int rayStep = 0; rayStep < RAY_STEP_MAX; ++rayStep) {
		float dist = max (getDistanceScene (origin, -1, materialTo), DELTA);
		normal.w += dist;
		if (materialTo != -1 || normal.w > RAY_LENGTH_MAX) {
			break;
		}
		origin += direction * dist;
	}

	// Check whether we hit something
	if (materialTo == -1) {
		fragColor.rgb = texture (iChannel0, direction).rgb;
	} else {

		// Get the normal
		normal.xyz = getNormal (origin, materialTo);

		// Basic lighting
		float relfectionDiffuse = max (0.0, dot (normal.xyz, lightDirection));
		float relfectionSpecular = pow (max (0.0, dot (reflect (direction, normal.xyz), lightDirection)), SPECULAR_POWER) * SPECULAR_INTENSITY;
		fragColor.rgb = (AMBIENT + relfectionDiffuse) * getMaterial (materialTo).color + relfectionSpecular;

		// The ray continues...
		#ifdef PROPAGATION
			#ifdef DISPERSION
				fragColor.r = raycast (origin, direction, normal, materialTo, fragColor.r, vec3 (1.0, 0.0, 0.0));
				fragColor.g = raycast (origin, direction, normal, materialTo, fragColor.g, vec3 (0.0, 1.0, 0.0));
				fragColor.b = raycast (origin, direction, normal, materialTo, fragColor.b, vec3 (0.0, 0.0, 1.0));
			#else
				fragColor.rgb = raycast (origin, direction, normal, materialTo, fragColor.rgb, vec3 (1.0 / 3.0));
			#endif
		#endif
	}

	// Set the alpha channel
	fragColor.a = 1.0;
}

// From https://www.shadertoy.com/view/XdBGDd
// From https://www.shadertoy.com/view/MljXDw (from Duke)

// Rendering parameters
#define CAMERA_FOCAL_LENGTH	3.0
#define RAY_STEP_MAX		100.0
#define RAY_LENGTH_MAX		150.0
#define NOISE_FACTOR		2.0
#define DIST_CORRECTION		0.6
#define DIST_MIN			0.6
#define DENSITY_FACTOR_STEP	0.01
#define DENSITY_FACTOR_DIST	0.3

// Math constants
#define PI	3.14159265359

// Rotation on the Z axis
vec3 vRotateZ (in vec3 p, in float angle) {
	float c = cos (angle);
	float s = sin (angle);
	return vec3 (c * p.x + s * p.y, c * p.y - s * p.x, p.z);
}

// Noise (from iq)
float noise (in vec3 p) {
	vec3 f = fract (p);
	p = floor (p);
	f = f * f * (3.0 - 2.0 * f);
	f.xy += p.xy + p.z * vec2 (37.0, 17.0);
	f.xy = texture (iChannel0, (f.xy + 0.5) / 256.0, -256.0).yx;
	return mix (f.x, f.y, f.z);
}

// FBM
float fbm (in vec3 p) {
	return noise (p) + noise (p * 2.0) / 2.0 + noise (p * 4.0) / 4.0;
}

// HSV to RGB
vec3 hsv2rgb (in vec3 hsv) {
	hsv.yz = clamp (hsv.yz, 0.0, 1.0);
	return hsv.z * (1.0 + hsv.y * clamp (abs (fract (hsv.x + vec3 (0.0, 2.0 / 3.0, 1.0 / 3.0)) * 6.0 - 3.0) - 2.0, -1.0, 0.0));
}

// Distance to the scene
float distScene (in vec3 p, out float hue) {

	// Global deformation
	p.xy += vec2 (3.0 * sin (p.z * 0.1 + 2.0 * iTime), 2.0 * sin (p.z * 0.2 + iTime));

	// Cylinder
	float cylinder = 6.0 - length (p.xy) + sin (atan (p.y, p.x) * 6.0) * sin (p.z);
	float d = max (cylinder, -cylinder - 6.0);
	hue = 0.3 + 0.3 * noise (p * 0.2);

	// Rotating spheres
	vec3 q = vRotateZ (p, sin (iTime * 4.0));
	q.xy = mod (q.xy, 6.5) - 0.5 * 6.5;
	q.z = mod (q.z + 0.5 * 32.0, 32.0) - 0.5 * 32.0;
	float sphereTorus = max (length (q) - 3.0, -cylinder);

	// Torus
	q.xy = vec2 (length (p.xy) - 6.0, q.z);
	sphereTorus = min (sphereTorus, length (q.xy) - 3.0);
	if (sphereTorus < d) {
		d = sphereTorus;
		hue = 0.3;
	}

	// Return the distance
	return d;
}

// Main function
void mainImage (out vec4 fragColor, in vec2 fragCoord) {

	// Define the position and orientation of the camera
	vec3 rayOrigin = vec3 (6.0 * cos (iTime * 1.5) + 0.1 * cos (iTime * 10.0), 0.1 * cos (iTime * 20.0), iTime * 20.0 + 5.0 * cos (iTime * 2.0));
	vec3 cameraForward = vec3 (-rayOrigin.xy, 15.0 * cos (iTime * 0.6));
	vec3 cameraUp = vRotateZ (vec3 (0.0, 1.0, 0.0), PI * sin (iTime) * sin (iTime * 0.2));
	mat3 cameraOrientation;
	cameraOrientation [2] = normalize (cameraForward);
	cameraOrientation [0] = normalize (cross (cameraUp, cameraForward));
	cameraOrientation [1] = cross (cameraOrientation [2], cameraOrientation [0]);
	vec3 rayDirection = cameraOrientation * normalize (vec3 ((2.0 * fragCoord.xy - iResolution.xy) / iResolution.y, CAMERA_FOCAL_LENGTH));

	// Set the background color
	vec3 colorBackground = vec3 (0.0);

	// Ray marching
	float densityTotal = 0.0;
	vec3 colorTotal = vec3 (0.0);
	float rayLength = 0.0;
	for (float rayStep = 0.0; rayStep < RAY_STEP_MAX; ++rayStep) {

		// Compute the maximum density
		float densityMax = 1.0 - rayLength / RAY_LENGTH_MAX - rayStep * DENSITY_FACTOR_STEP;
		if (densityTotal > densityMax) {
			break;
		}

		// Get the scene information
		vec3 p = rayOrigin + rayDirection * rayLength;
		float hue;
		float dist = (distScene (p, hue) + NOISE_FACTOR * (fbm (p) - 0.5)) * DIST_CORRECTION;
		if (dist < 0.0) {

			// Compute the local density
			float densityLocal = (densityTotal - densityMax) * dist * DENSITY_FACTOR_DIST;
			densityTotal += densityLocal;

			// Update the color
			vec3 colorLocal = hsv2rgb (vec3 (hue, 0.5, 0.8));
			colorTotal += colorLocal * densityLocal;
		}

		// Go ahead
		rayLength += max (dist, DIST_MIN);
	}
	colorTotal += colorBackground * (1.0 - densityTotal);

	// Set the fragment color
	fragColor = vec4 (colorTotal, 1.0);
}

// From https://www.shadertoy.com/view/MlfGR4
// From https://www.shadertoy.com/view/MljXDw (from Duke)

// Rendering parameters
#define GROUND
#define GHOST

#define CAMERA_FOCAL_LENGTH	3.0
#define RAY_STEP_MAX		100
#define RAY_LENGTH_MAX		500.0
#define DIST_CORRECTION		0.8
#define DIST_MIN			1.5

// Math constants
#define PI	3.14159265359

// Noise (from iq)
float noise (in vec3 p) {
	vec3 f = fract (p);
	p = floor (p);
	f = f * f * (3.0 - 2.0 * f);
	f.xy += p.xy + p.z * vec2 (37.0, 17.0);
	f.xy = texture (iChannel0, (f.xy + 0.5) / 256.0, -256.0).yx;
	return mix (f.x, f.y, f.z);
}

// FBM
float fbm (in vec3 p) {
	return noise (p) + noise (p * 2.0) / 2.0 + noise (p * 4.0) / 4.0;
}

// Distance to the scene and color of the closest point
vec2 distScene (in vec3 p, in float noiseFactor) {

	// Velocity, period of the waves, spacing of the gums
	float v = iTime * 100.0;
	const float k1 = 0.05;
	const float k2 = 60.0;

	// Pac-Man
	float body = length (p);
	body = max (body - 32.0, 27.0 - body);
	float eyes = 10.0 - length (vec3 (abs (p.x) - 15.0, p.y - 20.0, p.z - 20.0));
	float mouthAngle = PI * (0.07 + 0.07 * cos (2.0 * v * PI / k2));
	float mouthTop = dot (p, vec3 (0.0, -cos (mouthAngle), sin (mouthAngle))) - 2.0;
	mouthAngle *= 2.5;
	float mouthBottom = dot (p, vec3 (0.0, cos (mouthAngle), sin (mouthAngle)));
	float pacMan = max (max (body, eyes), min (mouthTop, mouthBottom));
	vec2 d = vec2 (pacMan, 0.13);
	vec3 P = p;

	// Gums
	vec3 q = vec3 (p.xy, mod (p.z + v, k2) - k2 * 0.5);
	float gum = max (length (q) - 6.0 * min (p.z / 20.0, 1.0), -p.z);
	if (gum < d.x) {
		d = vec2 (gum, 0.35);
		P = q;
	}

	// Ground
	#ifdef GROUND
	q = vec3 (p.xy, p.z + v);
	float ground = q.y + 50.0 + 14.0 * cos (q.x * k1) * cos (q.z * k1);
	ground = max (ground, -6.0 - ground) * 0.7;
	if (ground < d.x) {
		d = vec2 (ground, 0.55);
		P = q;
	}
	#endif

	// Ghost
	#ifdef GHOST
	v = 130.0 + 60.0 * cos (iTime * 3.0);
	q = vec3 (p.xy, p.z + v);
	body = length (vec3 (q.x, max (q.y - 4.0, 0.0), q.z));
	body = max (body - 28.0, 22.0 - body);
	eyes = 10.0 - length (vec3 (abs (q.x) - 14.0, q.y - 10.0, q.z - 22.0));
	float bottom = (q.y + 28.0 + 4.0 * cos (p.x * 0.4) * cos (p.z * 0.4)) * 0.7;
	float ghost = max (max (body, eyes), -bottom);
	if (ghost < d.x) {
		d = vec2 (ghost, 0.76);
		P = q;
	}
	#endif

	// FBM
	d.x += noiseFactor * (fbm (P * 0.5) - 1.4);
	d.y += 0.3 * noise (P * 0.5) - 0.15;
	return d;
}

// HSV to RGB
vec3 hsv2rgb (in vec3 hsv) {
	#ifdef HSV2RGB_SAFE
	hsv.yz = clamp (hsv.yz, 0.0, 1.0);
	#endif
	return hsv.z * (1.0 + hsv.y * clamp (abs (fract (hsv.x + vec3 (0.0, 2.0 / 3.0, 1.0 / 3.0)) * 6.0 - 3.0) - 2.0, -1.0, 0.0));
}

// Main function
void mainImage (out vec4 fragColor, in vec2 fragCoord) {

	// Define the position of the camera
	float cameraDist = 125.0 + 60.0 * cos (iTime * 0.8);
	vec3 rayOrigin;
	if (iMouse.z < 0.5) {
		rayOrigin = vec3 (cameraDist * cos (iTime), 5.0 + 70.0 * sin (iTime * 0.5), cameraDist * sin (iTime));
	} else {
		float cameraAngle = 4.0 * PI * iMouse.x / iResolution.x;
		rayOrigin = vec3 (cameraDist * sin (cameraAngle), 5.0 + 70.0 * (1.0 - 2.0 * iMouse.y / iResolution.y), cameraDist * cos (cameraAngle));
	}

	// Define the orientation of the camera
	vec3 cameraLookAt = vec3 (10.0 * cos (iTime * 0.8), 10.0 + 10.0 * sin (iTime * 0.2), 20.0 * sin (iTime * 0.5) - 10.0);
	vec3 cameraForward = cameraLookAt - rayOrigin;
	vec3 cameraUp = vec3 (0.2 * cos (iTime * 0.1), 1.0, 0.2 * sin (iTime * 0.1));
	mat3 cameraOrientation;
	cameraOrientation [2] = normalize (cameraForward);
	cameraOrientation [0] = normalize (cross (cameraUp, cameraForward));
	cameraOrientation [1] = cross (cameraOrientation [2], cameraOrientation [0]);
	vec3 rayDirection = cameraOrientation * normalize (vec3 ((2.0 * fragCoord.xy - iResolution.xy) / iResolution.y, CAMERA_FOCAL_LENGTH));

	// Define the sun direction
	float sunYawAngle = PI * 0.25;
	float sunPitchAngle = iTime * 0.1;
	vec3 sunDirection = vec3 (sin (sunYawAngle) * sin (sunPitchAngle), cos (sunPitchAngle), cos (sunYawAngle) * sin (sunPitchAngle));

	// Set the sky color
	vec3 skyColor = mix (vec3 (0.8, 0.4, 0.4), vec3 (0.4, 0.4, 0.6), smoothstep (0.0, 0.5, sunDirection.y));
	skyColor += vec3 (0.8, 0.6, 0.4) * pow (max (0.0, dot (rayDirection, sunDirection)), 12.0);
	skyColor = mix (vec3 (0.15, 0.15, 0.24), skyColor, min (1.0, 1.0 + sunDirection.y));

	// Set the noise and density factors
	float candyControl = smoothstep (0.4, 0.6, sin (iTime * 0.7));
	float noiseFactor = mix (4.0, 8.0, candyControl);
	float densityFactor = mix (0.1, 0.5, candyControl);

	// Ray marching
	float densityTotal = 0.0;
	vec3 colorTotal = vec3 (0.0);
	float rayLength = 0.0;
	for (int rayStep = 0; rayStep < RAY_STEP_MAX; ++rayStep) {

		// Compute the maximum density
		float densityMax = 1.0 - rayLength / RAY_LENGTH_MAX;
		if (densityTotal > densityMax) {
			break;
		}

		// Get the scene information
		vec3 p = rayOrigin + rayDirection * rayLength;
		vec2 data = distScene (p, noiseFactor);
		float dist = data.x * DIST_CORRECTION;
		if (dist < 0.0) {

			// Compute the local density
			float densityLocal = (densityTotal - densityMax) * dist * densityFactor;
			densityTotal += densityLocal;

			// Update the color
			vec3 colorLocal = hsv2rgb (vec3 (data.y, 0.2, 1.0));
			colorTotal += colorLocal * densityLocal;
		}

		// Go ahead
		rayLength += max (dist, DIST_MIN);
	}
	colorTotal += skyColor * (1.0 - densityTotal);

	// Set the fragment color
	fragColor = vec4 (colorTotal, 1.0);
}

// Rendering parameters
#define MAZE_DENSITY	0.5
#define MAZE_ZOOM		5.0
#define TEXTURE_ZOOM	0.25
#define RAY_LENGTH		5.0
#define FADE_POWER		1.5
#define AMBIENT			0.0

// Animation parameters
#define LIGHT_SPEED_X		1.7
#define LIGHT_MOVE_X		3.0
#define LIGHT_MOVE_Y		4.3
#define LIGHT_FREQUENCY_X	0.2
#define LIGHT_FREQUENCY_Y1	0.3
#define LIGHT_FREQUENCY_Y2	0.5
#define FADE_VARIATION		0.2
#define FADE_FREQUENCY		3.0

// Math constants
#define SQRT2	1.41421356237
#define DELTA	0.001

// Animation formulas
#define LIGHT_Y(t) LIGHT_MOVE_Y * cos (LIGHT_FREQUENCY_Y1 * (t)) * cos (LIGHT_FREQUENCY_Y2 * (t))
#define LIGHT_DY(t) LIGHT_MOVE_Y * (LIGHT_FREQUENCY_Y1 * sin (LIGHT_FREQUENCY_Y1 * (t)) * cos (LIGHT_FREQUENCY_Y2 * (t)) + LIGHT_FREQUENCY_Y2 * cos (LIGHT_FREQUENCY_Y1 * (t)) * sin (LIGHT_FREQUENCY_Y2 *(t)))

// PRNG
float rand (in vec2 seed) {
	return fract (11.0 * sin (3.0 * seed.x + 5.0 * seed.y));
}

// Check whether there is a wall on a given tile edge
float wallCheck (in vec2 p) {
	vec2 tile = floor (p + 0.5);
	float wall = step (1.0 - MAZE_DENSITY, rand (tile));
	wall *= step (max (1.0, abs (LIGHT_DY (tile.x))), abs (LIGHT_Y (tile.x) - tile.y));
	return wall;
}

// Return the distance to the nearest wall
float wallDistance (in vec2 rayOrigin, in vec2 rayDirection) {

	// Launch the ray
	vec2 raySign = sign (rayDirection);
	vec2 rayInv = 1.0 / rayDirection;
	float rayLength = 0.0;
	vec2 rayLengthNext = (0.5 * raySign - fract (rayOrigin + 0.5) + 0.5) * rayInv;
	for (float rayStep = 0.0; rayStep < RAY_LENGTH * SQRT2; ++rayStep) {

		// Reach the edge of the tile
		rayLength = min (rayLengthNext.x, rayLengthNext.y);
		vec2 hitNormal = step (rayLengthNext.xy, rayLengthNext.yx) * raySign;

		// Check whether we hit a wall
		if (wallCheck (rayOrigin + rayLength * rayDirection + hitNormal * 0.5) > 0.5) {
			break;
		}

		// Next tile
		rayLengthNext += hitNormal * rayInv;
	}

	// Return the distance to the hit point
	return rayLength;
}

// Main function
void mainImage (out vec4 fragColor, in vec2 fragCoord) {

	// Define the coordinates
	float cameraX = LIGHT_SPEED_X * iTime;
	vec2 rayOrigin = MAZE_ZOOM * (2.0 * fragCoord - iResolution.xy) / iResolution.y;
	rayOrigin.x += cameraX;

	// Lighting
	vec2 lightPosition;
	if (iMouse.z < 0.5) {
		lightPosition.x = cameraX + LIGHT_MOVE_X * cos (LIGHT_FREQUENCY_X * iTime);
		lightPosition.y = LIGHT_Y (lightPosition.x);
	} else {
		lightPosition = MAZE_ZOOM * (2.0 * iMouse.xy - iResolution.xy) / iResolution.y;
		lightPosition.x += cameraX;
	}
	vec2 lightDirection = lightPosition - rayOrigin;
	float lightDistance = length (lightDirection);
	float lightIntensity = pow (max (0.0, 1.0 - lightDistance / RAY_LENGTH), FADE_POWER + FADE_VARIATION * cos (FADE_FREQUENCY * iTime));
	if (wallCheck (rayOrigin) < 0.5) {

		// Corridor
		if (lightDistance < RAY_LENGTH) {
			lightIntensity *= step (lightDistance + DELTA, wallDistance (rayOrigin, lightDirection / lightDistance));
		}
		fragColor = texture (iChannel0, rayOrigin * TEXTURE_ZOOM);
	} else {

		// Wall
		fragColor = texture (iChannel1, rayOrigin * TEXTURE_ZOOM);
	}
	fragColor *= mix (lightIntensity, 1.0, AMBIENT);
	fragColor += smoothstep (0.2, 0.0, lightDistance) * step (0.2, rand (vec2 (iTime)));
}

void mainImage (out vec4 fragColor, in vec2 fragCoord) {
	vec2 uv = vec2 (1.3, 0.8) * (2.0 * fragCoord.xy - iResolution.xy) / iResolution.y;
	float d1 = abs (length (uv - vec2 (0.0, -1.0)) - 0.55);
	float d2 = abs (length (uv - vec2 (-1.95, 0.8)) - 1.95);
	float d3 = max (abs (length (uv - vec2 (1.95, 0.8)) - 1.95), 0.075 - uv.x);
	float d = max (min (d1, min (d2, d3)), abs (uv.y) - 0.65);
	vec3 color = mix ((d1 < d2 && d1 < d3) ? vec3 (0.9) : d2 < d3 ? vec3 (0.4, 0.4, 1.0) : vec3 (1.0, 0.4, 0.4), vec3 (0.2 * (uv.y + 0.5) * (0.7 + 0.3 * cos (iTime))), smoothstep (0.02 - 0.01 * uv.y, 0.04, d));
	fragColor = vec4 (color * smoothstep (1.2, 0.0, abs (uv.x)), 1.0);
}

// Rendering parameters
#define FOV					80.0
#define RAY_STEP_MAX		20.0
#define RAY_LENGTH_MAX		10.0
#define EDGE_LENGTH			0.1
#define EDGE_FULL
#define TEXTURE
#define SHADOW
#define BUMP_RESOLUTION		100.0
#define BUMP_INTENSITY		0.1
#define AMBIENT_NORMAL		0.2
#define AMBIENT_HIGHLIGHT	2.5
#define SPECULAR_POWER		2.0
#define SPECULAR_INTENSITY	0.3
#define FADE_POWER			1.5
#define GAMMA				0.8

// Math constants
#define DELTA	0.002
#define PI		3.14159265359

// PRNG (unpredictable)
float randUnpredictable (in vec3 seed) {
	seed = fract (seed * vec3 (5.6789, 5.4321, 6.7890));
	seed += dot (seed.yzx, seed.zxy + vec3 (21.0987, 12.3456, 15.1273));
	return fract (seed.x * seed.y * seed.z * 5.1337);
}

// PRNG (predictable)
float randPredictable (in vec3 seed) {
	return fract (11.0 * sin (3.0 * seed.x + 5.0 * seed.y + 7.0 * seed.z));
}

// PRNG (2D only)
float rand (in vec2 seed) {
	seed = fract (seed * vec2 (5.3983, 5.4427));
	seed += dot (seed.yx, seed.xy + vec2 (21.5351, 14.3137));
	return fract (seed.x * seed.y * 95.4337);
}

// HSV to RGB
vec3 hsv2rgb (in vec3 hsv) {
	hsv.yz = clamp (hsv.yz, 0.0, 1.0);
	return hsv.z * (1.0 + hsv.y * clamp (abs (fract (hsv.x + vec3 (0.0, 2.0 / 3.0, 1.0 / 3.0)) * 6.0 - 3.0) - 2.0, -1.0, 0.0));
}

// Check whether there is a block at a given voxel edge
float blockCheck (in vec3 p, in vec3 n) {
	vec3 blockPosition = floor (p + 0.5 + n * 0.5);
	vec3 blockEven = mod (blockPosition, 2.0);
	float blockSum = blockEven.x + blockEven.y + blockEven.z;
	return max (step (blockSum, 1.5), step (blockSum, 2.5) * step (0.5, randPredictable (blockPosition))) *
		step (4.5, mod (blockPosition.x, 32.0)) *
		step (2.5, mod (blockPosition.y, 16.0)) *
		step (4.5, mod (blockPosition.z, 32.0));
}

// Cast a ray
vec3 hit (in vec3 rayOrigin, in vec3 rayDirection, in float rayLengthMax, out float rayLength, out vec3 hitNormal) {

	// Launch the ray
	vec3 hitPosition = rayOrigin;
	vec3 raySign = sign (rayDirection);
	vec3 rayInv = 1.0 / rayDirection;
	vec3 rayLengthNext = (0.5 * raySign - fract (rayOrigin + 0.5) + 0.5) * rayInv;
	for (float rayStep = 0.0; rayStep < RAY_STEP_MAX; ++rayStep) {

		// Reach the edge of the voxel
		rayLength = min (rayLengthNext.x, min (rayLengthNext.y, rayLengthNext.z));
		hitNormal = step (rayLengthNext.xyz, rayLengthNext.yzx) * step (rayLengthNext.xyz, rayLengthNext.zxy) * raySign;
		hitPosition = rayOrigin + rayLength * rayDirection;

		// Check whether we hit a block
		if (blockCheck (hitPosition, hitNormal) > 0.5 || rayLength > rayLengthMax) {
			break;
		}

		// Next voxel
		rayLengthNext += hitNormal * rayInv;
	}

	// Return the hit point
	return hitPosition;
}

// Main function
void mainImage (out vec4 fragColor, in vec2 fragCoord) {

	// Read the data
	vec3 headPosition;
	mat3 headOrientation;
	vec3 lightPosition;

	if (iFrame < 5) {
		headPosition = vec3 (13.05, 1.5, 13.0);
		lightPosition = vec3 (13.0, 1.0, 13.0);
		headOrientation [0] = vec3 (0.0, 0.0, -1.0);
		headOrientation [1] = vec3 (1.0, 0.0, 0.0);
	} else {
		vec4 data = texture (iChannel0, vec2 (0.5, 0.5) / iResolution.xy);
		headPosition = data.xyz;
		headOrientation [1].x = data.w;
		data = texture (iChannel0, vec2 (2.5, 0.5) / iResolution.xy);
		lightPosition = data.xyz;
		headOrientation [1].y = data.w;
		data = texture (iChannel0, vec2 (4.5, 0.5) / iResolution.xy);
		headOrientation [0] = data.xyz;
		headOrientation [1].z = data.w;
	}
	headOrientation [2] = cross (headOrientation [0], headOrientation [1]);

	// Animate the ambient lighting
	float ambientIntensity = max (step (1.0, mod (iTime, 10.0)), step (0.25, randUnpredictable (vec3 (iTime))));

	// Define the ray corresponding to this fragment
	vec3 rayOrigin = headPosition;
	vec3 rayDirection = headOrientation * normalize (vec3 (2.0 * fragCoord - iResolution.xy, 0.5 * iResolution.x / tan (FOV * PI / 360.0)));

	// Cast a ray
	float hitDistance;
	vec3 hitNormal;
	vec3 hitPosition = hit (rayOrigin, rayDirection, RAY_LENGTH_MAX, hitDistance, hitNormal);
	vec3 hitUV = hitPosition * abs (hitNormal.yzx + hitNormal.zxy);

	// Basic edge detection
	vec3 edgeDistance = fract (hitUV + 0.5) - 0.5;
	vec3 edgeDirection = sign (edgeDistance);
	edgeDistance = abs (edgeDistance);

	#ifdef EDGE_FULL
	vec3 hitNormalAbs = abs (hitNormal);
	vec2 edgeSmooth = vec2 (dot (edgeDistance, hitNormalAbs.yzx), dot (edgeDistance, hitNormalAbs.zxy));
	float highlightIntensity = (1.0 - blockCheck (hitPosition + edgeDirection * hitNormalAbs.yzx, hitNormal)) * smoothstep (0.5 - EDGE_LENGTH, 0.5 - EDGE_LENGTH * 0.5, edgeSmooth.x);
	highlightIntensity = max (highlightIntensity, (1.0 - blockCheck (hitPosition + edgeDirection * hitNormalAbs.zxy, hitNormal)) * smoothstep (0.5 - EDGE_LENGTH, 0.5 - EDGE_LENGTH * 0.5, edgeSmooth.y));
	highlightIntensity = max (highlightIntensity, (1.0 - blockCheck (hitPosition + edgeDirection, hitNormal)) * smoothstep (0.5 - EDGE_LENGTH, 0.5 - EDGE_LENGTH * 0.5, min (edgeSmooth.x, edgeSmooth.y)));
	#else
	float highlightIntensity = 1.0 - blockCheck (hitPosition + step (edgeDistance.yzx, edgeDistance.xyz) * step (edgeDistance.zxy, edgeDistance.xyz) * edgeDirection, hitNormal);
	highlightIntensity *= smoothstep (0.5 - EDGE_LENGTH, 0.5 - EDGE_LENGTH * 0.5, max (edgeDistance.x, max (edgeDistance.y, edgeDistance.z)));
	#endif

	// Texture
	#ifdef TEXTURE
	vec2 textureUV = fract (vec2 (dot (hitUV, hitNormal.yzx), dot (hitUV, hitNormal.zxy)) + 0.5);
	textureUV.x = (textureUV.x + mod (floor (iTime * 20.0), 6.0)) * 40.0 / 256.0;
	float textureIntensity = 1.0 - texture (iChannel1, textureUV).r;
	float texturePhase = 2.0 * PI * randUnpredictable (floor (hitPosition + 0.5 + hitNormal * 1.5));
	textureIntensity *= smoothstep (0.8, 1.0, cos (iTime * 0.2 + texturePhase));
	highlightIntensity = max (highlightIntensity, textureIntensity);
	#endif

	// Set the object color
	vec3 color = cos ((hitPosition + hitNormal * 0.5) * 0.05);
	color = hsv2rgb (vec3 (color.x + color.y + color.z + highlightIntensity * 0.05, 1.0, 1.0));

	// Lighting
	vec3 lightDirection = hitPosition - lightPosition;
	float lightDistance = length (lightDirection);
	lightDirection /= lightDistance;

	float lightIntensity = min (1.0, 1.0 / lightDistance);
	#ifdef SHADOW
	float lightHitDistance;
	vec3 lightHitNormal;
	hit (hitPosition - hitNormal * DELTA, -lightDirection, lightDistance, lightHitDistance, lightHitNormal);
	lightIntensity *= step (lightDistance, lightHitDistance);
	#endif

	// Bump mapping
	vec2 bumpUV = BUMP_RESOLUTION * vec2 (dot (hitUV, hitNormal.yzx), dot (hitUV, hitNormal.zxy));
	vec4 bumpID = floor (vec4 (bumpUV, bumpUV + 1.0));
	bumpUV = fract (bumpUV);
	float bump = mix (
		mix (rand (bumpID.xy), rand (bumpID.zy), bumpUV.x),
		mix (rand (bumpID.xw), rand (bumpID.zw), bumpUV.x),
		bumpUV.y);
	hitNormal = normalize (hitNormal + hitNormal.yzx * (1.0 - highlightIntensity) * BUMP_INTENSITY * (bump - 0.5));

	// Shading
	float ambient = mix (AMBIENT_NORMAL, AMBIENT_HIGHLIGHT, highlightIntensity) * ambientIntensity;
	float diffuse = max (0.0, dot (hitNormal, lightDirection));
	float specular = pow (max (0.0, dot (reflect (rayDirection, hitNormal), lightDirection)), SPECULAR_POWER) * SPECULAR_INTENSITY;
	color = (ambient + diffuse * lightIntensity) * color + specular * lightIntensity;
	color *= pow (max (0.0, 1.0 - hitDistance / RAY_LENGTH_MAX), FADE_POWER);

	// Light source
	lightDirection = lightPosition - rayOrigin;
	if (dot (rayDirection, lightDirection) > 0.0) {
		lightDistance = length (lightDirection);
		if (lightDistance < hitDistance) {
			vec3 lightNormal = cross (rayDirection, lightDirection);
			color += smoothstep (0.001, 0.0, dot (lightNormal, lightNormal));
		}
	}

	// Adjust the gamma
	color = pow (color, vec3 (GAMMA));

	// Set the fragment color
	fragColor = vec4 (color, 1.0);
}

// Physics parameters
#define GRAVITY vec3 (0.0, -3.0, 0.0) // Feel free to comment this out to explore the maze freely :)

// Math constants
#define DELTA	0.002
#define PI 		3.14159265359

// Element
struct Element {
	vec3 position;
	vec3 speed;
	vec3 force;
	float friction;
	float radius;
	float elasticity;
};

// Head
struct Head {
	Element element;
	mat3 orientation;
	float forceOrientation;
};

// Light ball
struct LightBall {
	Element element;
	float aheadDistance;
	float movementAmplitude;
	float forceSpringStiffness;
	float forceMaxSqr;
	float collisionTimer;
	float collisionTimerMax;
};

// PRNG (predictable)
float randPredictable (in vec3 seed) {
	return fract (11.0 * sin (3.0 * seed.x + 5.0 * seed.y + 7.0 * seed.z));
}

// Check whether there is a block at a given position
float blockCheck (in vec3 blockPosition) {
	vec3 blockEven = mod (blockPosition, 2.0);
	float blockSum = blockEven.x + blockEven.y + blockEven.z;
	return max (step (blockSum, 1.5), step (blockSum, 2.5) * step (0.5, randPredictable (blockPosition))) *
		step (4.5, mod (blockPosition.x, 32.0)) *
		step (2.5, mod (blockPosition.y, 16.0)) *
		step (4.5, mod (blockPosition.z, 32.0));
}

// React to collisions
void collisionReact (inout Element element, in vec3 relativePosition) {

	// Compute the position relative to the actual hit point
	relativePosition -= 0.5 * sign (relativePosition);

	// Make sure there is a hit
	float distance = length (relativePosition);
	if (distance < element.radius) {

		// Compute the normalized direction of the hit
		relativePosition /= distance;

		// Upate the position
		distance -= element.radius;
		element.position -= relativePosition * distance;

		// Update the speed
		element.speed -= (1.0 + element.elasticity) * dot (element.speed, relativePosition) * relativePosition;
	}
}

// Detect collisions (here, "element" is a sphere, and the environment is made of cubes)
void collisionDetect (inout Element element) {

	// Get the position of the current block
	vec3 blockPosition = floor (element.position + 0.5);

	// There is no collision if we are inside a block already
	if (blockCheck (blockPosition) > 0.5) {
		return;
	}

	// Compute the relative position within the block
	vec4 relativePosition = vec4 (element.position - blockPosition, 0.0);

	// Check whether we are close to a side of the current block
	vec3 check = step (0.5 - element.radius, abs (relativePosition.xyz));
	if (check.x + check.y + check.z < 0.5) {
		return;
	}

	// Prepare to check nearby blocks
	vec4 blockDelta = sign (relativePosition);

	// Handle collisions with the sides
	if (check.x > 0.5 && blockCheck (blockPosition + blockDelta.xww) > 0.5) {
		check.x = 0.0;
		collisionReact (element, relativePosition.xww);
	}
	if (check.y > 0.5 && blockCheck (blockPosition + blockDelta.wyw) > 0.5) {
		check.y = 0.0;
		collisionReact (element, relativePosition.wyw);
	}
	if (check.z > 0.5 && blockCheck (blockPosition + blockDelta.wwz) > 0.5) {
		check.z = 0.0;
		collisionReact (element, relativePosition.wwz);
	}

	// Take note of whether we have to check the collision with the corner
	float checkXYZ = check.x * check.y * check.z;

	// Handle collisions with the edges
	if (check.x * check.y > 0.5 && blockCheck (blockPosition + blockDelta.xyw) > 0.5) {
		checkXYZ = 0.0;
		collisionReact (element, relativePosition.xyw);
	}
	if (check.y * check.z > 0.5 && blockCheck (blockPosition + blockDelta.wyz) > 0.5) {
		checkXYZ = 0.0;
		collisionReact (element, relativePosition.wyz);
	}
	if (check.z * check.x > 0.5 && blockCheck (blockPosition + blockDelta.xwz) > 0.5) {
		checkXYZ = 0.0;
		collisionReact (element, relativePosition.xwz);
	}

	// Handle the collision with the corner
	if (checkXYZ > 0.5 && blockCheck (blockPosition + blockDelta.xyz) > 0.5) {
		collisionReact (element, relativePosition.xyz);
	}
}

// Handle movements
void move (inout Element element, in bool collide) {

	// Handle the friction
	element.force -= element.speed * element.friction;

	// Update the speed
	element.speed += element.force * iTimeDelta;

	// Compute the movement
	float speed = length (element.speed);
	float movementLength = speed * iTimeDelta;
	vec3 movementDirection = element.speed / speed;

	// Move towards the destination by small increments, to make sure we detect all collisions
	// Note: we could optimize this by going faster when within the inner part of a block, far from the sides
	// ...but this isn't really needed, so let's keep it simple
	if (collide && element.radius > 0.0) {
		for (int iteration = 0; iteration < 8; ++iteration) {
			if (movementLength > element.radius) {
				element.position += element.radius * movementDirection;
				movementLength -= element.radius;
				collisionDetect (element);
			}
		}
	}
	element.position += movementLength * movementDirection;
	if (collide) {
		collisionDetect (element);
	}
}

// Get the orientation of the head
mat3 headOrientationGet () {

	float yawAngle = 3.0 * PI * (iMouse.x / iResolution.x - 0.5);
	float pitchAngle = PI * (0.5 - iMouse.y / iResolution.y);

	float cosYaw = cos (yawAngle);
	float sinYaw = sin (yawAngle);
	float cosPitch = cos (pitchAngle);
	float sinPitch = sin (pitchAngle);

	mat3 headOrientation;
	headOrientation [0] = vec3 (cosYaw, 0.0, -sinYaw);
	headOrientation [1] = vec3 (sinYaw * sinPitch, cosPitch, cosYaw * sinPitch);
	headOrientation [2] = vec3 (sinYaw * cosPitch, -sinPitch, cosYaw * cosPitch);
	return headOrientation;
}

// Main function
void mainImage (out vec4 fragColor, in vec2 fragCoord) {

	// Don't waste time...
	if (fragCoord.x > 5.0 || fragCoord.y > 1.0) {
		discard;
	}

	// Define the head
	Head head;
	head.element.friction = 1.0;
	head.element.radius = 0.1;
	head.element.elasticity = 0.5;
	head.forceOrientation = 2.0;

	// Define the light ball
	LightBall lightBall;
	lightBall.element.friction = 1.0;
	lightBall.element.radius = 0.03;
	lightBall.element.elasticity = 0.5;
	lightBall.aheadDistance = 0.5;
	lightBall.movementAmplitude = 0.1;
	lightBall.forceSpringStiffness = 15.0;
	lightBall.forceMaxSqr = 900.0;
	lightBall.collisionTimerMax = 5.0;

	// Initialize the position and speed of both the head and the light ball
	if (iFrame < 5) {
		head.element.position = vec3 (13.05, 1.5, 13.0);
		head.element.speed = vec3 (0.0);
		lightBall.element.position = vec3 (13.0, 1.0, 13.0);
		lightBall.element.speed = vec3 (0.0);
		lightBall.collisionTimer = 0.0;
	} else {
		head.element.position = texture (iChannel0, vec2 (0.5, 0.5) / iResolution.xy).xyz;
		head.element.speed = texture (iChannel0, vec2 (1.5, 0.5) / iResolution.xy).xyz;
		lightBall.element.position = texture (iChannel0, vec2 (2.5, 0.5) / iResolution.xy).xyz;
		vec4 data = texture (iChannel0, vec2 (3.5, 0.5) / iResolution.xy);
		lightBall.element.speed = data.xyz;
		lightBall.collisionTimer = data.w;
	}

	// Move the head
	head.orientation = headOrientationGet ();
	head.element.force = head.orientation [2] * head.forceOrientation;
	#ifdef GRAVITY
	float gravitySqr = dot (GRAVITY, GRAVITY);
	if (gravitySqr > DELTA) {
		head.element.force += (1.0 - dot (head.element.force, GRAVITY) / gravitySqr) * GRAVITY;
	}
	#endif
	move (head.element, true);

	// Move the light ball (using a spring force)
	vec3 lightBallPositionTarget = lightBall.movementAmplitude * vec3 (sin (iTime * 2.0), sin (iTime * 3.0), sin (iTime));
	lightBallPositionTarget.z += lightBall.aheadDistance;
	lightBallPositionTarget = head.element.position + head.orientation * lightBallPositionTarget;
	lightBall.element.force = (lightBallPositionTarget - lightBall.element.position) * lightBall.forceSpringStiffness;
	if (dot (lightBall.element.force, lightBall.element.force) < lightBall.forceMaxSqr) {
		lightBall.collisionTimer = 0.0;
	} else {
		lightBall.collisionTimer += iTimeDelta;
	}
	move (lightBall.element, lightBall.collisionTimer < lightBall.collisionTimerMax);

	// Store the data
	if (fragCoord.x < 1.0) {
		fragColor = vec4 (head.element.position, head.orientation [1].x);
	} else if (fragCoord.x < 2.0) {
		fragColor = vec4 (head.element.speed, 0.0);
	} else if (fragCoord.x < 3.0) {
		fragColor = vec4 (lightBall.element.position, head.orientation [1].y);
	} else if (fragCoord.x < 4.0) {
		fragColor = vec4 (lightBall.element.speed, lightBall.collisionTimer);
	} else {
		fragColor = vec4 (head.orientation [0], head.orientation [1].z);
	}
}

info.description:
This is an attempt to simulate liquid in 2D, using particles.
- Mouse: draw colliders;
- LEFT & RIGHT: rotate the gravity;
- SPACE: invert the gravity;
- R: reset the scene.

renderpass.code:
// Display particles and colliders

#define RADIUS_PARTICLE			1.5
//#define GLOW_THICKNESS		1.0 // Choose either GLOW_THICKNESS *or* PARTICLE_COUNT_MIN
#define PARTICLE_COUNT_MIN		2.0
#define VELOCITY_COLOR_FACTOR	0.02

#define FLOOR(x) float (int (x)) // To workaround a bug with Firefox on Windows...

float rand (in vec2 seed) {
	return fract (sin (dot (seed, vec2 (12.9898, 78.233))) * 137.5453);
}

vec3 hsv2rgb (in vec3 hsv) {
	hsv.yz = clamp (hsv.yz, 0.0, 1.0);
	return hsv.z * (1.0 + hsv.y * clamp (abs (fract (hsv.x + vec3 (0.0, 2.0 / 3.0, 1.0 / 3.0)) * 6.0 - 3.0) - 2.0, -1.0, 0.0));
}

float segDist (in vec2 p, in vec2 a, in vec2 b) {
	p -= a;
	b -= a;
	return length (p - b * clamp (dot (p, b) / dot (b, b), 0.0, 1.0));
}

vec3 particleColor (in float particleVelocity) {
	return mix (vec3 (0.5, 0.5, 1.0), vec3 (1.0), particleVelocity * VELOCITY_COLOR_FACTOR);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord) {

	// Check whether there is a collider here
	vec4 data = texture (iChannel1, fragCoord / iResolution.xy);
	vec3 color;
	if (data.a > 0.5) {

		// Collider (wood)
		vec2 uv = 0.02 * fragCoord;
		uv = uv.y * 13.0 + sin (uv * 17.0) * sin (uv.yx * 7.0) * sin (iTime * 0.2 + uv);
		color = vec3 (0.8, 0.6, 0.4) * (1.0 - 0.5 * length (fract (uv) - 0.5));
	} else {

		// Background (light squares)
		vec2 uv = 0.05 * fragCoord;
		uv += 0.5 * cos (uv.yx + iTime);
		float angle = rand (floor (uv)) * 3.14159;
		vec3 hsv = vec3 (0.6 + 0.1 * cos (angle), 1.0, 0.2 + 0.1 * cos (angle * iTime));
		color = hsv2rgb (hsv) * smoothstep (1.0, 0.2, length (fract (uv) - 0.5));

		// Check whether there is a particle here
		float particleVelocity = data.b;
		if (particleVelocity >= 0.0) {
			color += particleColor (particleVelocity);
		} else {

			// Look around (spiral loop from the current position)
			vec2 offset = vec2 (0.0);
			vec2 direction = vec2 (1.0, 0.0);
#if defined (GLOW_THICKNESS) && !defined (PARTICLE_COUNT_MIN)
			const float radiusGlow = FLOOR (RADIUS_PARTICLE + GLOW_THICKNESS);
			for (float n = 1.0; n < (2.0 * radiusGlow + 1.0) * (2.0 * radiusGlow + 1.0); ++n) {
				offset += direction;
				if (offset.x == offset.y || (offset.x < 0.0 && offset.x == -offset.y) || (offset.x > 0.0 && offset.x == 1.0 - offset.y)) {
					direction = vec2 (-direction.y, direction.x);
				}
				particleVelocity = texture (iChannel1, (fragCoord + offset) / iResolution.xy).b;
				if (particleVelocity >= 0.0) {
					color += particleColor (particleVelocity) * smoothstep (RADIUS_PARTICLE + GLOW_THICKNESS, RADIUS_PARTICLE, length (offset));
					break;
				}
			}
#elif !defined (GLOW_THICKNESS) && defined (PARTICLE_COUNT_MIN)
			float count = 0.0;
			for (float n = 1.0; n < (2.0 * FLOOR (RADIUS_PARTICLE) + 1.0) * (2.0 * FLOOR (RADIUS_PARTICLE) + 1.0); ++n) {
				offset += direction;
				if (offset.x == offset.y || (offset.x < 0.0 && offset.x == -offset.y) || (offset.x > 0.0 && offset.x == 1.0 - offset.y)) {
					direction = vec2 (-direction.y, direction.x);
				}
				if (dot (offset, offset) <= RADIUS_PARTICLE * RADIUS_PARTICLE) {
					particleVelocity = texture (iChannel1, (fragCoord + offset) / iResolution.xy).b;
					if (particleVelocity >= 0.0 && ++count >= PARTICLE_COUNT_MIN) {
						color += particleColor (particleVelocity);
						break;
					}
				}
			}
#endif
		}
	}

	// Display the direction of the gravity
	data = texture (iChannel2, vec2 (1.5, 0.5) / iResolution.xy);
	float gravityTimer = data.g;
	if (gravityTimer > 0.0) {
		float gravityDirection = data.r;
		vec2 frag = fragCoord - 0.5 * iResolution.xy;
		vec2 direction = vec2 (cos (gravityDirection), sin (gravityDirection));
		vec2 pointA = 25.0 * direction;
		vec2 pointB = 15.0 * direction;
		vec2 offset = 10.0 * vec2 (direction.y, -direction.x);
		float dist = segDist (frag, -pointA, pointA);
		dist = min (dist, segDist (frag, pointA, pointB + offset));
		dist = min (dist, segDist (frag, pointA, pointB - offset));
		color = mix (color, vec3 (smoothstep (4.0, 3.0, dist)), gravityTimer * smoothstep (6.0, 5.0, dist));
	}

	// Set the fragment color
	fragColor = vec4 (color, 1.0);
}

// Update the velocity and position of each particle

#define COLLISION_SPRING_STIFFNESS_COLLIDER	5000.0
#define COLLISION_SPRING_STIFFNESS_PARTICLE	1500.0
#define COLLISION_SPRING_DAMPING			10.0
#define RADIUS_COLLIDER						0.5
#define RADIUS_PARTICLE						1.0
#define PACKED
#define GRAVITY								10.0
#define TIME_STEP_MAX						0.01
#define VELOCITY_MAX						1.0
#define SPAWN_VELOCITY						vec2 (-200.0, 0.0)
#define SPAWN_POSITION						iResolution.xy - 12.5
#define SQRT3								1.732

#define CEIL(x) float (int (x + 0.9999)) // To workaround a bug with Firefox on Windows...

vec2 particleVelocity;
vec2 particlePosition;
vec2 particleForce;
vec2 particleIdCheck;

void collide (in vec2 offset) {

	// Get the position of the cell
	vec2 cellPosition = floor (particlePosition + offset) + 0.5;

	// Get the particle ID and the collider
	vec4 data = texture (iChannel1, cellPosition / iResolution.xy);
	vec2 particleId = data.rg;
	float collider = data.a;

	// Check whether there is a particle here
	if (offset == vec2 (0.0)) {

		// This is the current particle
		particleIdCheck = particleId;
	}
	else if (particleId.x > 0.0) {

		// Get the velocity and position of this other particle
		data = texture (iChannel0, particleId / iResolution.xy);
		vec2 otherParticleVelocity = data.rg;
		vec2 otherParticlePosition = data.ba;

		// Compute the distance between these 2 particles
		vec2 direction = otherParticlePosition - particlePosition;
		float distSquared = dot (direction, direction);

		// Check whether these 2 particles touch each other
		if (distSquared < 4.0 * RADIUS_PARTICLE * RADIUS_PARTICLE) {

			// Normalize the direction
			float dist = sqrt (distSquared);
			direction /= dist;

			// Apply the collision force (spring)
			float compression = 2.0 * RADIUS_PARTICLE - dist;
			particleForce -= direction * (compression * COLLISION_SPRING_STIFFNESS_PARTICLE - dot (otherParticleVelocity - particleVelocity, direction) * COLLISION_SPRING_DAMPING);
		}
	}

	// Collision with a collider?
	if (collider > 0.5) {

		// Compute the distance between the center of the particle and the collider
		vec2 direction = cellPosition - particlePosition;
		vec2 distCollider = max (abs (direction) - RADIUS_COLLIDER, 0.0);
		float distSquared = dot (distCollider, distCollider);

		// Check whether the particle touches the collider
		if (distSquared < RADIUS_PARTICLE * RADIUS_PARTICLE) {

			// Normalize the direction
			float dist = sqrt (distSquared);
			direction = sign (direction) * distCollider / dist;

			// Apply the collision force (spring)
			float compression = RADIUS_PARTICLE - dist;
			particleForce -= direction * (compression * COLLISION_SPRING_STIFFNESS_COLLIDER + dot (particleVelocity, direction) * COLLISION_SPRING_DAMPING);
		}
	}
}

vec2 rand (in float seed) {
	vec2 n = seed * vec2 (12.9898, 78.233);
	return fract (n.yx * fract (n));
}

void mainImage (out vec4 fragColor, in vec2 fragCoord) {

	// Check for a reset
	bool reset = iFrame == 0 || texture (iChannel2, vec2 (0.5) / iResolution.xy).a > 0.5;

	// Define the particle data
	if (reset) {

		// Define the particle spawning area
		float liquid =
			step (abs (fragCoord.x - iResolution.x * 0.5), iResolution.x * 0.5 - 5.0 - RADIUS_PARTICLE)
			* step (iResolution.y * 0.5, fragCoord.y)
			* step (fragCoord.y, iResolution.y - 5.0 - RADIUS_PARTICLE)
#ifndef PACKED
			* step (mod (fragCoord.x + SQRT3 * fragCoord.y, ceil (2.0 * RADIUS_PARTICLE)), 1.0)
			* step (mod (fragCoord.y, ceil (SQRT3 * RADIUS_PARTICLE)), 1.0);
#else
			* step (mod (fragCoord.x + fragCoord.y, 2.0), 0.5);
#endif
		float rand = 0.01 * cos (fragCoord.x * 13.37 + fragCoord.y * 17.73);

		// Initialize the particle
		particleVelocity = vec2 (0.0);
		particlePosition = liquid > 0.5 ? fragCoord + rand: vec2 (-1.0);
	} else {

		// Get the particle data
		vec4 data = texture (iChannel0, fragCoord / iResolution.xy);
		particleVelocity = data.rg;
		particlePosition = data.ba;
		if (particlePosition.x > 0.0) {

			// Get the gravity
			float gravityDirection = texture (iChannel2, vec2 (1.5, 0.5) / iResolution.xy).r;
			particleForce = GRAVITY * vec2 (cos (gravityDirection), sin (gravityDirection));

			// Check for collisions with nearby particles and colliders
			const float collisionRadius = CEIL (RADIUS_PARTICLE * 2.0);
			for (float i = -collisionRadius; i <= collisionRadius; ++i) {
				for (float j = -collisionRadius; j <= collisionRadius; ++j) {
					collide (vec2 (i, j));
				}
			}

			// Make sure the particle is still tracked
			if (particleIdCheck != fragCoord) {

				// The particle is lost...
				particlePosition = vec2 (-1.0);
			} else {

				// Limit the time step
				float timeStep = min (iTimeDelta, TIME_STEP_MAX);

				// Update the velocity of the particle
				particleVelocity += particleForce * timeStep;

				// Limit the velocity (to avoid losing track of the particle)
				vec2 delta = particleVelocity * timeStep;
				float dist = length (delta);
				if (dist > VELOCITY_MAX) {
					particleVelocity *= VELOCITY_MAX / dist;
				}

				// Update the position of the particle
				particlePosition += particleVelocity * timeStep;
			}
		} else {

			// Spawn a new particle?
			vec2 particleId = 0.5 + floor (iResolution.xy * rand (iTime));
			if (fragCoord == particleId) {
				particleVelocity = SPAWN_VELOCITY;
				particlePosition = SPAWN_POSITION;
			}
		}
	}

	// Update the fragment
	fragColor = vec4 (particleVelocity, particlePosition);
}

// Track the particles

#define VELOCITY_MAX	1.0
#define SPAWN_POSITION	iResolution.xy - 12.5

#define CEIL(x) float (int (x + 0.9999)) // To workaround a bug with Firefox on Windows...

bool reset;
vec2 particleIdFound;
float particleVelocity;

float track (in vec2 fragCoord, in vec2 offset) {

	// Get the particle ID and collider
	vec2 cellPosition = fragCoord + offset;
	vec4 data;
	if (reset) {

		// Define the colliders
		vec2 midScreen = 0.5 * iResolution.xy;
		vec2 dist = abs (fragCoord - midScreen);
		vec2 container = step (midScreen - 5.0, dist);
		container.y *= step (iResolution.y * 0.03, dist.x);
		float obstacle = step (length (fragCoord - iResolution.xy * vec2 (0.5, 0.3)), iResolution.y * 0.1);

		// Set the initial data
		data = vec4 (cellPosition, 0.0, container.x + container.y + obstacle);
	} else {

		// Get the exisiting data
		data = texture (iChannel1, cellPosition / iResolution.xy);
	}
	vec2 particleId = data.rg;
	float collider = data.a;

	// Get the position of this particle
	if (particleId.x > 0.0) {
		data = texture (iChannel0, particleId / iResolution.xy);
		vec2 particlePosition = data.ba;

		// Check whether this particle is the one to track
		vec2 delta = floor (particlePosition - fragCoord + 0.5);
		if (delta == vec2 (0.0)) {

			// Take note of the particle ID and its velocity
			particleIdFound = particleId;
			particleVelocity = length (data.rg);
		}
	}

	// Return the collider
	return collider;
}

vec2 rand (in float seed) {
	vec2 n = seed * vec2 (12.9898, 78.233);
	return fract (n.yx * fract (n));
}

float segDist (in vec2 p, in vec2 a, in vec2 b) {
	p -= a;
	b -= a;
	return length (p - b * clamp (dot (p, b) / dot (b, b), 0.0, 1.0));
}

void mainImage (out vec4 fragColor, in vec2 fragCoord) {

	// Initialization
	particleIdFound = vec2 (-1.0);
	particleVelocity = -1.0;
	float collider = 0.0;

	// Check the player inputs
	vec4 data = texture (iChannel2, vec2 (0.5) / iResolution.xy);

	// Allow to add colliders (removing particles)
	if (iMouse.z > 0.5) {
		float dist;
		if (data.b < 0.5) {
			dist = length (fragCoord - iMouse.xy);
		} else {
			dist = segDist (fragCoord, data.rg, iMouse.xy);
		}
		collider = step (dist, 3.0);
	}
	if (collider < 0.5) {

		// Check the current position
		reset = iFrame == 0 || data.a > 0.5;
		vec2 offset = vec2 (0.0);
		collider = track (fragCoord, offset);

		// Track the particle (spiral loop from the current position)
		vec2 direction = vec2 (1.0, 0.0);
		for (float n = 1.0; n < (2.0 * CEIL (VELOCITY_MAX) + 1.0) * (2.0 * CEIL (VELOCITY_MAX) + 1.0); ++n) {
			if (particleIdFound.x > 0.0) {
				break;
			}
			offset += direction;
			track (fragCoord, offset);
			if (offset.x == offset.y || (offset.x < 0.0 && offset.x == -offset.y) || (offset.x > 0.0 && offset.x == 1.0 - offset.y)) {
				direction = vec2 (-direction.y, direction.x);
			}
		}

		// Spawn a new particle?
		if (particleIdFound.x < 0.0 && fragCoord == SPAWN_POSITION) {
			vec2 particleId = 0.5 + floor (iResolution.xy * rand (iTime));
			vec2 particlePosition = texture (iChannel0, particleId / iResolution.xy).ba;
			if (particlePosition == fragCoord) {
				particleIdFound = particleId;
			}
		}
	}

	// Update the fragment
	fragColor = vec4 (particleIdFound, particleVelocity, collider);
}

// Handle player inputs

#define KEY_R		(vec2 (82.5, 0.5) / 256.0)
#define KEY_LEFT	(vec2 (37.5, 0.5) / 256.0)
#define KEY_RIGHT	(vec2 (39.5, 0.5) / 256.0)
#define KEY_SPACE	(vec2 (32.5, 0.5) / 256.0)
#define PI			3.14159265359

void mainImage (out vec4 fragColor, in vec2 fragCoord) {

	// Don't waste time
	if (fragCoord.x > 2.0 || fragCoord.y > 1.0) {
		discard;
	}

	// Get the status of the reset (R) key
	float reset = texture (iChannel3, KEY_R).r;

	// Check what to do
	if (fragCoord.x < 1.0) {

		// Update the fragment
		fragColor = vec4 (iMouse.xyz, reset);
	} else {

		// Set the direction of the gravity
		float gravityDirection;
		float gravityTimer;
		if (iFrame == 0 || reset > 0.5) {

			// Reset the gravity
			gravityDirection = -PI * 0.5;
			gravityTimer = 0.0;
		} else {

			// Get the current values
			vec2 data = texture (iChannel2, fragCoord / iResolution.xy).rg;
			gravityDirection = data.r;
			gravityTimer = data.g;

			// Get the status of the left, right and space keys
			float keyLeft = texture (iChannel3, KEY_LEFT).r;
			float keyRight = texture (iChannel3, KEY_RIGHT).r;
			float keySpace = texture (iChannel3, KEY_SPACE).r;
			if (keyLeft + keyRight + keySpace < 0.5) {
				gravityTimer = max (0.0, gravityTimer - iTimeDelta * 5.0);
			} else {
				if (keyLeft > 0.5) {
					gravityDirection -= PI * 0.5 * iTimeDelta;
				} else if (keyRight > 0.5) {
					gravityDirection += PI * 0.5 * iTimeDelta;
				} else if (gravityTimer == 0.0) {
					gravityDirection += PI;
				}
				gravityTimer = 1.0;
			}
		}

		// Update the fragment
		fragColor = vec4 (gravityDirection, gravityTimer, 0.0, 0.0);
	}
}

info.description:
Similar to [url=https://www.shadertoy.com/view/4sK3zm]Liquid stuff[/url], but with better physics.

- Mouse: draw colliders;
- Mouse + SPACE: erase colliders;
- LEFT / RIGHT: rotate the gravity;
- DOWN: invert the gravity;
- R: reset the scene.

