	if (reset) {

		// Define the particle spawning area
		float liquid =
			step (fragCoord.y, iResolution.y * 0.5)
			* step (mod (fragCoord.x + SQRT3 * fragCoord.y, ceil (2.0 * PARTICLE_RADIUS)), 1.0)
			* step (mod (fragCoord.y, ceil (SQRT3 * PARTICLE_RADIUS)), 1.0);

