vec2 rand (in float seed) {
	vec2 n = seed * vec2 (12.9898, 78.233);
	return fract (n.yx * fract (n));
}

float segDist (in vec2 p, in vec2 a, in vec2 b) {
	p -= a;
	b -= a;
	return length (p - b * clamp (dot (p, b) / dot (b, b), 0.0, 1.0));
}

void mainImage (out vec4 fragColor, in vec2 fragCoord) {

	// Initialization
	particleIdFound = vec2 (-1.0);
	particleVelocity = -1.0;
	float collider = 0.0;

	// Check the player inputs
	vec4 data = texture (iChannel3, vec2 (0.5) / iResolution.xy);
	reset = iFrame == 0 || data.a > 0.5;

	// Check the current position
	vec2 offset = vec2 (0.0);
	collider = track (fragCoord, offset);

	// Allow to add colliders (removing particles)
	if (iMouse.z > 0.5) {
		float dist;
		if (data.b < 0.5) {
			dist = length (fragCoord - iMouse.xy);
		} else {
			dist = segDist (fragCoord, data.rg, iMouse.xy);
		}
		if (dist < 3.0) {
			collider = texture (iChannel3, vec2 (1.5, 0.5) / iResolution.xy).b;
		}
	}
	if (collider < 0.5) {

		// Track the particle (spiral loop from the current position)
		vec2 direction = vec2 (1.0, 0.0);
		for (float n = 1.0; n < (2.0 * CEIL (PARTICLE_VELOCITY_MAX) + 1.0) * (2.0 * CEIL (PARTICLE_VELOCITY_MAX) + 1.0); ++n) {
			if (particleIdFound.x > 0.0) {
				break;
			}
			offset += direction;
			track (fragCoord, offset);
			if (offset.x == offset.y || (offset.x < 0.0 && offset.x == -offset.y) || (offset.x > 0.0 && offset.x == 1.0 - offset.y)) {
				direction = vec2 (-direction.y, direction.x);
			}
		}

		// Spawn a new particle?
		if (particleIdFound.x < 0.0 && fragCoord == floor (PARTICLE_SPAWN_POSITION) + 0.5) {
			vec2 particleId = 0.5 + floor (iResolution.xy * rand (iTime));
			vec2 particlePosition = texture (iChannel1, particleId / iResolution.xy).ba;
			if (particlePosition == fragCoord) {
				particleIdFound = particleId;
			}
		}
	}

	// Update the fragment
	fragColor = vec4 (particleIdFound, particleVelocity, collider);
}

// Handle player inputs

// iChannel0 = Keyboard
// iChannel1 = [nothing]
// iChannel2 = [nothing]
// iChannel3 = Buf D (inputs)

#define KEY_R		(vec2 (82.5, 0.5) / 256.0)
#define KEY_LEFT	(vec2 (37.5, 0.5) / 256.0)
#define KEY_RIGHT	(vec2 (39.5, 0.5) / 256.0)
#define KEY_DOWN	(vec2 (40.5, 0.5) / 256.0)
#define KEY_SPACE	(vec2 (32.5, 0.5) / 256.0)
#define PI			3.14159265359

void mainImage (out vec4 fragColor, in vec2 fragCoord) {

	// Don't waste time
	if (fragCoord.x > 2.0 || fragCoord.y > 1.0) {
		discard;
	}

	// Get the status of the reset (R) key
	float reset = texture (iChannel0, KEY_R).r;

	// Check what to do
	if (fragCoord.x < 1.0) {

		// Update the fragment
		fragColor = vec4 (iMouse.xyz, reset);
	} else {

		// Set the direction of the gravity
		float gravityDirection;
		float gravityTimer;
		if (iFrame == 0 || reset > 0.5) {

			// Reset the gravity
			gravityDirection = -PI * 0.5;
			gravityTimer = 0.0;
		} else {

			// Get the current values
			vec2 data = texture (iChannel3, fragCoord / iResolution.xy).rg;
			gravityDirection = data.r;
			gravityTimer = data.g;

			// Get the status of the left, right and down keys
			float keyLeft = texture (iChannel0, KEY_LEFT).r;
			float keyRight = texture (iChannel0, KEY_RIGHT).r;
			float keyDown = texture (iChannel0, KEY_DOWN).r;
			if (keyLeft + keyRight + keyDown < 0.5) {
				gravityTimer = max (0.0, gravityTimer - iTimeDelta * 5.0);
			} else {
				if (keyLeft > 0.5) {
					gravityDirection -= PI * 0.5 * iTimeDelta;
				} else if (keyRight > 0.5) {
					gravityDirection += PI * 0.5 * iTimeDelta;
				} else if (gravityTimer == 0.0) {
					gravityDirection += PI;
				}
				gravityTimer = 1.0;
			}
		}

		// Get the status of the space key
		float keySpace = texture (iChannel0, KEY_SPACE).r;

		// Update the fragment
		fragColor = vec4 (gravityDirection, gravityTimer, 1.0 - keySpace, 0.0);
	}
}

// Global rendering switch
//#define FAST

// Rendering parameters
#define RAY_LENGTH_MAX		100.0
#define RAY_BOUNCE_MAX		10
#define RAY_STEP_MAX		100
#define LIGHT				vec3 (-1.0, 0.5, 0.0)
#define AMBIENT				0.2
#define SPECULAR_POWER		3.0
#define SPECULAR_INTENSITY	0.8
#define DIAMOND_FACES		8.0
#define RING_ROTATE			PI * 0.465
#define RING_TRANSLATE		3.0
#define TRAY_HEIGHT			1.3

// Rendering options (for those who have a slow GPU)
#define PROPAGATION
#ifndef FAST
	#define DISPERSION
	#define COVER
#endif

// Macros used to handle color channels
#ifdef DISPERSION
	#define COLOR float
	#define CHANNEL(x) dot (x, channel)
#else
	#define COLOR vec3
	#define CHANNEL(x) x
#endif

// Math constants
#define DELTA	0.01
#define PI		3.14159265359

// Rotation on the X axis
vec3 rotateX (in vec3 p, in float angle) {
	float c = cos (angle);
	float s = sin (angle);
	return vec3 (p.x, c * p.y + s * p.z, c * p.z - s * p.y);
}

// Rotation on the Y axis
vec3 rotateY (in vec3 p, in float angle) {
	float c = cos (angle);
	float s = sin (angle);
	return vec3 (c * p.x - s * p.z, p.y, c * p.z + s * p.x);
}

// Materials
struct Material {
	vec3 color;
	float behavior; // from -1.0 (fully reflective) to 1.0 (fully transparent)
	vec3 refractIndex; // not used if behavior < 0.0
};
Material getMaterial (in int materialIndex) {
	Material material;
	if (materialIndex == 0) { // Ring
		material = Material (vec3 (1.0, 1.0, 0.8), -0.5, vec3 (1.50, 1.55, 1.60));
	} else if (materialIndex == 1) { // Diamond
		material = Material (vec3 (0.9, 0.9, 1.0), 0.7, vec3 (2.407, 2.426, 2.451));
	} else if (materialIndex == 2) { // Tray
		material = Material (vec3 (0.5, 0.5, 0.7), -0.4, vec3 (1.50, 1.55, 1.60));
	} else { // materialIndex == 3 // Cover
		material = Material (vec3 (1.0, 1.0, 1.0), 0.8, vec3 (1.50, 1.55, 1.60));
	}
	return material;
}

// Distance to the ring
float getDistanceRing (in vec3 p) {
	vec2 q = vec2 (length (p.xy) - 7.8, p.z);
	float d1 = max (length (q) - 0.6, -0.2 - q.x);
	float d2 = length (max (abs (vec3 (abs (p.x), p.y, abs (p.z)) - vec3 (0.7, 8.2, 0.7)) - vec3 (0.1, 0.5, 0.1), 0.0)) - 0.1;
	return min (d1, d2);
}

// Distance to the diamond
vec3 normalTopA = normalize (vec3 (0.0, 1.0, 1.4));
vec3 normalTopB = normalize (vec3 (0.0, 1.0, 1.0));
vec3 normalTopC = normalize (vec3 (0.0, 1.0, 0.5));
vec3 normalBottomA = normalize (vec3 (0.0, -1.0, 1.0));
vec3 normalBottomB = normalize (vec3 (0.0, -1.0, 1.6));
float getDistanceDiamond (in vec3 p) {
	p.y -= 10.0;
	float topCut = p.y - 1.0;
	float angleStep = PI / DIAMOND_FACES;
	float angle = angleStep * (0.5 + floor (atan (p.x, p.z) / angleStep));
	vec3 q = rotateY (p, angle);
	float topA = dot (q, normalTopA) - 2.0;
	float topC = dot (q, normalTopC) - 1.5;
	float bottomA = dot (q, normalBottomA) - 1.7;
	q = rotateY (p, -angleStep * 0.5);
	angle = angleStep * floor (atan (q.x, q.z) / angleStep);
	q = rotateY (p, angle);
	float topB = dot (q, normalTopB) - 1.85;
	float bottomB = dot (q, normalBottomB) - 1.9;

	return max (topCut, max (topA, max (topB, max (topC, max (bottomA, bottomB)))));
}

// Distance to the tray
float getDistanceTray (in vec3 p) {
	p.y += TRAY_HEIGHT;
	float d = length (p.xz);
	return max (min (d - 4.0, max (d - 17.0, -p.y - 1.0)), p.y);
}

// Distance to the cover
float getDistanceCover (in vec3 p) {
	p.y += TRAY_HEIGHT + 4.0;
	float d = length (p) - 17.0;
	return max (max (d, -d - 1.0), -p.y + 3.5);
}

// Distance to a given material
float getDistanceMaterial (in vec3 p, in int materialIndex) {
	vec3 q = rotateX (p + vec3 (0.0, 0.0, RING_TRANSLATE), RING_ROTATE);
	float materialDist;
	if (materialIndex == 0) {
		materialDist = getDistanceRing (q);
	} else if (materialIndex == 1) {
		materialDist = getDistanceDiamond (q);
	} else if (materialIndex == 2) {
		materialDist = getDistanceTray (p);
	#ifdef COVER
	} else { // materialIndex == 3
		materialDist = getDistanceCover (p);
	#endif
	}
	return materialDist;
}

// Distance to the scene
#define MATERIAL_PROCESS(MATERIAL) if (materialDist < 0.0) materialTo = MATERIAL; sceneDist = min (sceneDist, materialFrom != MATERIAL ? materialDist : -materialDist);
float getDistanceScene (in vec3 p, in int materialFrom, out int materialTo) {
	vec3 q = rotateX (p + vec3 (0.0, 0.0, RING_TRANSLATE), RING_ROTATE);

	// Air
	materialTo = -1;
	float sceneDist = RAY_LENGTH_MAX;

	// Ring
	float materialDist = getDistanceRing (q);
	MATERIAL_PROCESS (0)

	// Diamond
	materialDist = getDistanceDiamond (q);
	MATERIAL_PROCESS (1)

	// Tray
	materialDist = getDistanceTray (p);
	MATERIAL_PROCESS (2)

	// Cover
	#ifdef COVER
	materialDist = getDistanceCover (p);
	MATERIAL_PROCESS (3)
	#endif

	// Return the distance
	return sceneDist;
}

// Normal at a given point
vec3 getNormal (in vec3 p, in int materialIndex) {
	const vec2 h = vec2 (DELTA, -DELTA);
	return normalize (
		h.xxx * getDistanceMaterial (p + h.xxx, materialIndex) +
		h.xyy * getDistanceMaterial (p + h.xyy, materialIndex) +
		h.yxy * getDistanceMaterial (p + h.yxy, materialIndex) +
		h.yyx * getDistanceMaterial (p + h.yyx, materialIndex)
	);
}

// Cast a ray for a given color channel (and its corresponding refraction index)
vec3 lightDirection = normalize (LIGHT);
COLOR raycast (in vec3 origin, in vec3 direction, in vec4 normal, in int materialTo, in COLOR color, in vec3 channel) {

	// Check the behavior of the material
	Material material = getMaterial (materialTo);
	float alpha = abs (material.behavior);
	color *= 1.0 - alpha;

	// The ray continues...
	int materialFrom = -1;
	float refractIndexFrom = 1.0;
	for (int rayBounce = 1; rayBounce < RAY_BOUNCE_MAX; ++rayBounce) {

		// Interface with the material
		float refractIndexTo;
		vec3 refraction;
		if (materialTo == -1) {
			refractIndexTo = 1.0;
			refraction = refract (direction, normal.xyz, refractIndexFrom);
		} else {
			refractIndexTo = dot (material.refractIndex, channel);
			refraction = material.behavior < 0.0 ? vec3 (0.0) : refract (direction, normal.xyz, refractIndexFrom / refractIndexTo);
		}
		if (dot (refraction, refraction) < DELTA) {
			direction = reflect (direction, normal.xyz);
			origin += direction * DELTA * 2.0;
		} else {
			direction = refraction;
			materialFrom = materialTo;
			refractIndexFrom = refractIndexTo;
		}

		// Ray marching
		for (int rayStep = 0; rayStep < RAY_STEP_MAX; ++rayStep) {
			float dist = max (getDistanceScene (origin, materialFrom, materialTo), DELTA);
			normal.w += dist;
			if (materialFrom != materialTo || normal.w > RAY_LENGTH_MAX) {
				break;
			}
			origin += direction * dist;
		}

		// Check whether we hit something
		if (materialFrom == materialTo) {
			break;
		}

		// Get the normal
		if (materialTo == -1) {
			normal.xyz = -getNormal (origin, materialFrom);
		} else {
			normal.xyz = getNormal (origin, materialTo);

			// Basic lighting
			material = getMaterial (materialTo);
			float relfectionDiffuse = max (0.0, dot (normal.xyz, lightDirection));
			float relfectionSpecular = pow (max (0.0, dot (reflect (direction, normal.xyz), lightDirection)), SPECULAR_POWER) * SPECULAR_INTENSITY;
			COLOR localColor = (AMBIENT + relfectionDiffuse) * CHANNEL (material.color) + relfectionSpecular;
			float localAlpha = abs (material.behavior);
			color += localColor * (1.0 - localAlpha) * alpha;
			alpha *= localAlpha;
		}
	}

	// Get the background color
	COLOR backColor = CHANNEL (texture (iChannel0, -direction).rgb);

	// Return the intensity of this color channel
	return color + backColor * alpha;
}

// Main function
void mainImage (out vec4 fragColor, in vec2 fragCoord) {

	// Define the ray corresponding to this fragment
	vec2 frag = (2.0 * fragCoord.xy - iResolution.xy) / iResolution.y;
	vec3 direction = normalize (vec3 (frag, 4.0));

	// Set the camera
	vec3 origin = (35.0 + 10.0 * cos (iTime * 0.5)) * vec3 (cos (iTime * 0.1), 0.6 + 0.5 * sin (iTime * 0.2), sin (iTime * 0.1));
	vec3 forward = -origin;
	vec3 up = vec3 (sin (iTime * 0.3), 2.0, 0.0);
	mat3 rotation;
	rotation [2] = normalize (forward);
	rotation [0] = normalize (cross (up, forward));
	rotation [1] = cross (rotation [2], rotation [0]);
	direction = rotation * direction;
	origin.z += 6.0;

	// Cast the initial ray
	vec4 normal = vec4 (0.0);
	int materialTo = -1;
	for (int rayStep = 0; rayStep < RAY_STEP_MAX; ++rayStep) {
		float dist = max (getDistanceScene (origin, -1, materialTo), DELTA);
		normal.w += dist;
		if (materialTo != -1 || normal.w > RAY_LENGTH_MAX) {
			break;
		}
		origin += direction * dist;
	}

	// Check whether we hit something
	if (materialTo == -1) {
		fragColor.rgb = texture (iChannel0, -direction).rgb;
	} else {

		// Get the normal
		normal.xyz = getNormal (origin, materialTo);

		// Basic lighting
		float relfectionDiffuse = max (0.0, dot (normal.xyz, lightDirection));
		float relfectionSpecular = pow (max (0.0, dot (reflect (direction, normal.xyz), lightDirection)), SPECULAR_POWER) * SPECULAR_INTENSITY;
		fragColor.rgb = (AMBIENT + relfectionDiffuse) * getMaterial (materialTo).color + relfectionSpecular;

		// The ray continues...
		#ifdef PROPAGATION
			#ifdef DISPERSION
				fragColor.r = raycast (origin, direction, normal, materialTo, fragColor.r, vec3 (1.0, 0.0, 0.0));
				fragColor.g = raycast (origin, direction, normal, materialTo, fragColor.g, vec3 (0.0, 1.0, 0.0));
				fragColor.b = raycast (origin, direction, normal, materialTo, fragColor.b, vec3 (0.0, 0.0, 1.0));
			#else
				fragColor.rgb = raycast (origin, direction, normal, materialTo, fragColor.rgb, vec3 (1.0 / 3.0));
			#endif
		#endif
	}

	// Set the alpha channel
	fragColor.a = 1.0;
}

// Rendering parameters
#define CAMERA_FOCAL_LENGTH	3.0
#define CAMERA_SPEED		10.0
#define RAY_STEP_MAX		100.0
#define RAY_STEP_DIST		0.3
#define RAY_LENGTH_MAX		20.0
#define NOISE_FACTOR		6.0
#define NOISE_FREQUENCY		0.4
#define NOISE_TEXTURE_SIZE	64.0
#define CLOUDS_SPACING		1.0
#define DENSITY_STEP_MAX	1.0
#define DENSITY_FACTOR		0.05
#define COLOR_CLOUDS_A		vec3 (0.2, 0.2, 0.4)
#define COLOR_CLOUDS_B		vec3 (0.95, 0.9, 1.0)
#define COLOR_SKY_A			vec3 (0.95, 0.95, 1.0)
#define COLOR_SKY_B			vec3 (0.4, 0.4, 0.6)
#define COLORIZE

// Math constants
#define PI 3.14159265359

// PRNG
float rand (in vec2 seed) {
//	seed = mod (seed, NOISE_TEXTURE_SIZE);
	return fract (sin (dot (seed, vec2 (11.9898, 78.233))) * 137.5453);
}

// Noise
float noise (in vec3 p) {
	vec3 f = fract (p);
	f = f * f * (3.0 - 2.0 * f);
	p = floor (p);
	p.xy += p.z * vec2 (37.0, 17.0);
#ifdef NOISE_TEXTURE_SIZE
	f.xy = texture (iChannel0, (mod (p.xy + f.xy, NOISE_TEXTURE_SIZE) + 0.5) / iResolution.xy).xy;
#else
	vec2 q = p.xy - vec2 (37.0, 17.0);
	vec2 n = vec2 (1.0, 0.0) + 0.5;
	f.xy = vec2(
		mix (
			mix (rand (q + n.yy), rand (q + n.xy), f.x),
			mix (rand (q + n.yx), rand (q + n.xx), f.x),
			f.y
		),
		mix (
			mix (rand (p.xy + n.yy), rand (p.xy + n.xy), f.x),
			mix (rand (p.xy + n.yx), rand (p.xy + n.xx), f.x),
			f.y
		)
	);
#endif
	return mix (f.x, f.y, f.z);
}

// FBM
float fbm (in vec3 p) {
#if 0
	return noise (p) / 2.0 + noise (p * 2.0) / 4.0 + noise (p * 4.0) / 8.0;
#else
	return (noise (p) + noise (p * 2.0) / 2.0 + noise (p * 4.0) / 4.0) / (1.0 + 1.0 / 2.0 + 1.0 / 4.0);
#endif
}

// Distance to the scene
float distScene (in vec3 p, in float noise) {
	return CLOUDS_SPACING - abs (p.y + noise);
}

// HSV to RGB
vec3 hsv2rgb (in vec3 hsv) {
	hsv.yz = clamp (hsv.yz, 0.0, 1.0);
	return hsv.z * (1.0 + hsv.y * clamp (abs (fract (hsv.x + vec3 (0.0, 2.0 / 3.0, 1.0 / 3.0)) * 6.0 - 3.0) - 2.0, -1.0, 0.0));
}

// Main function
void mainImage (out vec4 fragColor, in vec2 fragCoord) {

	// Define the position and orientation of the camera
	vec3 rayOrigin = vec3 (0.0, 1.5 * cos (iTime * 0.5), CAMERA_SPEED * iTime);
	float cameraAngleY = PI * 0.6 * cos(iTime * 0.3);
	vec3 cameraForward = vec3 (sin (cameraAngleY), 0.0, cos (cameraAngleY));
	vec3 cameraUp = vec3 (sin (iTime * 0.2), 1.0, 0.0);
	mat3 cameraOrientation;
	cameraOrientation [2] = normalize (cameraForward);
	cameraOrientation [0] = normalize (cross (cameraUp, cameraForward));
	cameraOrientation [1] = cross (cameraOrientation [2], cameraOrientation [0]);
	vec3 rayDirection = cameraOrientation * normalize (vec3 ((2.0 * fragCoord.xy - iResolution.xy) / iResolution.y, CAMERA_FOCAL_LENGTH));

	// Define the colors
	float colorMix = smoothstep (-0.4, 0.4, cos (iTime * 0.2));
	vec3 colorClouds = mix (COLOR_CLOUDS_A, COLOR_CLOUDS_B, colorMix);
	vec3 colorSky = mix (COLOR_SKY_A, COLOR_SKY_B, colorMix);

	// Ray marching
#ifdef COLORIZE
	vec3 color = vec3 (0.0);
	float colorCloudsValue = max (max (colorClouds.x, colorClouds.y), colorClouds.z);
	float colorCloudsSaturation = colorCloudsValue > 0.0 ? 1.0 - min (min (colorClouds.x, colorClouds.y), colorClouds.z) / colorCloudsValue : 0.0;
#endif
	float densityTotal = 0.0;
	float rayLength = 0.0;
	for (float rayStep = 0.0; rayStep < RAY_STEP_MAX; ++rayStep) {

		// Compute the maximum density
		float densityMax = 1.0 - rayLength / max (RAY_LENGTH_MAX, RAY_STEP_MAX * RAY_STEP_DIST);
		if (densityTotal >= densityMax) {
			break;
		}

		// Get the distance to the scene
		vec3 p = rayOrigin + rayDirection * rayLength;
		float f = NOISE_FACTOR * (fbm (p * NOISE_FREQUENCY) - 0.5);
		float dist = distScene (p, f);
		if (dist < 0.0) {

			// Compute the local density
			float densityLocal = min (-dist, DENSITY_STEP_MAX) * DENSITY_FACTOR;
			densityLocal *= densityMax - densityTotal;

#ifdef COLORIZE
			// Update the color
			color += hsv2rgb (vec3 (noise (p * 0.2), colorCloudsSaturation, densityLocal * colorCloudsValue));
#endif

			// Update the total density
			densityTotal += densityLocal;
		}

		// Go ahead
		rayLength += max (RAY_STEP_DIST, dist - NOISE_FACTOR);
	}
#ifdef COLORIZE
	color += colorSky * (1.0 - densityTotal);
#else
	vec3 color = mix (colorSky, colorClouds, densityTotal);
#endif

	// Set the fragment color
	fragColor = vec4 (color, 1.0);
}

// Rendering parameters
#define NOISE_TEXTURE_SIZE	64.0

// PRNG
float rand (in vec2 seed) {
	return fract (sin (dot (seed, vec2 (11.9898, 78.233))) * 137.5453);
}

// Main function
void mainImage (out vec4 fragColor, in vec2 fragCoord) {

	// Don't waste time
	if (iFrame > 1 || fragCoord.x > (NOISE_TEXTURE_SIZE + 1.0) || fragCoord.y > (NOISE_TEXTURE_SIZE + 1.0)) {
		discard;
	}

	// Set the fragment color
	vec2 current = mod (fragCoord, NOISE_TEXTURE_SIZE);
	vec2 previous = mod (current - vec2 (37.0, 17.0), NOISE_TEXTURE_SIZE);
	fragColor = vec4 (rand (previous), rand (current), 1.0, 1.0);
}

