// Parameters
#define VOXEL_RESOLUTION	1.0
#define VOXEL_LIGHTING
#define SHADOW
#define GROUND
#define GHOST
#define MOUSE
#define HSV2RGB_FAST

#define CAMERA_FOCAL_LENGTH	8.0
#define DELTA				0.01
#define RAY_LENGTH_MAX		500.0
#define RAY_STEP_MAX		100.0
#define AMBIENT				0.2
#define SPECULAR_POWER		2.0
#define SPECULAR_INTENSITY	0.3
#define SHADOW_LENGTH		150.0
#define SHADOW_POWER		3.0
#define FADE_POWER			0.7
#define BACKGROUND			0.7
#define GLOW				0.4
#define GAMMA				0.8

// Math constants
#define PI		3.14159265359
#define SQRT3	1.73205080757

// Global variable to handle the glow effect
float glowCounter;

// PRNG (from https://www.shadertoy.com/view/4djSRW)
float rand (in vec3 seed) {
	seed = fract (seed * vec3 (5.3983, 5.4427, 6.9371));
	seed += dot (seed.yzx, seed.xyz + vec3 (21.5351, 14.3137, 15.3219));
	return fract (seed.x * seed.y * seed.z * 95.4337);
}

// Distance to the voxel
float distVoxel (in vec3 p) {

	// Update the glow counter
	++glowCounter;

	// Rounded box
	const float voxelRadius = 0.25;
	return length (max (abs (p) - 0.5 + voxelRadius, 0.0)) - voxelRadius;
}

// Distance to the scene and color of the closest point
vec2 distScene (in vec3 p, out vec3 P) {

	// Update the glow counter
	++glowCounter;

	// Scaling
	p *= VOXEL_RESOLUTION;

	// Velocity, period of the waves, spacing of the gums
	float v = VOXEL_RESOLUTION * floor (iTime * 100.0 / VOXEL_RESOLUTION);
	const float k1 = 0.05;
	const float k2 = 60.0;

	// Giant Pac-Man
	float body = length (p);
	float eyes = 6.0 - length (vec3 (abs (p.x) - 12.5, p.y - 19.5, p.z - 20.0));
	float mouthAngle = PI * (0.075 + 0.065 * cos (2.0 * v * PI / k2));
	float mouthTop = dot (p, vec3 (0.0, -cos (mouthAngle), sin (mouthAngle))) - 2.0;
	mouthAngle *= 2.5;
	float mouthBottom = dot (p, vec3 (0.0, cos (mouthAngle), sin (mouthAngle)));
	float pacMan = max (max (body - 32.0, eyes), min (mouthTop, mouthBottom));
	vec2 d = vec2 (pacMan, pacMan > 29.0 - body ? 0.13 : 1.0);
	P = p;

	// Gums
	vec3 q = vec3 (p.xy, mod (p.z + v, k2) - k2 * 0.5);
	float gum = max (length (q) - 6.0, -p.z);
	if (gum < d.x) {
		d = vec2 (gum, 0.35);
		P = q;
	}

	// Ground
	#ifdef GROUND
	q = vec3 (p.xy, p.z + v);
	float ground = (q.y + 50.0 + 14.0 * cos (q.x * k1) * cos (q.z * k1)) * 0.7;
	if (ground < d.x) {
		d = vec2 (ground, 0.55);
		P = q;
	}
	#endif

	// Ghost
	#ifdef GHOST
	v = VOXEL_RESOLUTION * floor ((110.0 + 40.0 * cos (iTime * 3.0)) / VOXEL_RESOLUTION);
	q = vec3 (p.xy, p.z + v);
	body = length (vec3 (q.x, max (q.y - 4.0, 0.0), q.z));
	eyes = 8.0 - length (vec3 (abs (q.x) - 12.0, q.y - 10.0, q.z - 22.0));
	float bottom = (q.y + 28.0 + 4.0 * cos (p.x * 0.4) * cos (p.z * 0.4)) * 0.7;
	float ghost = max (max (body - 28.0, eyes), -bottom);
	if (ghost < d.x) {
		d = vec2 (ghost, ghost > 25.0 - body ? 0.76 : 1.0);
		P = q;
	}
	#endif

	// Scaling
	d.x /= VOXEL_RESOLUTION;
	return d;
}

// Distance to the (voxelized?) scene
vec4 dist (inout vec3 p, in vec3 ray, in float voxelized, in float rayLengthMax) {
	vec3 P = p;
	vec2 d = vec2 (1e10, 0.0);
	float rayLength = 0.0;
	float rayLengthInVoxel = 0.0;
	float rayLengthCheckVoxel = 0.0;
	vec3 raySign = sign (ray);
	vec3 rayDeltaVoxel = raySign / ray;
	for (float rayStep = 0.0; rayStep < RAY_STEP_MAX; ++rayStep) {
		if (rayLength < rayLengthInVoxel) {
			d.x = distVoxel (fract (p + 0.5) - 0.5);
			if (d.x < DELTA) {
				break;
			}
		} else if (rayLength < rayLengthCheckVoxel) {
			vec3 rayDelta = (0.5 - raySign * (fract (p + 0.5) - 0.5)) * rayDeltaVoxel;
			float dNext = min (rayDelta.x, min (rayDelta.y, rayDelta.z));
			d = distScene (floor (p + 0.5), P);
			if (d.x < 0.0) {
				rayDelta = rayDeltaVoxel - rayDelta;
				d.x = max (rayLengthInVoxel - rayLength, DELTA - min (rayDelta.x, min (rayDelta.y, rayDelta.z)));
				rayLengthInVoxel = rayLength + dNext;
			} else {
				d.x = DELTA + dNext;
			}
		} else {
			d = distScene (p, P);
			if (voxelized > 0.5) {
				if (d.x < SQRT3 * 0.5) {
					rayLengthCheckVoxel = rayLength + abs (d.x) + SQRT3 * 0.5;
					d.x = max (rayLengthInVoxel - rayLength + DELTA, d.x - SQRT3 * 0.5);
				}
			} else if (d.x < DELTA) {
				break;
			}
		}
		rayLength += d.x;
		if (rayLength > rayLengthMax) {
			break;
		}
		p += d.x * ray;
	}
	return vec4 (d, rayLength, rand (P));
}

// Normal at a given point
vec3 normal (in vec3 p, in float voxelized) {
	vec2 h = vec2 (DELTA, -DELTA);
	vec3 n;
	if (voxelized > 0.5) {
		p = fract (p + 0.5) - 0.5;
		n = h.xxx * distVoxel (p + h.xxx) +
			h.xyy * distVoxel (p + h.xyy) +
			h.yxy * distVoxel (p + h.yxy) +
			h.yyx * distVoxel (p + h.yyx);
	} else {
		n = h.xxx * distScene (p + h.xxx, n).x +
			h.xyy * distScene (p + h.xyy, n).x +
			h.yxy * distScene (p + h.yxy, n).x +
			h.yyx * distScene (p + h.yyx, n).x;
	}
	return normalize (n);
}

// HSV to RGB
vec3 hsv2rgb (in vec3 hsv) {
	#ifdef HSV2RGB_SAFE
	hsv.yz = clamp (hsv.yz, 0.0, 1.0);
	#endif
	#ifdef HSV2RGB_FAST
	return hsv.z * (1.0 + 0.5 * hsv.y * (cos (2.0 * PI * (hsv.x + vec3 (0.0, 2.0 / 3.0, 1.0 / 3.0))) - 1.0));
	#else
	return hsv.z * (1.0 + hsv.y * clamp (abs (fract (hsv.x + vec3 (0.0, 2.0 / 3.0, 1.0 / 3.0)) * 6.0 - 3.0) - 2.0, -1.0, 0.0));
	#endif
}

// Main function
void mainImage (out vec4 fragColor, in vec2 fragCoord) {

	// Get the fragment
	vec2 frag = (2.0 * fragCoord.xy - iResolution.xy) / iResolution.y;

	// Define the rendering mode
	float modeTiming = iTime * 0.234;
	float modeAngle = PI * cos (iTime * 0.2);
	modeAngle = dot (frag - vec2 (cos (iTime * 2.0), 0.0), vec2 (cos (modeAngle), sin (modeAngle)));
	float modeVoxel = step (0.5, fract (modeTiming / (4.0 * PI)));
	modeTiming = cos (modeTiming);
	float mode3D = smoothstep (0.8, 0.5, modeTiming);
	float modeSwitch = smoothstep (0.995, 1.0, modeTiming) + smoothstep (0.02, 0.0, abs (modeAngle)) * modeVoxel;
	modeVoxel = 1.0 + (step (0.0, modeAngle) - 1.0) * modeVoxel;

	// Define the ray corresponding to this fragment
	vec3 ray = normalize (vec3 (frag, mix (12.0, CAMERA_FOCAL_LENGTH, mode3D)));

	// Compute the orientation of the camera
	float yawAngle = PI * (1.2 + 0.2 * cos (iTime * 0.5));
	float pitchAngle = PI * (0.1 * cos (iTime * 0.3) - 0.05);
	#ifdef MOUSE
	yawAngle += 4.0 * PI * iMouse.x / iResolution.x;
	pitchAngle += PI * 0.3 * (1.0 - iMouse.y / iResolution.y);
	#endif
	yawAngle = mix (PI * 1.5, yawAngle, mode3D);
	pitchAngle *= mode3D;

	float cosYaw = cos (yawAngle);
	float sinYaw = sin (yawAngle);
	float cosPitch = cos (pitchAngle);
	float sinPitch = sin (pitchAngle);

	mat3 cameraOrientation;
	cameraOrientation [0] = vec3 (cosYaw, 0.0, -sinYaw);
	cameraOrientation [1] = vec3 (sinYaw * sinPitch, cosPitch, cosYaw * sinPitch);
	cameraOrientation [2] = vec3 (sinYaw * cosPitch, -sinPitch, cosYaw * cosPitch);

	ray = cameraOrientation * ray;

	// Compute the origin of the ray
	float cameraDist = mix (400.0, 200.0 + 80.0 * cos (iTime * 0.8), mode3D);
	vec3 origin = (vec3 (0.0, 0.0, 80.0 * sin (iTime * 0.2) - 20.0) - cameraOrientation [2] * cameraDist) / VOXEL_RESOLUTION;

	// Compute the distance to the scene
	glowCounter = 0.0;
	vec4 d = dist (origin, ray, modeVoxel, RAY_LENGTH_MAX / VOXEL_RESOLUTION);

	// Set the background color
	vec3 finalColor = hsv2rgb (vec3 (0.2 * ray.y + 0.4 * modeVoxel - 0.37, 1.0, mode3D * BACKGROUND));
	vec3 glowColor = GLOW * vec3 (1.0, 0.3, 0.0) * glowCounter / RAY_STEP_MAX;
	if (d.x < DELTA) {

		// Set the object color
		vec3 color = d.y != 1.0 ? hsv2rgb (vec3 (d.y + 0.1 * d.w * modeVoxel, 0.5 + 0.5 * modeVoxel, 1.0)) : vec3 (0.0);

		// Lighting
		vec3 l = normalize (mix (vec3 (1.0, 0.0, 0.0), vec3 (1.25 + cos (iTime * 0.2), 1.0, 1.0), mode3D));
		#ifdef VOXEL_LIGHTING
		if (modeVoxel > 0.5) {
			vec3 n = normal (floor (origin + 0.5), 0.0);
			float diffuse = max (0.0, dot (n, l));
			float specular = pow (max (0.0, dot (reflect (ray, n), l)), SPECULAR_POWER) * SPECULAR_INTENSITY;
			color = (AMBIENT + diffuse) * color + specular;
		}
		#endif
		vec3 n = normal (origin, modeVoxel);
		float diffuse = dot (n, l);
		float specular;
		if (diffuse < 0.0) {
			diffuse = 0.0;
			specular = 0.0;
		} else {
			specular = pow (max (0.0, dot (reflect (ray, n), l)), SPECULAR_POWER) * SPECULAR_INTENSITY;
			#ifdef SHADOW
			origin += n * DELTA * 2.0;
			vec4 shadow = dist (origin, l, modeVoxel, SHADOW_LENGTH / VOXEL_RESOLUTION);
			if (shadow.x < DELTA) {
				shadow.z = pow (min (1.0, shadow.z * VOXEL_RESOLUTION / SHADOW_LENGTH), SHADOW_POWER);
				diffuse *= shadow.z;
				specular *= shadow.z;
			}
			#endif
		}
		color = (AMBIENT + diffuse) * color + specular;

		// Fading
		float fade = pow (max (0.0, 1.0 - d.z * VOXEL_RESOLUTION / RAY_LENGTH_MAX), FADE_POWER);
		finalColor = mix (finalColor, color, fade);
	}

	// Set the fragment color
	finalColor = mix (pow (finalColor, vec3 (GAMMA)) + glowColor, vec3 (1.0), modeSwitch);
	fragColor = vec4 (finalColor, 1.0);
}

// This is a very basic 2D FBM example in reply to this Facebook post:
// https://www.facebook.com/groups/IndieGameDevs/permalink/10156876829611573/
// Comment out line 26 to get a triangular look.

#define SPEED		0.2
#define SCALE		3.0
#define WIDTH		0.005
#define DX			0.001
#define STAR_SIZE	0.005

// PRNG (1D)
float rand (in float seed) {
	return fract (sin (seed * 12.9898) * 137.5453);
}

// PRNG (2D)
float rand (in vec2 seed) {
	seed = fract (seed * vec2 (5.3983, 5.4427));
	seed += dot (seed.yx, seed.xy + vec2 (21.5351, 14.3137));
	return fract (seed.x * seed.y * 95.4337);
}

// Noise
float noise (in float p) {
	float f = fract (p);
//	f = f * f * (3.0 - 2.0 * f); // Comment this to get a triangular look.
	p = floor (p);
	return mix (rand (p), rand (p + 1.0), f);
}

// FBM
float fbm (in float p) {
	p *= SCALE;
	return (noise (p) + noise (p * 2.0) / 2.0 + noise (p * 4.0) / 4.0) / (1.0 + 1.0 / 2.0 + 1.0 / 4.0);
}

// Main
void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
	// Define the local coordinates
	vec2 uv = fragCoord / iResolution.y;
	uv.x -= iMouse.x / iResolution.y;
	uv *= 1.0 + iMouse.y / iResolution.y;
	uv.x += iTime * SPEED;

	// Call the FBM function to define the profile of the mountain
	float y = fbm (uv.x);

	// Compute the distance of the fragment to the mountain, to draw a nice line
#ifdef DX
	float dy = (fbm (uv.x + DX) - fbm (uv.x - DX)) / (2.0 * DX);
#else
	float dy = dFdx (y) / dFdx (uv.x);
#endif
	float dist = abs (uv.y - y) / sqrt (1.0 + dy * dy);
	float mountain = smoothstep (WIDTH * 1.1, WIDTH, dist);

	// Add some (blocky) stars above the mountain
	float star = step (0.995, rand (floor (uv / STAR_SIZE)));
	star *= step (y, uv.y) * step (WIDTH, dist);

	// Final color
	fragColor = vec4 (mountain + star, star, star, 1.0);
}

#define PI			3.14159265358979
#define STEPS		5.0
#define RADIUS		0.8
#define SLOPE		2.5
#define THICKNESS	0.08
#define AA			0.01
#define REPEAT		2.0

mat2 rotate (in float angle)
{
	float c = cos (angle);
	float s = sin (angle);
	return mat2 (c, s, -s, c);
}

float starDist (in vec2 p)
{
	// Divide the plan in STEPS sectors
	const float arc = 2.0 * PI / STEPS;
	p *= rotate (arc * floor (atan (p.y, p.x) / arc + 0.5));

	// Compute the signed distance to the triangle edge (there is one triangle per sector)
	return (p.x + abs (p.y) * SLOPE - RADIUS) / sqrt (1.0 + SLOPE * SLOPE);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
	// Normalization of the fragment coordinates
	fragCoord = (2.0 * fragCoord - iResolution.xy) / iResolution.y;

	// Define the strip pattern
	float strip = smoothstep (0.3 - AA * 10.0, 0.3 + AA * 10.0, cos (fragCoord.y * 40.0 + sin (fragCoord.x * 20.0) + iTime * 10.0));

	// Translation of the plan
	vec2 p = fragCoord + vec2 (0.5 * cos (iTime * 0.7), 0.2 * sin (iTime * 1.4));

	// Rotation of the plan
	p *= rotate (iTime);

	// Zoom of the plan
	float zoom = 1.0 + 0.5 * cos (iTime * 1.8);
	p /= zoom;

	// Consider the plan is a grid of squares
	p = p / REPEAT + 0.5;
	vec2 id = floor (p);
	p = REPEAT * (fract (p) - 0.5);

	// Rotation of the square's contents (the angle depends on the position of the square in the grid)
	p *= rotate (iTime * (id.x * 2.0 + id.y * 3.0));

	// Compute the signed distance to the star shape within each square
	float star = starDist (p);

	// For the fun, add little stars in the big stars, using the same approach as above
	p /= 0.1;
	p = REPEAT * (fract (p / REPEAT + 0.5) - 0.5);
	star = max (star, -abs (starDist (p)));

	// What about adding other stars in the background? (Again, using the same approach as above but starting with "fragCoord") 
	p = fragCoord * rotate (-iTime * 0.6) / 0.3;
	p = REPEAT * (fract (p / REPEAT + 0.5) - 0.5);
	star = min (star, max (-star,  starDist (p)));

	// Display the stars and strips
	const float halfThickness = THICKNESS * 0.5;
	float halfThicknessAA = halfThickness + AA / zoom;
	float redGradient =  0.7 + 0.3 * cos (PI * fragCoord.x * iResolution.y / iResolution.x);
	fragColor = vec4 (
		smoothstep (-halfThicknessAA, -halfThickness, star) * redGradient,
		smoothstep (halfThicknessAA, halfThickness, abs (star)),
		smoothstep (-halfThickness, -halfThicknessAA, star) * strip,
		1.0);
}

#define C1	vec2 (0.2, 0.55)
#define C2	vec2 (-0.743644, 0.131826)
#define N	900.0
#define PI	3.14159265358979

mat2 Rotate (in float angle)
{
	float c = cos (angle);
	float s = sin (angle);
	return mat2 (c, s, -s, c);
}

float Mandelbrot (in vec2 c)
{
	vec2 m;

	#ifndef NO_OPTIM
	m = vec2 (c.x + 1.0, c.y);
	if (dot (m, m) < 0.0625) {
		return 1.0;
	}

	m = vec2 (c.x - 0.25, c.y);
	float l = dot (m, m);
	if (c.x < sqrt (l) - 2.0 * l + 0.25) {
		return 1.0;
	}
	#endif

	m = c;
	for (float n = 0.0; n < N; ++n) {
		if (dot (m, m) > 256.0) {
			return n / N;
		}
		m = vec2 (m.x * m.x - m.y * m.y, 2.0 * m.x * m.y) + c;
	}
	return 1.0;
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
	vec2 c = (2.0 * fragCoord - iResolution.xy) / iResolution.y;

	float time = iTime * 0.1;
	float zoom = 1.5 * pow (0.5, 18.0 * (0.5 - 0.5 * cos (time * 2.0)));
	float angle = PI * 6.0 * cos (time);
	vec2 translate = mix (C1, C2, smoothstep (-0.4, 0.4, sin (time)));

	c = zoom * Rotate (angle) * c + translate;
	float m = Mandelbrot (c);
	fragColor = vec4 (m, pow (m, 0.6), pow (m, 0.3), 1.0);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
	// Render the scene normally
	fragColor = Render (fragCoord, iResolution.xy, iTime);

	// Mix with the scene shifted by half a pixel
	// Notes:
	// - Linear filtering has to be used to fetch the color
	// - A value of 0.8 for the mix returns the actual average of the 5 sampling points,
	//   while 0.5 acts more like a Gaussian filter
	if (iMouse.z < 0.5) {
		fragColor = mix (fragColor, texture (iChannel0, (fragCoord + 0.5) / iResolution.xy), 0.5);
	}
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
	// Render the scene shifted by half a pixel
	if (iMouse.z < 0.5) {
		fragColor = Render (fragCoord - 0.5, iResolution.xy, iTime);
	}
}

// Example: basic Mandelbrot set display (from https://www.shadertoy.com/view/tt2yWV)
#define C vec2 (-0.743603, 0.1318205)
#define N 900.0

mat2 Rotate (in float angle)
{
	float c = cos (angle);
	float s = sin (angle);
	return mat2 (c, s, -s, c);
}

float Mandelbrot (in vec2 c)
{
	vec2 m = c;
	for (float n = 0.0; n < N; ++n) {
		if (dot (m, m) > 4.0) {
			return n / N;
		}
		m = vec2 (m.x * m.x - m.y * m.y, 2.0 * m.x * m.y) + c;
	}
	return 1.0;
}

vec4 Render (in vec2 fragCoord, in vec2 resolution, in float time)
{
	vec2 c = (2.0 * fragCoord - resolution) / resolution.y;
	float zoom = pow (0.5, 18.0 * (0.72 - 0.28 * cos (time * 0.2)));
	float m = Mandelbrot (zoom * Rotate (time * 0.3) * c + C);
	return vec4 (m, pow (m, 0.6), pow (m, 0.3), 1.0);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
	// Render the scene normally
	fragColor = Render (fragCoord, iResolution.xy, iTime, iMouse.xy);

	// Mix with the scene shifted by half a pixel
	// Notes:
	// - Linear filtering has to be used to fetch the color
	// - A value of 0.8 for the mix returns the actual average of the 5 sampling points,
	//   while 0.5 acts more like a Gaussian filter
	fragColor = mix (fragColor, texture (iChannel0, (fragCoord + 0.5) / iResolution.xy), 0.5);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
	// Render the scene shifted by half a pixel
	fragColor = Render (fragCoord - 0.5, iResolution.xy, iTime, iMouse.xy);
}

// Giant Voxel Pac-Man (from https://www.shadertoy.com/view/ltGyWz)

// Parameters
#define VOXEL_RESOLUTION	1.0
#define CAMERA_FOCAL_LENGTH	8.0
#define DELTA				0.01
#define RAY_LENGTH_MAX		500.0
#define RAY_STEP_MAX		100.0
#define AMBIENT				0.2
#define SPECULAR_POWER		2.0
#define SPECULAR_INTENSITY	0.3
#define SHADOW_LENGTH		150.0
#define SHADOW_POWER		3.0
#define FADE_POWER			0.7
#define BACKGROUND			0.7
#define GLOW				0.4
#define GAMMA				0.8

//#define GROUND

// Math constants
#define PI		3.14159265359
#define SQRT3	1.73205080757

// Global variables
float time;
float glowCounter;

// PRNG (from https://www.shadertoy.com/view/4djSRW)
float rand (in vec3 seed) {
	seed = fract (seed * vec3 (5.3983, 5.4427, 6.9371));
	seed += dot (seed.yzx, seed.xyz + vec3 (21.5351, 14.3137, 15.3219));
	return fract (seed.x * seed.y * seed.z * 95.4337);
}

// Distance to the voxel
float distVoxel (in vec3 p) {

	// Update the glow counter
	++glowCounter;

	// Rounded box
	const float voxelRadius = 0.25;
	return length (max (abs (p) - 0.5 + voxelRadius, 0.0)) - voxelRadius;
}

// Distance to the scene and color of the closest point
vec2 distScene (in vec3 p, out vec3 P) {

	// Update the glow counter
	++glowCounter;

	// Scaling
	p *= VOXEL_RESOLUTION;

	// Velocity, period of the waves, spacing of the gums
	float v = VOXEL_RESOLUTION * floor (time * 100.0 / VOXEL_RESOLUTION);
	const float k1 = 0.05;
	const float k2 = 60.0;

	// Giant Pac-Man
	float body = length (p);
	float eyes = 6.0 - length (vec3 (abs (p.x) - 12.5, p.y - 19.5, p.z - 20.0));
	float mouthAngle = PI * (0.075 + 0.065 * cos (2.0 * v * PI / k2));
	float mouthTop = dot (p, vec3 (0.0, -cos (mouthAngle), sin (mouthAngle))) - 2.0;
	mouthAngle *= 2.5;
	float mouthBottom = dot (p, vec3 (0.0, cos (mouthAngle), sin (mouthAngle)));
	float pacMan = max (max (body - 32.0, eyes), min (mouthTop, mouthBottom));
	vec2 d = vec2 (pacMan, pacMan > 29.0 - body ? 0.13 : 1.0);
	P = p;

	// Gums
	vec3 q = vec3 (p.xy, mod (p.z + v, k2) - k2 * 0.5);
	float gum = max (length (q) - 6.0, -p.z);
	if (gum < d.x) {
		d = vec2 (gum, 0.35);
		P = q;
	}

	// Ground
	#ifdef GROUND
	q = vec3 (p.xy, p.z + v);
	float ground = (q.y + 50.0 + 14.0 * cos (q.x * k1) * cos (q.z * k1)) * 0.7;
	if (ground < d.x) {
		d = vec2 (ground, 0.55);
		P = q;
	}
	#endif

	// Ghost
	v = VOXEL_RESOLUTION * floor ((110.0 + 40.0 * cos (time * 3.0)) / VOXEL_RESOLUTION);
	q = vec3 (p.xy, p.z + v);
	body = length (vec3 (q.x, max (q.y - 4.0, 0.0), q.z));
	eyes = 8.0 - length (vec3 (abs (q.x) - 12.0, q.y - 10.0, q.z - 22.0));
	float bottom = (q.y + 28.0 + 4.0 * cos (p.x * 0.4) * cos (p.z * 0.4)) * 0.7;
	float ghost = max (max (body - 28.0, eyes), -bottom);
	if (ghost < d.x) {
		d = vec2 (ghost, ghost > 25.0 - body ? 0.76 : 1.0);
		P = q;
	}

	// Scaling
	d.x /= VOXEL_RESOLUTION;
	return d;
}

// Distance to the (voxelized?) scene
vec4 dist (inout vec3 p, in vec3 ray, in float voxelized, in float rayLengthMax) {
	vec3 P = p;
	vec2 d = vec2 (1e10, 0.0);
	float rayLength = 0.0;
	float rayLengthInVoxel = 0.0;
	float rayLengthCheckVoxel = 0.0;
	vec3 raySign = sign (ray);
	vec3 rayDeltaVoxel = raySign / ray;
	for (float rayStep = 0.0; rayStep < RAY_STEP_MAX; ++rayStep) {
		if (rayLength < rayLengthInVoxel) {
			d.x = distVoxel (fract (p + 0.5) - 0.5);
			if (d.x < DELTA) {
				break;
			}
		} else if (rayLength < rayLengthCheckVoxel) {
			vec3 rayDelta = (0.5 - raySign * (fract (p + 0.5) - 0.5)) * rayDeltaVoxel;
			float dNext = min (rayDelta.x, min (rayDelta.y, rayDelta.z));
			d = distScene (floor (p + 0.5), P);
			if (d.x < 0.0) {
				rayDelta = rayDeltaVoxel - rayDelta;
				d.x = max (rayLengthInVoxel - rayLength, DELTA - min (rayDelta.x, min (rayDelta.y, rayDelta.z)));
				rayLengthInVoxel = rayLength + dNext;
			} else {
				d.x = DELTA + dNext;
			}
		} else {
			d = distScene (p, P);
			if (voxelized > 0.5) {
				if (d.x < SQRT3 * 0.5) {
					rayLengthCheckVoxel = rayLength + abs (d.x) + SQRT3 * 0.5;
					d.x = max (rayLengthInVoxel - rayLength + DELTA, d.x - SQRT3 * 0.5);
				}
			} else if (d.x < DELTA) {
				break;
			}
		}
		rayLength += d.x;
		if (rayLength > rayLengthMax) {
			break;
		}
		p += d.x * ray;
	}
	return vec4 (d, rayLength, rand (P));
}

// Normal at a given point
vec3 normal (in vec3 p, in float voxelized) {
	vec2 h = vec2 (DELTA, -DELTA);
	vec3 n;
	if (voxelized > 0.5) {
		p = fract (p + 0.5) - 0.5;
		n = h.xxx * distVoxel (p + h.xxx) +
			h.xyy * distVoxel (p + h.xyy) +
			h.yxy * distVoxel (p + h.yxy) +
			h.yyx * distVoxel (p + h.yyx);
	} else {
		n = h.xxx * distScene (p + h.xxx, n).x +
			h.xyy * distScene (p + h.xyy, n).x +
			h.yxy * distScene (p + h.yxy, n).x +
			h.yyx * distScene (p + h.yyx, n).x;
	}
	return normalize (n);
}

// HSV to RGB
vec3 hsv2rgb (in vec3 hsv) {
	return hsv.z * (1.0 + 0.5 * hsv.y * (cos (2.0 * PI * (hsv.x + vec3 (0.0, 2.0 / 3.0, 1.0 / 3.0))) - 1.0));
}

// Main function
vec4 Render (in vec2 fragCoord, in vec2 iResolution, in float iTime, in vec2 iMouse) {

	// Make the time global
	time = iTime;

	// Get the fragment
	vec2 frag = (2.0 * fragCoord.xy - iResolution) / iResolution.y;

	// Define the rendering mode
	float modeTiming = iTime * 0.234;
	float modeAngle = PI * cos (iTime * 0.2);
	modeAngle = dot (frag - vec2 (cos (iTime * 2.0), 0.0), vec2 (cos (modeAngle), sin (modeAngle)));
	float modeVoxel = step (0.5, fract (modeTiming / (4.0 * PI)));
	modeTiming = cos (modeTiming);
	float mode3D = smoothstep (0.8, 0.5, modeTiming);
	float modeSwitch = smoothstep (0.995, 1.0, modeTiming) + smoothstep (0.02, 0.0, abs (modeAngle)) * modeVoxel;
	modeVoxel = 1.0 + (step (0.0, modeAngle) - 1.0) * modeVoxel;

	// Define the ray corresponding to this fragment
	vec3 ray = normalize (vec3 (frag, mix (12.0, CAMERA_FOCAL_LENGTH, mode3D)));

	// Compute the orientation of the camera
	float yawAngle = PI * (1.2 + 0.2 * cos (iTime * 0.5));
	float pitchAngle = PI * (0.1 * cos (iTime * 0.3) - 0.05);

	yawAngle += 4.0 * PI * iMouse.x / iResolution.x;
	pitchAngle += PI * 0.3 * (1.0 - iMouse.y / iResolution.y);

	yawAngle = mix (PI * 1.5, yawAngle, mode3D);
	pitchAngle *= mode3D;

	float cosYaw = cos (yawAngle);
	float sinYaw = sin (yawAngle);
	float cosPitch = cos (pitchAngle);
	float sinPitch = sin (pitchAngle);

	mat3 cameraOrientation;
	cameraOrientation [0] = vec3 (cosYaw, 0.0, -sinYaw);
	cameraOrientation [1] = vec3 (sinYaw * sinPitch, cosPitch, cosYaw * sinPitch);
	cameraOrientation [2] = vec3 (sinYaw * cosPitch, -sinPitch, cosYaw * cosPitch);

	ray = cameraOrientation * ray;

	// Compute the origin of the ray
	float cameraDist = mix (400.0, 200.0 + 80.0 * cos (iTime * 0.8), mode3D);
	vec3 origin = (vec3 (0.0, 0.0, 80.0 * sin (iTime * 0.2) - 20.0) - cameraOrientation [2] * cameraDist) / VOXEL_RESOLUTION;

	// Compute the distance to the scene
	glowCounter = 0.0;
	vec4 d = dist (origin, ray, modeVoxel, RAY_LENGTH_MAX / VOXEL_RESOLUTION);

	// Set the background color
	vec3 finalColor = hsv2rgb (vec3 (0.53 - 0.1 * ray.y + 0.1 * modeVoxel, 1.0, mode3D * BACKGROUND));
	vec3 glowColor = GLOW * vec3 (1.0, 0.3, 0.0) * glowCounter / RAY_STEP_MAX;
	if (d.x < DELTA) {

		// Set the object color
		vec3 color = d.y != 1.0 ? hsv2rgb (vec3 (d.y + 0.1 * d.w * modeVoxel, 0.5 + 0.5 * modeVoxel, 1.0)) : vec3 (0.0);

		// Lighting
		vec3 l = normalize (mix (vec3 (1.0, 0.0, 0.0), vec3 (1.25 + cos (iTime * 0.2), 1.0, 1.0), mode3D));
		if (modeVoxel > 0.5) {
			vec3 n = normal (floor (origin + 0.5), 0.0);
			float diffuse = max (0.0, dot (n, l));
			float specular = pow (max (0.0, dot (reflect (ray, n), l)), SPECULAR_POWER) * SPECULAR_INTENSITY;
			color = (AMBIENT + diffuse) * color + specular;
		}
		vec3 n = normal (origin, modeVoxel);
		float diffuse = dot (n, l);
		float specular;
		if (diffuse < 0.0) {
			diffuse = 0.0;
			specular = 0.0;
		} else {
			specular = pow (max (0.0, dot (reflect (ray, n), l)), SPECULAR_POWER) * SPECULAR_INTENSITY;
			origin += n * DELTA * 2.0;
			vec4 shadow = dist (origin, l, modeVoxel, SHADOW_LENGTH / VOXEL_RESOLUTION);
			if (shadow.x < DELTA) {
				shadow.z = pow (min (1.0, shadow.z * VOXEL_RESOLUTION / SHADOW_LENGTH), SHADOW_POWER);
				diffuse *= shadow.z;
				specular *= shadow.z;
			}
		}
		color = (AMBIENT + diffuse) * color + specular;

		// Fading
		float fade = pow (max (0.0, 1.0 - d.z * VOXEL_RESOLUTION / RAY_LENGTH_MAX), FADE_POWER);
		finalColor = mix (finalColor, color, fade);
	}

	// Set the fragment color
	finalColor = mix (pow (finalColor, vec3 (GAMMA)) + glowColor, vec3 (1.0), modeSwitch);
	return vec4 (finalColor, 1.0);
}

// Inspired by: https://twitter.com/jagarikin/status/1331409504953540613

#define PI			3.14159265358979
#define RADIUS		0.5
#define THICKNESS	0.1
#define SPEED		10.0
#define STRIPS		3.0

float segDist (in vec2 p, in vec2 a, in vec2 b) {
	p -= a;
	b -= a;
	return length (p - b * clamp (dot (p, b) / dot (b, b), 0.0, 1.0));
}

void mainImage (out vec4 fragColor, in vec2 p) {

	// Normalization of the fragment coordinates
	p = (2.0 * p - iResolution.xy) / iResolution.y;
	float halfPixel = 0.5 * dFdy (p.y);

	// Background color
	fragColor = vec4 (vec3 (0.5 + 0.05 * cos (p.x * 50.0) * cos (p.y * 50.0)), 1.0);

	// Select the animation
	float select = floor (6.0 * fract (iTime * 0.03 + step (0.0, p.x) / 3.0));

	// Display the rings & marker
	p.x = abs (p.x) - RADIUS * 1.6;

	float d = length (p) - RADIUS;
	float a = atan (p.y, p.x);
	float t = STRIPS * (a + iTime * SPEED);

	vec4 c = vec4 (1.0, smoothstep (-0.05, 0.05, cos (t)), 0.0, 1.0);
	fragColor = mix (fragColor, c, smoothstep (halfPixel, -halfPixel, abs (d) - THICKNESS));

	float m;
	float f;
	if (select < 1.5) {
		m = abs (length (p) - 0.15 + 0.1 * select);
		f = 1.0;
		t -= PI * (0.5 + select);
	} else {
		float dir = PI * 0.5 * (select - 2.0);
		vec2 tip = 0.15 * vec2 (-sin (dir), cos (dir));
		m = segDist (p, -tip * 0.5, tip);
		m = min (m, segDist (p, tip, tip * 0.7 + 0.3 * vec2 (tip.y, -tip.x)));
		m = min (m, segDist (p, tip, tip * 0.7 - 0.3 * vec2 (tip.y, -tip.x)));
		f = abs (sin (a + dir));
		t -= PI * 0.5 * sign (dot (p, tip));
	}
	fragColor.rgb *= smoothstep (-halfPixel, halfPixel, m - 0.02);

	c.g = smoothstep (-0.05, 0.05, cos (t));
	fragColor = mix (fragColor, c, f * smoothstep (halfPixel, -halfPixel, abs (d - THICKNESS) - 2.0 * halfPixel));
	c.g = 1.0 - c.g;
	fragColor = mix (fragColor, c, f * smoothstep (halfPixel, -halfPixel, abs (d + THICKNESS) - 2.0 * halfPixel));
}

#define PI				3.14159265358979
#define DISC_COUNT		9.0
#define DISC_DIST		0.7
#define DISC_RADIUS		0.1
#define HUE_SPEED		0.06
#define HUE_STEP		3.0

vec3 hsv2rgb (in vec3 hsv) {
	return hsv.z * (1.0 + hsv.y * clamp (abs (fract (hsv.x + vec3 (0.0, 2.0 / 3.0, 1.0 / 3.0)) * 6.0 - 3.0) - 2.0, -1.0, 0.0));
}

mat2 rotate (in float angle)
{
	float c = cos (angle);
	float s = sin (angle);
	return mat2 (c, s, -s, c);
}

float segDist (in vec2 p, in vec2 a, in vec2 b) {
	p -= a;
	b -= a;
	return length (p - b * clamp (dot (p, b) / dot (b, b), 0.0, 1.0));
}

void mainImage (out vec4 fragColor, in vec2 p) {

	// Normalization of the fragment coordinates
	p = (2.0 * p - iResolution.xy) / iResolution.y;
	float halfPixel = 0.5 * dFdy (p.y);

	// Background color
	fragColor = vec4 (vec3 (0.6 + 0.05 * cos (p.x * 50.0) * cos (p.y * 50.0)), 1.0);

	// Select the direction
	float dir = sign (cos (iTime * 0.8));

	// Display the direction
	vec2 q = p *rotate (iTime * dir * 0.2);
	q.x *= dir;
	vec2 tip = vec2 (-0.01, 0.3);
	float d = max (abs (length (q) - tip.y), min (-q.x, q.x + q.y));
	d = min (d, segDist (q, tip, tip + vec2 (0.05, 0.05)));
	d = min (d, segDist (q, tip, tip + vec2 (0.05, -0.05)));
	d -= 0.01;
	fragColor *= smoothstep (-halfPixel, halfPixel, d);

	// Divide the plan in DISC_COUNT sectors
	const float arc = 2.0 * PI / DISC_COUNT;
	p *= rotate (arc * floor (atan (p.y, p.x) / arc + 0.5));
	p.x -= DISC_DIST;

	// Display the discs
	float h = float (iFrame) * HUE_SPEED;
	vec3 hsv = vec3 (h, 0.9, 0.8);

	d = length (p) - DISC_RADIUS;
	fragColor = mix (fragColor, vec4 (hsv2rgb (hsv), 1.0), step (d, 0.0));

	p.y *= dir;
	d = max (d, -d - 2.0 * halfPixel);

	hsv.x = h - HUE_STEP * HUE_SPEED;
	fragColor = mix (fragColor, vec4 (hsv2rgb (hsv), 1.0), smoothstep (halfPixel, -halfPixel, max (d, -p.y)));

	hsv.x = h + HUE_STEP * HUE_SPEED;
	fragColor = mix (fragColor, vec4 (hsv2rgb (hsv), 1.0), smoothstep (halfPixel, -halfPixel, max (d, p.y)));
}

// Inspired by: https://twitter.com/jagarikin/status/1329610899976437765

#define PI			3.14159265358979
#define REPEAT		0.3
#define HEIGHT		2.2
#define THICKNESS	0.025
#define COLOR_SPEED	0.04
#define COLOR_DELAY	4.0
#define OUTLINE		2.0
#define DELTA		0.0001

vec4 color (in float t) {
	return vec4 (vec3 (abs (2.0 * fract (t * COLOR_SPEED) - 1.0)), 1.0);
}

float segDist (in vec2 p, in vec2 a, in vec2 b) {
	p -= a;
	b -= a;
	return length (p - b * clamp (dot (p, b) / dot (b, b), 0.0, 1.0));
}

float charDist (in vec2 p, in float jump) {
	p.y -= 0.25;
	float d = length (p - vec2 (0.0, 0.1)) - 0.04;
	d = min (d, segDist (p, vec2 (0.0, 0.1), vec2 (-0.02, -0.1)));
	if (jump < 0.5) {
		d = min (d, segDist (p, vec2 (-0.02, -0.1), vec2 (0.04, -0.15)));
		d = min (d, segDist (p, vec2 (0.04, -0.15), vec2 (0.08, -0.22)));
		d = min (d, segDist (p, vec2 (-0.02, -0.1), vec2 (-0.05, -0.16)));
		d = min (d, segDist (p, vec2 (-0.05, -0.16), vec2 (-0.1, -0.2)));
		d = min (d, segDist (p, vec2 (0.0, 0.04), vec2 (0.06, -0.03)));
		d = min (d, segDist (p, vec2 (0.06, -0.03), vec2 (0.1, -0.05)));
		d = min (d, segDist (p, vec2 (0.0, 0.04), vec2 (-0.08, -0.01)));
		d = min (d, segDist (p, vec2 (-0.08, -0.01), vec2 (-0.09, -0.05)));
	} else {
		d = min (d, segDist (p, vec2 (-0.02, -0.1), vec2 (0.06, -0.1)));
		d = min (d, segDist (p, vec2 (0.06, -0.1), vec2 (0.04, -0.2)));
		d = min (d, segDist (p, vec2 (-0.02, -0.1), vec2 (-0.02, -0.18)));
		d = min (d, segDist (p, vec2 (-0.02, -0.18), vec2 (-0.06, -0.24)));
		d = min (d, segDist (p, vec2 (0.0, 0.04), vec2 (0.07, 0.15)));
		d = min (d, segDist (p, vec2 (0.0, 0.04), vec2 (0.05, 0.02)));
		d = min (d, segDist (p, vec2 (0.05, 0.02), vec2 (0.11, 0.06)));
	}
	return d - THICKNESS;
}

float charY (in float x) {
	x = floor (x / REPEAT) * REPEAT;
	return max (0.0, HEIGHT - mix (-x, x * x * 0.8, step (0.0, x)));
}

float frameDist (in vec2 p) {

	// Vertical position of the character
	float dy = charY (p.x);

	// Distance to the stairs
	float d = p.y;
	if (p.x < -REPEAT * 0.5) {
		float dx = ceil ((p.y - HEIGHT) / REPEAT) * REPEAT;
		vec2 q = vec2 (dx - p.x, p.y - dy);
		d = min (d, max (min (q.x, q.y), min (max (q.x, q.y), 0.0)));
	}

	// Check whether the character is in the air
	float jump = step (-REPEAT, p.x) * step (DELTA, dy);

	// Distance to the character
	p.x = (fract (p.x / (2.0 * REPEAT)) - 0.75) * REPEAT * 2.0;
	p.y -= dy;
	return min (d, charDist (p, jump));
}

void mainImage (out vec4 fragColor, in vec2 p) {

	// Normalization of the fragment coordinates
	p = (2.0 * p - iResolution.xy) / iResolution.y;
	p = 1.6 * p - vec2 (0.15, -1.2);

	float halfPixel = 0.5 * dFdy (p.y);

	// Background color
	fragColor = vec4 (vec3 (0.5), 1.0);

	// Display
	vec4 c = color (float (iFrame));
	float d = frameDist (p);
	fragColor = mix (fragColor, c, smoothstep (halfPixel, -halfPixel, d));

	if (iMouse.z < 0.5) {
		vec2 n = normalize (vec2 (frameDist (p - vec2 (DELTA, 0.0)), frameDist (p - vec2 (0.0, DELTA))) - d);
		vec2 v = normalize (vec2 (REPEAT, (charY (p.x) - charY (p.x - REPEAT)))) * step (2.0 * halfPixel * OUTLINE, p.y);
		float f = dot (n, v);

		c = color (float (iFrame) + COLOR_DELAY * sign (f));
		d = abs (d - halfPixel * OUTLINE) - halfPixel * OUTLINE;
		fragColor = mix (fragColor, c, abs (f) * smoothstep (halfPixel, -halfPixel, d));
	}
}

// Inspired by: https://twitter.com/jagarikin/status/1329610899976437765

#define COLOR_SPEED	2.4
#define COLOR_DELAY	0.06
#define KEY_SPACE	(vec2 (32.5, 0.5) / 256.0)

vec4 color (in float t) {
	return vec4 (vec3 (abs (2.0 * fract (t * COLOR_SPEED) - 1.0)), 1.0);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord) {
	float run = step (iMouse.z, 0.5);
	mat4 m;
	if (texture (iChannel1, KEY_SPACE).r < 0.5) {
		vec4 background = vec4 (0.5, 0.5, 0.5, 1.0);
		m = mat4 (
			background,
			mix (background, color (iTime - COLOR_DELAY), run),
			color (iTime),
			mix (background, color (iTime + COLOR_DELAY), run)
		);
	} else {
		vec4 character = vec4 (0.8, 0.3, 0.3, 1.0);
		m = mat4 (
			color (iTime),
			color (iTime + COLOR_DELAY * run),
			character,
			color (iTime - COLOR_DELAY * run)
		);
	}
	fragColor = m * texture (iChannel0, fragCoord / iResolution.xy);
}

